INSERT INTO `question` VALUES (1, 'ACP云计算', '阿里云的云盾会检查通过公共互联网登录云服务器ECS的来源IP,登录方式包括SSH和远程桌面,当来自某个IP的登录请求出现多次密码错误的情况时,会发出\"ECS遭遇密码暴力破解\"的报警,当收到这个报警后,最安全的处理方法应该是。', '通知自己业务平台的所有用户立即修改密码,并通过技术手段杜绝简单密码', '这个报警无关紧要,可以忽略', '立即更新云服务器ECS实例的系统用户的密码,并启用安全组防火墙,只允许特定IP连接ECS实例', '立即登录云服务器ECS实例,检查登录日志,如果没有异常登录成功的记录,可直接忽略', 'C', '4', 1);
INSERT INTO `question` VALUES (2, 'ACP云计算', '您基于阿里云的云服务器ECS实例部署了Mysql数据库,随着业务量的不断上涨,您自己部署的数据库的服务能力越来越不足,表现在并发连接数不足,磁盘的IOPS不能满足业务需要等,可以采用阿里云的_________产品来解决这些问题。', '对象存储OSS', '表格存储', '云数据库RDS', '大数据分析ODPS', 'C', '4', 1);
INSERT INTO `question` VALUES (3, 'ACP云计算', '作为一个互联网初创企业的技术负责人,小A在早期选择了阿里云的云服务器ECS并将Java应用程序、数据库MySQL等部署在了一台云服务器ECS实例上。随着业务发展和用户量增加,发现部署在这台ECS实例上数据库性能出现瓶颈。为了最快速度解决该问题,同时提高系统安全性和稳定性,您建议选择阿里云的_________产品。', '对象存储OSS', '云数据库RDS', '负载均衡SLB', '专有云VPC', 'B', '4', 1);
INSERT INTO `question` VALUES (4, 'ACP云计算', '在公共云的环境中,阿里云负责云计算基础设施的安全(如:IDC环境、物理服务器运维、云产品虚拟化层等),用户需要对自己购买的云产品进行必要的安全优化和防护设置,以下几种客户的行为中您认为最安全的是。', '为了管理方便,将云服务器ECS实例的管理员密码修改为123456', '云服务器ECS实例购买成功后,立即通过管理控制台启用该ECS实例的安全组防火墙,并设置为只允许某管理IP远程登录', '为了减少沟通成本,公司的5个管理员都用root帐号登录云服务器ECS实例', '为了方便在家办公的同事更新数据,对云数据库RDS实例开通了公网IP,并允许所有IP连接', 'B', '4', 1);
INSERT INTO `question` VALUES (5, 'ACP云计算', '阿里云内容分发网络CDN系统能够实现内容加速的功能,需要智能DNS解析的支持,以下_________功能不是智能DNS解析系统具备的。', '可以根据不同的访问者IP把同一个域名分别解析成不同的IP地址', '智能DNS解析系统具备网页内容缓存的功能,用户首次访问时从源站抓取数据,之后的请求优先从缓存中读取', '可以根据预先配置好的IP段位置信息,将用户请求指向离客户最近的服务器', '如果用户在多个机房放置相同的镜象站点,可以让不同的地方客户访问不同的站点,实现负载均衡', 'B', '4', 1);
INSERT INTO `question` VALUES (6, 'ACP云计算', '阿里云对象存储OSS的图片处理服务中,所有对图片的转换参数都会加在图片文件的URL后面,使URL变得冗长,不方便管理与阅读。为了简化操作,OSS  图片处理服务提供了_________功能,可以将常见的转换参数保存起来多次使用。', '样式', '方案', '短网址', '管道', 'A', '4', 1);
INSERT INTO `question` VALUES (7, 'ACP云计算', '开通阿里云对象存储OSS服务后,您首先需要使用OSS管理控制台或OpenAPI创建Bucket来存储文件。 创建Bucket时“所属地域”这个属性必须要指定,特别是您的云服务器ECS实例需要通过内网访问OSS Bucket时,必须选择与ECS实例相同的地域,以下有关“所属地域”的说法是正确的。', 'Bucket创建后,“所属地域”属性可随时更改', 'Bucket创建后,“所属地域”属性不可更改', 'Bucket创建后,如果未上传任何文件,则“所属地域”属性可以更改', 'Bucket创建后,要想修改“所属地域”属性,需申请特殊权限', 'B', '4', 1);
INSERT INTO `question` VALUES (8, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。使用阿里云OSS进行数据存储前,需要先建立创建一个或者多个存储空间,这个存储空间在OSS里叫。', 'key', 'value', 'object', 'bucket', 'D', '4', 1);
INSERT INTO `question` VALUES (9, 'ACP云计算', '阿里云对象存储服务(Object Storage Service,简称OSS),是阿里云提供的海量、安全、低成本、高可靠的云存储服务。用户操作OSS的基本数据单元是什么?', 'Object', 'Bucket', 'Service', '安全组', 'A', '4', 1);
INSERT INTO `question` VALUES (10, 'ACP云计算', '阿里云的负载均衡SLB包含了几个主要的部分:来自外部的访问请求、Listener、LoadBalancer、BackendServer等,关于Listener的说法中正确的是。', '主要包括用户定制的负载均衡策略和转发规则', '提供对外服务的ECS实例集合', '是指负载均衡SLB实例', '通过配置监控规则,可以生成负载均衡SLB实例的监控指标', 'A', '4', 1);
INSERT INTO `question` VALUES (11, 'ACP云计算', '在使用阿里云负载均衡SLB产品时,如果从负载均衡服务中移除ECS实例,直接将ECS实例从负载均衡实例中移除,可能会造成业务闪断。建议先执行以下哪个步骤?', '将ECS实例权重修改为0', '先停止ECS实例上web服务', '在管理控制台里将ECS实例停止', '将负载均衡的健康检查功能关闭', 'A', '4', 1);
INSERT INTO `question` VALUES (12, 'ACP云计算', '在阿里云上创建专有网络VPC时,VPC会自动为用户创建1条系统路由,这条路由的作用是_________。', '没有任何作用', '用于专有网络外的云产品实例访问专有网络内的云产品实例', '用于专有网络内的云产品实例访问专有网络外的云服务(该云服务支持VPC内的云产品实例直接访问)', '用于打通用户所有的专有网', 'C', '4', 1);
INSERT INTO `question` VALUES (13, 'ACP云计算', '阿里云的云盾补丁管理服务里发布的补丁来自。', '阿里云自己研发', '应用厂商官方发布', '开源社区网友的贡献', '操作系统厂商官方发布', 'A', '4', 1);
INSERT INTO `question` VALUES (14, 'ACP云计算', '阿里云绿网是云盾体系内的业务防护模块,它的作用是保护网站内容的安全,提前发现不符合政策规定的信息。以下对绿网功能的描述最准确的是。', '自动判别文本内容和图片是否合规,支持API批量检测图片', '只支持文本内容的识别', '可以限制服务器对外访问的资源,防止ECS云主机中毒后破坏内容的完整性', '用来限制访问ECS云主机的源IP,防止黑客篡改内容', 'A', '4', 1);
INSERT INTO `question` VALUES (15, 'ACP云计算', '当客户开通云盾安骑士功能后经常能收到一些安全的报警信息,但是没有时间处理,为了避免安全风险的发生,客户还可以选择云盾的_________服务,让阿里云专业的工程师代为处理。', '态势感知', '安全管家', '内容安全(原名:绿网)', 'DDoS高防IP', 'B', '4', 1);
INSERT INTO `question` VALUES (16, 'ACP云计算', '某视频手机应用,日均活跃用户为两百万,可以使用阿里云的哪一款或几款产品来解决用户对视频,图片,音频的大量访问?', 'OSS+CDN', 'VPC', '云盾+云监控', 'PTS', 'A', '4', 1);
INSERT INTO `question` VALUES (17, 'ACP云计算', '阿里云内容分发网络CDN在实际应用中,CDN效果的好坏与是否正确使用非常相关。 以下_________场景不适合使用CDN。', '重复访问率不高的个人网盘业务', '纯静态页的企业门户网站', '短视频分发网站', '手机APP的升级包下载站', 'A', '4', 1);
INSERT INTO `question` VALUES (18, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)中,可以配置伸缩规则来定义具体的扩展和收缩操作。以下_________是伸缩规则。', '加入或移出3个云服务器ECS实例', '配置周期性任务,每天13:00点定时触发', '自动创建云服务器ECS实例时,使用的配置以及镜像', '是否将不健康的云服务器ECS实例移出', 'A', '4', 1);
INSERT INTO `question` VALUES (19, 'ACP云计算', '您在阿里云两个在不同地域创建了专有网络VPC,如果希望实现两个VPC之间的私网通信,采用_________产品可以实现。', '通过高速通道实现VPC之间的私网通信', '通过路由器创建新的路由条目打通', '通过安全组创建新的访问规则打通', '无需任何操作,同一个帐号下的不同VPC本来就是内网连通的', 'A', '4', 1);
INSERT INTO `question` VALUES (20, 'ACP云计算', '当收到阿里云的云盾检测出的安全报警信息后,正确的处理方法是_________。', '无需理会,阿里云的云盾会搞定一切', '屏蔽报警信息,一点都不重要', '云盾只是一个产品,不能处理一切风险。当用户收到报警信息后需判断具体的风险点,主动修复。如:安装杀毒软件、修改管理密码、升级应用软件等', '云盾报警是非常可怕的,应立即向公安机关报案', 'C', '4', 1);
INSERT INTO `question` VALUES (21, 'ACP云计算', '阿里云内容分发网络CDN在基本配置之上为用户开放了很多优化功能,包括:页面优化、智能压缩、过滤参数、拖拽播放等。以下关于这些优化功能的描述,错误的是。', '可以压缩与去除页面中无用的空行、回车等内容,有效缩减页面大小', '支持多种内容格式的智能压缩,有效减少用户传输内容的大小', '可以提高站点的整体安全性,防止源站服务器帐号被爆力破解', '可实现视频、音频点播的随机拖拽播放功能', 'C', '4', 1);
INSERT INTO `question` VALUES (22, 'ACP云计算', '阿里云的云盾数据风控可以很好地解决WEB应用中常见的垃圾注册、刷库等业务风险识别的难题,要想使用这项服务首先得进行业务数据的采集,对于WEB应用系统,可以采用_________方式来采集信息。', 'JavaScript', 'SDK', 'JavaScript和SDK', 'HTML5', 'A', '4', 1);
INSERT INTO `question` VALUES (23, 'ACP云计算', '阿里云的云盾数据风控是通过领先的行为收集技术和机器学习模型,解决账号、活动、支付等关键业务环节存在的欺诈威胁,最终实验降低企业损失。该项服务是基于阿里巴巴的_________能力开发的。', 'BGP网络', '弹性计算', '大数据风控', 'DDoS攻防经验', 'C', '4', 1);
INSERT INTO `question` VALUES (24, 'ACP云计算', '在使用阿里云的负载均衡SLB实例时,做了如下健康检查的配置:响应超时时间为5秒,健康检查间隔为2秒,不健康阀值为3,健康阀值为3。即对于确认一个云服务器ECS实例是健康的,需要连续三次得到正常响应。如果某后端云服务器ECS实例当前状态是不健康的,现在对该实例进行修复,可以通过健康检查确认该实例是健康的时间窗是多久?', '19秒', '21秒', '5秒', '7秒', 'D', '4', 1);
INSERT INTO `question` VALUES (25, 'ACP云计算', '在使用阿里云的负载均衡SLB实例时,做了如下健康检查的配置:响应超时时间为5秒,健康检查间隔为2秒,不健康阀值为3,健康阀值为3,即对于确认一个后端云服务器ECS实例是不健康的,需要连续三次健康检查超时。则在某后端云服务器ECS实例状态异常后,被负载均衡SLB实例从后端服务器中移出的时间窗为是多久?', '19秒', '21秒', '5秒', '7秒', 'A', '4', 1);
INSERT INTO `question` VALUES (26, 'ACP云计算', '阿里云内容分发网络CDN和传统厂商的CDN相比具有稳定快速、节约成本和简单易用三个特点。以下关于阿里云CDN节约成本的描述错误的是_________。', '资源弹性扩展,按实际使用量付费,接入即可实现跨运营商、跨地域的全网覆盖', '先使用后付费,提供按流量或峰值带宽两种计费方式,满足不同业务需求', '对于网站突发流量,无需用户干预,自动作出响应和调整,有效减少源站压力', '同一地域的CDN和云服务ECS实例之间,不收回源流量费', 'D', '4', 1);
INSERT INTO `question` VALUES (27, 'ACP云计算', '关于阿里云CDN加速域名的“停用”和“删除”,表述正确的是?', '停用加速域名会自己停止相关域名的解析', '删除加速域名时,被加速域名的解析记录也会同时被自己恢复为加速前的内容', '\"停用“该加速域名后,该条加速域名信息仍保留,针对加速域名的请求系统不会再做自动回源处理', '“删除”该加速域名后,将删除该加速域名的全部相关记录,对于仅需要暂停使用该加速域名,推荐选择“停用”方式', 'D', '4', 1);
INSERT INTO `question` VALUES (28, 'ACP云计算', '关于阿里云CDN缓存数据更新的建议和描述,错误的是_________?', '缓存刷新指的是强制将分发节点上缓存的资源标记为过期,当用户再次对该资源发起请求时,节点会回源拉取资源,并缓存一份更新后的资源在分发节点', '同名更新时,可以从控制台提交刷新请求,或者使用API,完成主动刷新', '同名更新时,如果不主动刷新,只能等待缓存文件到期后才能回源拉取最新的文件', '阿里云CDN支持对同名更新的实时更新,用户只要做相关配置后,不必主动提交请求,便可以实现自动刷新', 'D', '4', 1);
INSERT INTO `question` VALUES (29, 'ACP云计算', '云盾反欺诈服务是阿里巴巴的什么能力开发的?', 'BGP网络', 'DDoS攻防经验', '大数据风控服务', '弹性计算', 'C', '4', 1);
INSERT INTO `question` VALUES (30, 'ACP云计算', '某企业托管到传统IDC机房的服务器(包括windows和Linux两种操作系统),每天晚上有来自很多国家的IP地址对其管理密码进行尝试破解。为此管理员小王很是担心,因为工作需要,远程管理端口必须对公网开放。 小王听说阿里云的云盾可以帮助客户提升安全防护的能力,想把系统迁到阿里云上。在阿里云上开通_________功能可以减少密码被暴力破解的可能。', '内容安全(原名:绿网)', '安骑士', 'DDoS基础防护', '态势感知', 'B', '4', 1);
INSERT INTO `question` VALUES (31, 'ACP云计算', '您希望在阿里云上构建一个安全隔离的网络环境,并且在这个网络环境中可以按自己的需求进行网络拓扑的设计和私网IP地址网段的划分,可以采用_________产品来满足这个需求。', '弹性伸缩', '高速通道', '专有网络VPC', '专线', 'C', '4', 1);
INSERT INTO `question` VALUES (32, 'ACP云计算', 'BGP(边界网关协议)主要用于互联网AS(自治系统)之间的互联,BGP的最主要功能在于控制路由的传播和选择最好的路由,阿里云的云服务器ECS产品支持的多线BGP接入,解决的主要问题是。', '提升全网内容分发效率;', '避免跨运营商的网络访问的速度瓶颈;', '解决网络边界内容缓存的问题;', '解决网络带宽的问题;', 'B', '4', 1);
INSERT INTO `question` VALUES (33, 'ACP云计算', '您的业务将面临一个新的挑战,要进行一次大型的促销,但无法预估业务增长的幅度,但您了解云服务器ECS实例的CPU利用率如果超过了70%,业务的响应速度将会有大幅度的下降,目前您的web应用部署在一组阿里云的云服务器ECS实例上,前端采用一个公网的负载均衡SLB作为流量入口。采用方式可以最高效并最节省地来应对这个场景。', '制作部署web服务的云服务器ECS实例的镜像,基于该镜像创建足够多的新的云服务器ECS实例,并加入到负载均衡SLB的集群中,提前做好准备', '采用弹性伸缩Auto Scaling服务,设定伸缩规则,当CPU利用率超过70%的时候自动增加新的服务器ECS实例加入到负载均衡SLB的集群当中,CPU利用率低于30%的时候释放多余的云服务器ECS实例', '制作部署web服务的云服务器ECS实例的镜像,基于该镜像创建部分新的云服务器ECS实例,并加入到负载均衡SLB的集群中,并由运维人员实时监控CPU的利用率,如果超过70%就立刻申请新的云服务器ECS实例加入到负载均衡的集群中', '采用弹性伸缩Auto Scaling服务,设定伸缩规则,当CPU利用率超过70%的时候自动增加新的云服务器ECS实例加入到负载均衡SLB的集群当中', 'B', '4', 1);
INSERT INTO `question` VALUES (34, 'ACP云计算', '某企业使用公共云的云服务器进行文件处理,出于改善性能的考虑,将正在使用的1核2G云服务器的配置提升至2核8G,这种操作在云计算中被称为_________。', '垂直扩展', '自动扩展', '水平扩展', '资源编排', 'A', '4', 1);
INSERT INTO `question` VALUES (35, 'ACP云计算', '由于业务的流量增长,您需要对您的阿里云的云服务ECS实例的带宽进行临时升级操作,以下的描述中错误的是。', '可以按天进行升级,升级后如果云服务器ECS续费,仍然按照原基础带宽进行续费', '不支持按天升级,升级之后按升级之后的带宽进行续费', '可以在当前生命周期内,设定时间段区间内临时增加带宽', '可以多次叠加操作,支持随时操作,不受任何操作影响', 'B', '4', 1);
INSERT INTO `question` VALUES (36, 'ACP云计算', '您在创建阿里云的云服务器ECS实例时必须要选择_________来指定新建的云服务器ECS实例的系统盘的配置?', 'IP地址', '镜像', '安全组', '区域', 'B', '4', 1);
INSERT INTO `question` VALUES (37, 'ACP云计算', '用户在阿里云以外的服务器上安装“安骑士客户端”后,通过_________方式与指定的阿里云官网帐号关联。', '在管理控制台生成的安装验证key', '用户帐号ID', '用户的AccessKey', '用户名和密码', 'A', '4', 1);
INSERT INTO `question` VALUES (38, 'ACP云计算', '如果您在非阿里云的服务器上安装了云盾安骑士客户端,通过_________可以确保在您的管理控制台中能查到此服务器的相关报告。', '报告无法在阿里云的管理控制台查看', '通过阿里云官网帐号关联', '非阿里云服务器不支持安骑士', '在安装安骑士的过程中需要输入“安装验证key”,通过此Key关联用户的管理控制台', 'D', '4', 1);
INSERT INTO `question` VALUES (39, 'ACP云计算', '用户在阿里云以外的服务器上安装“云安全中心客户端”后,通过_________方式与指定的阿里云官网帐号关联。', '在管理控制台生成的安装验证key', '用户帐号ID', '用户的AccessKey', '用户名和密码', 'A', '4', 1);
INSERT INTO `question` VALUES (40, 'ACP云计算', '采用云计算服务与传统自建IT系统不同,相比传统自建方式,云计算带来了巨大的便利性。以阿里云服务器ECS为例,这些便利性中不包括_________。', '用户按照需要获得计算量而不是按照峰值设计', '用户无需参与任何安全管理工作', '用户无需再去维护和管理硬件', '获得服务器实例在几分钟内而不是数天数周', 'B', '4', 1);
INSERT INTO `question` VALUES (41, 'ACP云计算', '您的特定业务要暂时停止一段时间,为了节省成本希望暂时不使用或者释放这些业务的云服务器ECS实例, 但希望能够继续保留现有的云服务器ECS实例系统盘上运行的服务和数据, 通过_________可以最节省高效地实现。', '直接释放该云服务器ECS实例,阿里云会自动为用户保留数据', '制作该云服务器ECS实例的自定义镜像,并释放该实例', '直接释放该云服务器ECS实例,自动镜像可以保证需要保留的应用和数据还能找回来', '暂停该云服务器ECS实例', 'B', '4', 1);
INSERT INTO `question` VALUES (42, 'ACP云计算', '为了提升您在阿里云上部署的应用的可用性,可以在多可用区创建云服务器ECS实例并进行应用的部署,同时可以与_________产品搭配使用,可以实现高可用的架构。', '云监控', '弹性伸缩Auto Scaling', '云数据库RDS', '负载均衡SLB', 'D', '4', 1);
INSERT INTO `question` VALUES (43, 'ACP云计算', '阿里云的云盾DDoS高防IP是阿里云在“基础DDoS防护”之上提供的高级防护产品,支持四层和七层的抗攻击能力, 以下关于云盾DDoS高防IP的功能描述错误的是。', '支持弹性按天计费', 'DDoS防护阈值弹性调整,您可以随时升级更高级别的防护,调整过程服务中断时间不超过5分钟', '提供实时精准的流量报表及攻击详情,让您及时准确获得当前服务详情', '防护多种DDoS类型攻击,包括但不限于以下攻击类型ICMP Flood、UDP Flood、TCP Flood、SYN Flood、ACK Flood等', 'B', '4', 1);
INSERT INTO `question` VALUES (44, 'ACP云计算', 'A电商平台近几年业务增长很快,访问量持续保持每年提升300%,平台运营团队因此获得了公司的年度特别奖励。在高兴之余运营团队发现平台系统带宽的支出是以每年500%的比例增长的,如果能有效降低这块成本可以提升整体的运营质量。此时,该公司选择阿里云的_________服务,效果会最明显。', '支持多可用区的云数据库RDS', '内容分发网络CDN', '对象存储OSS', '负载均衡SLB', 'B', '4', 1);
INSERT INTO `question` VALUES (45, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。在安全性方面OSS服务本身具备了防DDoS攻击和自动黑洞清洗的功能,如果采用传统IT的解决方案,实现和OSS类似的防DDos功能,需要做什么投入。', '只需要在存储服务器内安装特定的安全软件', '需要购买专业的流量清洗和黑洞设备,同时按预期的防护能力购买相应的IDC入口带宽', '购买多台存储服务器,同时启用,可实现相同级别的抗DDoS功能', '专业的存储服务器都自带了防DDoS功能,无需客户购买', 'B', '4', 1);
INSERT INTO `question` VALUES (46, 'ACP云计算', '阿里云的云服务器ECS实例的磁盘快照是某一个时间点上某一个磁盘的数据拷贝。以下针对云服务器ECS实例的磁盘快照的描述正确的是。', '快照被存放在用户自己的磁盘中', '自定义快照名称可以以auto开头', '快照存放在OSS上', '只有系统盘支持自动快照', 'C', '4', 1);
INSERT INTO `question` VALUES (47, 'ACP云计算', '阿里云对象存储OSS为每个存储空间Bucket自动分配一个内网地址和一个外网地址,如果正确使用内网地址,一方面可以实现OSS Bucket与云服务器ECS实例之间的流量免费,另一方面云服务器ECS实例通过内网访问OSS Bucket的网络质量较好,能够有效的提升部署在ECS实例上的应用的上传和下载质量。以下关于OSS Bucket内网地址的描述正确的是。', '如果ECS实例使用OSS Bucket内网地址,只能访问与ECS实例在同一地域且在同一可用区的OSS Bucket', '只要ECS实例和OSS Bucket属于同一用户,ECS实例就可以通过内网访问OSS Bucket', '从公共云上的任意一台ECS实例都可以通过内网访问任何地域的OSS Bucket', '如果ECS实例需要访问OSS Bucket的内网地址,只能访问与ECS实例在同一地域的Bucket', 'D', '4', 1);
INSERT INTO `question` VALUES (48, 'ACP云计算', '在互联网环境中,云服务器ECS要想被别的应用或网民访问到,就必须开通相应的“端口”,比如常见的HTTP应用工作在80端口,FTP应用工作在21端口,以下是某管理员对云服务器ECS配置的策略,请您选出最安全的一种方法。', '云服务器ECS实例购买成功后,立即从管理控制台启用了安全组防火墙,并对公网只开通必要的服务端口', '用户打算在1台云服务器ECS实例上搭建多个应用,为了方便管理,采用了默认的设置,开放全部端口', '云服务器ECS实例购买成功后,立即从管理控制台启用了安全组防火墙,对公网开放的端口范围是从0到1024', '云服务器ECS实例购买成功后,立即从管理控制台启用了安全组防火墙,对公网开放全部端口,ECS实例对外只能访问80端口', 'A', '4', 1);
INSERT INTO `question` VALUES (49, 'ACP云计算', '某企业使用公共云搭建了一个门户网站,目前访问缓慢。为了提升网站的响应速度,又购置了多台云服务器, 希望新增加的服务器和原有服务器一起对外提供服务,需要哪种技术配合实现这个方案?', 'CDN(内容分发网络)', '容器服务', 'VPC(虚拟专用网)服务', '负载均衡', 'D', '4', 1);
INSERT INTO `question` VALUES (50, 'ACP云计算', '云计算一个重要特点是弹性,阿里云的用户可以根据业务需求和策略实现计算资源的自动调整。在业务量高峰时增加ECS实例来提升系统的处理能力,在业务量低谷时自动减少ECS实例以节约成本。针对此场景,阿里云的_________产品可以和云服务器ECS实例配合使用实现弹性计算。', '负载均衡SLB', '弹性伸缩Auto Scaling', '云数据库RDS', '专有网络VPC', 'B', '4', 1);
INSERT INTO `question` VALUES (51, 'ACP云计算', '云计算以多种形式对外提供服务,比如提供给消费者的服务是运行在云计算基础设施上的应用程序,消费者可以在各种设备上通过瘦客户端界面访问,如浏览器(例如基于Web的邮件),不需要管理或控制任何云计算基础设施,包括网络、服务器、操作系统、存储,甚至独立的应用能力等等,仅需要对应用进行有限的、特殊的配置。这种服务形式被称作。', 'PaaS(平台即服务)', 'DaaS(数据存储即服务)', 'SaaS(软件即服务)', 'IaaS(基础设施即服务)', 'C', '4', 1);
INSERT INTO `question` VALUES (52, 'ACP云计算', '用户通过网络使用软件,无需购买软硬件、建设机房等,而改用向提供商租用基于Web的软件,来管理企业经营活动,且无需对软件进行维护,服务提供商会全权管理和维护软件。这种模式是云计算提供的服务。', 'SaaS(软件即服务)', 'DaaS(数据存储即服务)', 'IaaS(基础设施即服务)', 'PaaS(平台即服务)', 'A', '4', 1);
INSERT INTO `question` VALUES (53, 'ACP云计算', '阿里云云服务器ECS是一种简单高效、处理能力可弹性伸缩的计算服务,帮助您快速构建更稳定、安全的应用,提升运维效率,降低IT成本。ECS属于。', 'SaaS(软件即服务)', 'IaaS(基础设施即服务)', 'PaaS(平台即服务)', 'DaaS(数据存储即服务)', 'B', '4', 1);
INSERT INTO `question` VALUES (54, 'ACP云计算', '云计算以多种形式对外提供服务,比如提供给消费者的服务是把客户开发或者购买的应用程序部署到云计算基础设施上。消费者不需要管理或控制底层的云基础设施,包括网络、服务器、操作系统、存储等,但能够控制部署的应用程序,也可能控制运行应用程序的托管环境配置。这种服务形式被称作。', 'SaaS(软件即服务)', 'DaaS(数据存储即服务)', 'IaaS(基础设施即服务)', 'PaaS(平台即服务)', 'D', '4', 1);
INSERT INTO `question` VALUES (55, 'ACP云计算', '云计算以多种形式对外提供服务,比如可以提供给消费者处理能力、存储、网络和其他基本的计算资源,消费者能够利用这些计算资源部署和运行任意软件,包括操作系统和应用程序,但不能管理或控制任何云计算基础设施。这种服务形式被称作。', 'SaaS(软件即服务)', 'PaaS(平台即服务)', 'IaaS(基础设施即服务)', 'DaaS(数据存储即服务)', 'C', '4', 1);
INSERT INTO `question` VALUES (56, 'ACP云计算', '某企业利用某公共云服务,租用了若干台虚拟机,并把这些虚拟机放在一个隔离的虚拟网络中,可以完全掌控自己的虚拟网络,包括选择自有IP地址范围、划分网段、配置路由表和网关等,这个虚拟网络在公共云服务中通常称作_________。', 'NFV服务', 'VPN服务', 'SDN服务', 'VPC服务', 'D', '4', 1);
INSERT INTO `question` VALUES (57, 'ACP云计算', '您的阿里云专有网络VPC创建后,需要完成_________操作之后,才能够在专有网络内创建其他的云产品实例, 如云服务器、负载均衡和云数据库等.', '配置网段地址', '创建交换机', '设置路由表', '创建路由器', 'B', '4', 1);
INSERT INTO `question` VALUES (58, 'ACP云计算', 'D公司基于阿里云对象存储OSS和云服务器ECS构建了一个应用网站。初期采用OSS的原因是看上了它的大容量存储和能在多台ECS之间共享读写文件的功能,后来因业务需要逐渐增加了事务性的数据交互需求,在多个请求共同写OSS上的同一个文件时会互相覆盖,为此管理员很发愁。遇到这种情况可以选用阿里云的哪个云服务直接解决?', '增加更多的云服务器ECS', '内容分发网络CDN', '选用负载均衡SLB', '选用关系型云数据库RDS解决', 'D', '4', 1);
INSERT INTO `question` VALUES (59, 'ACP云计算', '阿里云的负载均衡SLB提供对多台云服务器进行流量分发的服务,支持四层和七层的流量转发。其中七层流量转发是通过_________实现的。', 'LVS', 'Nginx', 'Tengine', 'Heartbeat', 'C', '4', 1);
INSERT INTO `question` VALUES (60, 'ACP云计算', '阿里云的负载均衡SLB提供对多台云服务器进行流量分发的服务,支持四层和七层的流量转发。其中四层流量转发是通过_________实现的。', 'LVS', 'Tengine', 'Nginx', 'Heartbeat', 'A', '4', 1);
INSERT INTO `question` VALUES (61, 'ACP云计算', '阿里云的云服务器ECS产品支持的多线BGP接入,解决的主要问题是.', '提升全网内容分发效率', '避免跨运营商的网络访问的速度瓶颈', '解决网络边界内容缓存的问题', '其他都是', 'B', '4', 1);
INSERT INTO `question` VALUES (62, 'ACP云计算', '如果在配置阿里云的负载均衡SLB实例的监听时,开启了“获取真实访问IP”,针对7层服务可以通过http头部中的_________字段获取来访者真实IP。', 'Authorization', 'Connection', 'Etag', 'X-Forwarded-For', 'D', '4', 1);
INSERT INTO `question` VALUES (63, 'ACP云计算', '阿里云对象存储OSS是按使用收费的服务,为了防止用户在OSS上的数据被其他人盗链而产生不必要的支出,OSS设计了防盗链功能,以下有关OSS防盗链实现机制的说法正确的是_________?', '基于SSL密钥实现', '基于HTTP的Authorization实现', '基于IP黑、白名单机制', '基于HTTP header中表头字段referer实现', 'D', '4', 1);
INSERT INTO `question` VALUES (64, 'ACP云计算', '以下_________安全功能需要单独购买,不是在创建阿里云的云服务器ECS实例的同时可以免费获得的。', '木马查杀', '基础DDoS防护', '防暴力破解', 'DDoS高防IP', 'D', '4', 1);
INSERT INTO `question` VALUES (65, 'ACP云计算', '阿里云的云服务器ECS实例的安全组实现了类似虚拟防火墙的功能,用于设置单个或多个云服务器ECS实例的网络访问策略。对于云服务器安全组使用的说法正确的是。', '每个ECS实例只能属于一个安全组', '每个ECS实例可以加入多个安全组,无数量上限', 'ECS实例必须加入安全组', 'ECS实例可以不加入安全组', 'C', '4', 1);
INSERT INTO `question` VALUES (66, 'ACP云计算', '您已经成功购买了阿里云的弹性公网IP(EIP),需要通过管理控制台进行弹性公网IP的绑定,但是界面提示没有找到相应的云服务器ECS实例,可能是_________原因引起的。', '您所申请的EIP所在地域的某个可用区内没有经典网络的云服务器ECS实例', '您所申请的EIP所在地域的专有网络VPC内在某个可用区内的没有云服务器ECS实例', '您所申请的EIP所在的地域内的专有网络VPC内没有云服务器ECS实例', '您所申请的EIP所在的地域内没有经典网络的云服务器ECS实例', 'C', '4', 1);
INSERT INTO `question` VALUES (67, 'ACP云计算', '您的业务增长,会出现现有的阿里云的云服务器ECS实例的系统盘存储资源不足的问题,阿里云提供了系统盘扩容的功能帮您解决系统盘存储资源不足的问题。下列关于扩容系统盘的操作说法错误的是_________。', '扩容系统盘之后您实例的IP地址会发生变化', '其他都是错误的', '通过更换实例的系统盘来实现系统盘的扩容', '扩容系统盘的时候需要停止您的实例,因此会短暂中断您的业务', 'A', '4', 1);
INSERT INTO `question` VALUES (68, 'ACP云计算', '某企业使用负载均衡SLB将用户的访问请求分发到多台云服务器ECS实例上,同时为了应对业务波动带来的计算资源需求的变化,选用了阿里云弹性伸缩(Auto Scaling)对后端云服务器ECS实例进行动态的添加或者删除。基于对业务状况的判断,他们定义了两种伸缩模式的任务,一种是定时任务,即在指定的时间点执行伸缩活动,另一种是报警任务,即根据云监控的返回信息自动触发伸缩活动。假设在某一时间点,定时任务和报警任务同时满足执行条件,以下说法正确的是_________?', '两个伸缩活动同时执行', '同时只会有一个伸缩活动执行,两种任务并没有优先级的区分', '同时只会有一个伸缩活动执行,报警任务触发的伸缩活动优先执行', '同时只会有一个伸缩活动执行,定时任务触发的伸缩活动优先执行', 'B', '4', 1);
INSERT INTO `question` VALUES (69, 'ACP云计算', '您发现在创建云服务器ECS实例的磁盘快照时所需的时间每次都不同,关于这个现象说法错误的是_________?', '因为磁盘容量大小不同,导致快照创建的时间不同', '第一次给磁盘创建快照是一个全量的过程,会把整个磁盘都做成快照,所以第一次快照制作的时间比较长', '第一次快照之后的磁盘快照是增量创建,只会对磁盘增加的数据和修改的数据做快照,所以第一次之后的快照制作的时间相对短一些', '磁盘快照生成的时候磁盘本身的读写性能发生了变化', 'D', '4', 1);
INSERT INTO `question` VALUES (70, 'ACP云计算', '阿里云负载均衡SLB可以通过流量分发扩展部署在后端服务器中的应用系统对外服务的能力,通过消除单点故障提升应用系统的可用性,必须和阿里云提供的_________配合使用。', '云数据库RDS', '云服务器ECS', '专有网络VPC', '云监控', 'B', '4', 1);
INSERT INTO `question` VALUES (71, 'ACP云计算', 'www.bestcar.com是一个刚建立的汽车资讯车友交流网站。主站用Php搭建,有10GB的图片素材,部分JS文件。目前购买一台ECS放置所有程序代码,并在ECS上安装MySQL数据库。随着用户访问量的不断增长, 访问网站的速度越来越慢,图片加载慢,网站响应慢。用户上传的图片每周增长50GB。以下哪种产品组合方案可以同时解决大量图片存储和快速访问两个问题?', '采用OSS+ECS组合', '采用OSS+MTS组合', '采用OSS+RDS组合', '采用OSS+CDN组合', 'D', '4', 1);
INSERT INTO `question` VALUES (72, 'ACP云计算', '您已经有一台运行状态的云服务器ECS实例,并完成了所需的应用软件的部署。如果您希望创建一个部署同样软件的新的云服务器ECS实例,可以采用_________方式最高效的获得。', '上传本地制作好的镜像,并基于该镜像创建一台新的云服务器ECS实例', '基于现有的云服务器ECS实例的系统盘制作快照,并基于该快照生成新的云服务器ECS实例', '直接生成一台新的云服务器ECS实例,并进行所需软件的部署', '基于现有的云服务器ECS实例的系统盘制作自定义镜像,并基于该自定义镜像生成新的云服务器ECS实例', 'D', '4', 1);
INSERT INTO `question` VALUES (73, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)经常会和云服务器ECS、负载均衡SLB、云数据库RDS等产品配合使用, 以下说法中正确的是_________?', '弹性伸缩必须和云服务器ECS一起使用', '弹性伸缩可以单独使用,不依赖于其他任何产品', '弹性伸缩必须和云数据库RDS一起使用', '弹性伸缩必须和负载均衡SLB一起使用', 'A', '4', 1);
INSERT INTO `question` VALUES (74, 'ACP云计算', '互联网络设备之间的数据传输需要特定的规范,这个规范的专业述语被称为“协议”,有一种协议的发明对互联网的产生起了决定性的作用,通过这个协议可以使数万台的计算机连接在一起,这个协议的名称是_________。', 'HTTP', 'UDP', 'SOAP', 'TCP/IP', 'D', '4', 1);
INSERT INTO `question` VALUES (75, 'ACP云计算', '阿里云对象存储OSS提供了海量的存储能力。当用户需要将一些Object从一个Bucket复制到另外一个Bucket,且不改变内容时,可以使用OSS OpenAPI的CopyObject实现。使用CopyObjec来复制文件可以节省_________成本。', '存储成本', '网络带宽', 'API请求次数', '没有节约成本', 'B', '4', 1);
INSERT INTO `question` VALUES (76, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling),是根据用户的业务需求和策略,自动调整其弹性计算资源的管理服务。支持用户添加已有的云服务器ECS实例,但该云服务器ECS实例的状态必须为。', '已停止', '运行中', '准备中', '已创建', 'B', '4', 1);
INSERT INTO `question` VALUES (77, 'ACP云计算', '某公司有一个阿里云官网账号,并通过此账号申请使用了阿里云的负载均衡SLB实例,公司的账号的管理员创建了子账号,并将负载均衡SLB实例的只读权限授予该子账号,该子账号的拥有者可以查看SLB服务,但是无法对其进行修改。上述授权操作使用了阿里云的。', 'RAM (Resource Access Management)', '资源编排', 'DAC(Discretionary Access Control)', 'MAC(Mandatory Access Control)', 'A', '4', 1);
INSERT INTO `question` VALUES (78, 'ACP云计算', '当云服务器ECS实例选择了CentOS系统时,本地为Windows操作系统,采用方式无法正确的登录该ECS实例。', '阿里云管理控制台', 'SecureCRT', 'Putty', 'Windows自带的远程桌面客户端', 'D', '4', 1);
INSERT INTO `question` VALUES (79, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。用OSS管理的文件可以很方便地对外提供分享,分享前点击文件后面的“获取地址”文字链接即可得到当前文件的地址,这个分享使用的是_________应用层(七层)协议。', 'HTTP', 'TCP', 'FTP', 'SMTP', 'A', '4', 1);
INSERT INTO `question` VALUES (80, 'ACP云计算', '使用阿里云弹性伸缩(Auto Scaling)来实现计算资源的弹性配置时,做了如下设置:伸缩组的属性中设置MinSize=6,MaxSize=8,伸缩规则为“减少5台ECS”, 伸缩配置也进行了正常的配置。该伸缩组当前的云服务器ECS实例数为8台,通过设置定时任务来执行,执行一次后,会减少云服务器ECS实例。', '5台', '1台', '2台', '0台', 'C', '4', 1);
INSERT INTO `question` VALUES (81, 'ACP云计算', '使用阿里云弹性伸缩(Auto Scaling)来实现计算资源的弹性配置时,做了如下设置:伸缩组的属性中设置MinSize=2,MaxSize=7,伸缩规则为“增加6台ECS”,伸缩配置也进行了正常的配置。该伸缩组当前的云服务器ECS实例数为3台,通过设置定时任务来执行,执行一次后,会增加云服务器ECS实例。', '6台', '4台', '2台', '0台', 'B', '4', 1);
INSERT INTO `question` VALUES (82, 'ACP云计算', '云盾是阿里云整体安全体系的一部分,为云上客户和阿里云自身提供多方面的安全保障功能。以下关于云盾的描述,正确的是_________。', '是一款软件产品,用户开通云产品后需要安装才能使用', '是一款硬件产品', '是阿里云为用户提供的,包括安全漏洞检测、网页木马检测以及主机入侵检测、防DDoS攻击等功能的一站式安全服务', '云盾的所有功能都需要付费使用', 'C', '4', 1);
INSERT INTO `question` VALUES (83, 'ACP云计算', '当您的阿里云的云服务器ECS实例处于_________状态时,通过API查询云服务器状态时,返回状态是running。', '启动中', '运行中', '更换系统中', '重置中', 'B', '4', 1);
INSERT INTO `question` VALUES (84, 'ACP云计算', '您希望通过华北2(北京)地域的阿里云专有网络VPC中的云服务器ECS实例通过内网访问OSS,但是连接oss-cn-beijing-internal.aliyuncs.com(华北2(北京)地域的通用内网地址)失败,可以通过_________解决。', '只能通过外网地址进行访问', '其他方式都不能解决', '换其他地域的内网地址进行访问, 如华北1 ( 青岛) 地域的通用内网地址( oss-cn-qingdao- internal.aliyuncs.com)', '通过VPC专用的OSS内网地址进行访问,vpc100-oss-cn-beijing.aliyuncs.com', 'D', '4', 1);
INSERT INTO `question` VALUES (85, 'ACP云计算', '阿里云的负载均衡SLB是对多台后端服务器进行流量分发的服务。以下关于负载均衡SLB的说法正确的是_________。', '通过Tengine提供四层负载均衡', '可以不需要云服务器ECS实例就实现负载均衡服务', '通过集群提供服务,具有高可靠性', '通过LVS提供七层负载均衡', 'C', '4', 1);
INSERT INTO `question` VALUES (86, 'ACP云计算', '阿里云的云服务器ECS产品的应用非常广泛,既可以单独的作为Web服务器,也可以与其他阿里云产品配合提供多媒体解决方案。在多媒体、大流量的APP或网站应用场景下,云服务器ECS实例与阿里云的_________产品搭配,可大幅减少用户访问的等待时间并降低带宽费用。', '对象存储OSS', '负载均衡SLB', '内容分发网络CDN', '弹性伸缩Auto Scaling', 'C', '4', 1);
INSERT INTO `question` VALUES (87, 'ACP云计算', '阿里云会为每个云服务器ECS实例分配一个私网IP。以下关于云服务器ECS实例的私网IP说法正确的是_________。', '私网IP不可以用于SLB的负载均衡', '同一地域内实例之间通过私网IP进行的通讯流量是收费的', '不要在操作系统内部自行变更私网IP,否则可能会导致私网通讯中断', '私网不允许同一帐号下的云服务器ECS实例之间或者云服务器ECS实例与其他云服务之间互访', 'C', '4', 1);
INSERT INTO `question` VALUES (88, 'ACP云计算', '某用户是做香蕉销售生意的,所有的在线平台和数据都放在阿里云的云服务器ECS实例和云数据库RDS实例中,现在CEO想对香蕉销售额的变化趋势做一个监控,并在订单量发生较大变化时能收到预警信息,可以使用阿里云的_________产品实现,并在管理控制台中展现结果。', '态势感知', '阿里绿网', '云服务器ECS', '云监控', 'D', '4', 1);
INSERT INTO `question` VALUES (89, 'ACP云计算', '使用阿里云弹性伸缩(Auto Scaling)时,创建了伸缩组,指定了“伸缩最小实例数(台)”为5,“伸缩最大实例数(台)”为8,设定了正确的伸缩配置,并添加了伸缩规则为“增加3台ECS实例”,创建了基于该伸缩规则的定时任务(运行时间设置为1小时后)。当前伸缩组中有效的ECS实例个数为3,马上启用该伸缩组, 以下说法正确的是?', '会在定时任务触发时按照伸缩配置自动创建3个ECS实例,添加到伸缩组', '会在伸缩组启用时,按照伸缩配置自动创建7个ECS实例,添加到伸缩组', '会在伸缩组启用时,按照伸缩配置自动创建2个ECS实例,添加到伸缩组', '会在定时任务触发时按照伸缩配置自动创建2个ECS实例,添加到伸缩组', 'C', '4', 1);
INSERT INTO `question` VALUES (90, 'ACP云计算', '您的操作系统为Windows的阿里云的云服务器ECS实例,通过远程登录发现黑屏,不可能是_________原因造成的。', 'CPU资源耗尽', '内存空间不足', '系统宕机', '密码输入错误', 'D', '4', 1);
INSERT INTO `question` VALUES (91, 'ACP云计算', '某在线教育网站是基于云服务器ECS+负载均衡SLB+弹性伸缩(Auto Scaling)的方案实现的,在保证系统稳定、客户体验良好的前提下,大大节省了资源成本和运维成本。现在他们想把用户的访问信息(如来源IP、访问页面URL、停留时长等)保存下来进行分析,支持一些市场活动。以下说法中错误的是_________?', '伸缩组中的每台ECS实例,均保存各自处理的用户访问信息,这些ECS实例上的数据随时可以访问、分析,这种方案简单高效、稳定可靠,同时节省成本', '将用户相关的访问信息实时或者准实时的同步到对象存储OSS中,供后续使用', '将用户相关的访问信息实时或者准实时的保存到云数据库RDS MySQL版中去,后续的查询、分析均通过RDS实现', '将用户相关的访问信息实时或者准实时的同步到云数据库Memcache版(原OCS)中去,后续通过应用访问OCS,或者按照一定规则将OCS中的数据持久化,然后使用持久化后的数据', 'A', '4', 1);
INSERT INTO `question` VALUES (92, 'ACP云计算', '某保险公司的线上平台每天在线订单量超过20万笔,一年来频繁受到大流量攻击(超过10G),使服务断断续续,严重影响了公司的品牌和业务量。如果该客户使用阿里云的_________产品可以抵御这类攻击,保证业务的高可用性。', 'DDoS高防IP', '态势感知', 'DDoS基础防护', '服务器安全托管', 'A', '4', 1);
INSERT INTO `question` VALUES (93, 'ACP云计算', '您希望通过管理控制台进行阿里云的专有网络VPC内的云服务器ECS实例的私网IP地址的修改,但发现操作的菜单不可用,可能是_________原因引起的。', '该云服务器ECS实例所在的VPC类型是默认专有网络', '该云服务器ECS实例没有绑定EIP', '没有针对该云服务器ECS实例进行停止操作,且该云服务器ECS实例不是停止状态', '该云服务器ECS实例不是运行状态', 'C', '4', 1);
INSERT INTO `question` VALUES (94, 'ACP云计算', '如果您希望在使用阿里云云服务器ECS时,为了定义更灵活的网络拓扑,并按需选择私网IP地址范围并进行网段的划分,应该使用产品。', 'VPN', '负载均衡SLB', '专有网络VPC', '云服务器ECS', 'C', '4', 1);
INSERT INTO `question` VALUES (95, 'ACP云计算', '阿里云的负载均衡SLB提供对多台云服务器ECS实例进行流量分发的服务。负载均衡SLB实例和后端提供服务的云服务器ECS实例必须具有相同的。', '地域(Region)', '规格配置(CPU、内存等)', '可用区(Zone)', '带宽设置', 'A', '4', 1);
INSERT INTO `question` VALUES (96, 'ACP云计算', '使用阿里云的负载均衡SLB时,为了实现跨地域(Region)的高可用性,可以在不同的地域(Region)创建多个负载均衡SLB实例,通过的方式对外提供服务。', 'DNS多播', 'DNS最小连接数', 'DNS轮询', 'DNS广播', 'C', '4', 1);
INSERT INTO `question` VALUES (97, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。与自建存储对比,OSS在可靠性、安全、成本和数据处理能力等几个方面都具有优势。以下_________不是OSS在数据处理能力方面表现出来的优势。', '提供大规模的数据分析能力', '提供实时缩放图片的处理功能', '提供实时的“图片水印”的图片处理功能', '提供实时的“文字水印”的图片处理功能', 'A', '4', 1);
INSERT INTO `question` VALUES (98, 'ACP云计算', '您在阿里云的某台云服务器ECS实例上运行的服务出现响应缓慢的情况,通过云监控发现目前该ECS实例的带宽利用率已经达到80%以上。在不希望对云服务器ECS实例重启的前提下,可以尝试通过_________操作来缓解服务响应缓慢的问题。', '其他三个都不需要', '升级ECS实例的带宽', '为ECS实例增加磁盘', '升级ECS实例的CPU和内存', 'B', '4', 1);
INSERT INTO `question` VALUES (99, 'ACP云计算', '想要实现同一个阿里云帐号下的不同云服务器ECS实例之间的网络访问控制,可以通过阿里云管理控制台提供的_________功能实现。', '云盾', '安全组', '绿网', '防火墙', 'B', '4', 1);
INSERT INTO `question` VALUES (100, 'ACP云计算', '阿里云的负载均衡SLB中的证书(Certificate)用于HTTPS协议,在需要使用加密协议时,用户可以将证书上传至负载均衡SLB实例中,在创建HTTPS协议监听时绑定证书,提供HTTPS服务。目前阿里云的负载均衡SLB只支持_________格式的证书。', 'PEM', 'P7B', 'PFX', 'DER', 'A', '4', 1);
INSERT INTO `question` VALUES (101, 'ACP云计算', '如果您在创建Linux操作系统的云服务器ECS实例的同时选择增加了数据盘,以下关于系统盘和数据盘的说法中错误的是。', '云服务器支持对数据盘进行二次分区', '如果您使用第三方工具对系统盘进行二次分区操作,不会发生什么异常', '可以根据业务需要,对数据盘进行多分区配置', '系统需要先格式化数据盘,然后挂载数据盘', 'B', '4', 1);
INSERT INTO `question` VALUES (102, 'ACP云计算', '当您通过Open  API进行阿里云的云服务器ECS实例的磁盘挂载的时候,发现不能成功挂载,可能是由于_________原因引起的。', '云服务器ECS实例的状态为已停止', '云服务器ECS实例的状态为运行中', '云服务器ECS实例的状态为启动中', '磁盘的状态为待挂载', 'C', '4', 1);
INSERT INTO `question` VALUES (103, 'ACP云计算', '您希望基于已有的磁盘快照在青岛可用区A的云服务器ECS实例创建一个新的块存储磁盘,您可以使用快照创建该磁盘。', '以下都不行', '青岛可用区A的某个ECS实例的系统盘快照', '北京可用区A的某个ECS实例的数据盘快照', '香港可用区A的某个ECS实例的数据盘快照', 'A', '4', 1);
INSERT INTO `question` VALUES (104, 'ACP云计算', '您需要创建一个新的云服务器ECS实例进行一个10天的临时性的测试时,采用方式去开通云服务器ECS实例是最经济的模式。', '按量付费', '包年包月', '预付费模式', '其他都不对', 'A', '4', 1);
INSERT INTO `question` VALUES (105, 'ACP云计算', '阿里云对象存储OSS是阿里云提供的海量、安全、低成本、高可靠的云存储服务。如果您有一个网站是纯静态页的,或者是对某复杂网站做过动静分离,您可以直接将静态网页通过OSS提供服务,不仅如此,OSS还可以与_________产品结合起来提供加速服务。', '内容分发网络CDN', '云服务器ECS', '高性能计算HPC', '专有网络VPC', 'A', '4', 1);
INSERT INTO `question` VALUES (106, 'ACP云计算', 'C公司是一个互联网金融创业企业,自去年下半年开始公司上线的P2P理财项目规模增长了500倍,日参与融资和理财的客户数量超过8万人。一边是业务规模快速增长,一边是用户体验停滞不前。经过架构师团队仔细评审后发现核心问题是APP客户端经常因本地DNS篡改导致连不上服务器,以及敏感的内容不敢用传统CDN分发导致访问速度慢。 C公司可以选择阿里云的_________产品组合解决这两个问题。', '阿里云的云解析和内容分发网络CDN', '阿里云的云解析和对象存储OSS', '阿里云HTTPDNS和内容分发网络CDN', '阿里云HTTPDNS和对象存储OSS', 'C', '4', 1);
INSERT INTO `question` VALUES (107, 'ACP云计算', 'PFX是使用二进制的格式保存的个人证书,一般出现在Windows Server中,现在想把该格式的证书上传到阿里云的负载均衡SLB实例,以下说法正确的是_________。', '负载均衡SLB支持PFX格式的证书,可以将其直接上传', '负载均衡SLB不支持PFX格式的证书,只能先将其转换成P7B格式,再上传', '负载均衡SLB不支持PFX格式的证书,只能先将其转换成PEM格式,再上传', '负载均衡SLB不支持PFX格式的证书,只能先将其转换成DER格式,再上传', 'C', '4', 1);
INSERT INTO `question` VALUES (108, 'ACP云计算', '您基于阿里云的云服务器ECS实例部署了Mysql数据库,随着业务量的不断上涨,您的运维工作不断增加, 包括数据库的备份、主备数据库之间的数据同步等问题,可以采用阿里云的_________产品来快速解决该问题。', '表格存储', '云数据库RDS', '开放搜索服务OpenSearch', '对象存储OSS', 'B', '4', 1);
INSERT INTO `question` VALUES (109, 'ACP云计算', '您通过阿里云的云监控服务为某台云服务器ECS实例的磁盘使用率设置了1条报警规则:统计周期为5分钟, 磁盘使用率平均值超过80%,连续探测5次超过阈值后就报警。如果您的磁盘平均使用率超过80%后,至少需要几分钟后可以收到报警?', '40分钟', '0分钟', '20分钟', '30分钟', 'C', '4', 1);
INSERT INTO `question` VALUES (110, 'ACP云计算', '阿里云的云盾态势感知能从多个维度监控用户云上资源的安全状况,包括网络层、主机层、数据库层、应用层, 甚至还提供安全情报功能,目前态势感知可以通过_________方式向用户发送报警信息。', '包括手机短信和电子邮件', '只有手机短信', '只有电子邮件', '包括手机短信、电子邮件和旺旺', 'A', '4', 1);
INSERT INTO `question` VALUES (111, 'ACP云计算', 'B公司基于阿里云对象存储OSS和云服务器ECS实例构建了一个mp3音乐下载站点,用户在该网站注册后可获得mp3文件的下载链接。 最近一周OSS的公网流量突然增长了一倍,但注册用户数只增加了5%,经仔细分析后发现很多下载请求不是用户从该网站直接下载,而是来自某搜索引擎。您认为该网站采取_________措施可以快速降低这种风险。', '增加负载均衡SLB,多一层防护', '通过OSS Bucket属性里的“防盗链设置”功能做来源限制', '将音乐下载站点改成HTTPS访问', '将音乐文件从OSS搬迁到云服务器ECS实例的数据盘', 'B', '4', 1);
INSERT INTO `question` VALUES (112, 'ACP云计算', 'SQL注入是一种常见的应用层攻击,一般是通过构建特殊的输入作为参数传入Web应用程序,盗取或破坏应用的数据。请您指出SQL注入攻击最终破坏或盗取的对象是_________。', '数据库', '服务器上的机密文件', 'WEB应用', '服务器的图片', 'A', '4', 1);
INSERT INTO `question` VALUES (113, 'ACP云计算', '您基于阿里云的云服务器ECS实例部署了Mysql数据库,随着业务量的不断上涨,您自己部署的数据库的服务能力越来越不足,表现在并发连接数不足,磁盘的IOPS不能满足业务需求等,可以采用阿里云的_________产品来解决这些问题。', '云数据库RDS', '对象存储OSS', '大数据分析ODPS', '表格存储', 'A', '4', 1);
INSERT INTO `question` VALUES (114, 'ACP云计算', '阿里云对象存储OSS为用户提供保存访问日志记录的功能。Bucket的拥有者可以通过OSS管理控制台,为其所拥有的Bucket开启访问日志记录功能。当一个Bucket A开启访问日志记录功能后,OSS自动将访问这个Bucket的请求日志,以小时为单位,按照固定的命名规则,生成一个Object写入用户指定的Bucket B。以下关于OSS日志记录说法正确的。', 'Bucket A和Bucket B可以属于不同的用户', 'Bucket A和Bucket B可以是同一个Bucket,也可以是不同的Bucket,但必须属于同一个用户', 'Bucket A和Bucket B必须是同一个Bucket', 'Bucket A和Bucket B必须是不同的Bucket', 'B', '4', 1);
INSERT INTO `question` VALUES (115, 'ACP云计算', '某视频公司,使用负载均衡SLB将用户的访问请求分发到30台云服务器ECS实例上去,由这些配置相同的ECS实例来响应用户的请求。每天晚上20:00到次日凌晨2:00期间,业务量会大幅增加,基本上会比其他时间段增加1倍左右,为了能正常响应游戏用户的请求,从成本和容易实现的角度,以下哪种解决办法最可取?', '使用自定义的镜像,手工创建30台云服务器ECS实例,在每天晚上20:00前,手工增加到SLB中去,次日凌晨2:00再从SLB中移除', '使用阿里云弹性伸缩(Auto Scaling),通过合适的配置,自动提升每台ECS实例的规格', '基于自定义的镜像创建30台云服务器ECS实例,添加到SLB中去,通过设置权重,让这些ECS实例在每天晚上20:00到次日凌晨2:00之间可以响应用户需求,其他时间段权重修改为0', '使用阿里云弹性伸缩(Auto Scaling),通过合适的配置,自动增加ECS实例或者减少ECS实例', 'D', '4', 1);
INSERT INTO `question` VALUES (116, 'ACP云计算', '与传统的软件运行和维护模式相比,云计算要求硬件资源和软件资源能够更好地共享,任何一个企业用户都能够按照自己的需求对SaaS软件进行客户化配置而不影响其他用户的使用。云计算环境中能够满足上述需求的技术是_________?', 'VPC技术', '多租户技术', '计算资源隔离技术', 'CDN技术', 'B', '4', 1);
INSERT INTO `question` VALUES (117, 'ACP云计算', '开通阿里云对象存储OSS服务后,您首先需要使用OSS管理控制台或OpenAPI创建Bucket来存储文件。创建Bucket时“所属地域”这个属性必须要指定,特别是您的云服务器ECS实例需要通过内网访问OSS Bucket时,必须选择与ECS实例相同的地域,以下有关“所属地域”的说法_________是正确的。', 'Bucket创建后,如果未上传任何文件,则“所属地域”属性可以更改', 'Bucket创建后,要想修改“所属地域”属性,需申请特殊权限', 'Bucket创建后,“所属地域”属性可随时更改', 'Bucket创建后,“所属地域”属性不可更改', 'D', '4', 1);
INSERT INTO `question` VALUES (118, 'ACP云计算', '以下创建ECS镜像时,_________步骤的描述有错?', '输入的自定义镜像描述,不可以为空', '选择全部快照,可以看到快照的列表', '输入自定义镜像名称,可以为空', '登陆阿里云的控制台来创建ECS镜像', 'C', '4', 1);
INSERT INTO `question` VALUES (119, 'ACP云计算', '当阿里云用户想要通过API进行ECS实例删除的操作的时候,必须保证ECS实例状态为_________,才可以进行删除操作,删除后实例的状态变为_________?', 'Stopped;Deleted', 'Stopped;Starting', 'Stopping;Deleted', 'Stopping;Starting', 'A', '4', 1);
INSERT INTO `question` VALUES (120, 'ACP云计算', '下列_________参数代表的是负载均衡SLB实例的唯一标识?', 'LoadBalancerId', 'RegionId', 'Address', 'LoadBalancerName', 'A', '4', 1);
INSERT INTO `question` VALUES (121, 'ACP云计算', '在对ECS实例进行续费操作时,无法实现以下_________操作?', '增加CPU规格', '带宽升级', '增加内存空间', '无需重启即可享受升级后的配置', 'D', '4', 1);
INSERT INTO `question` VALUES (122, 'ACP云计算', '下列关于ECS连接实例的说法错误的是_________?', 'Windows 2008默认允许最多2个session远程连接', '可以通过两种方式连接和管理用户实例:远程连接客户端和阿里云ECS控制台', 'Linux或Mac OS X环境连接Linux实例,直接使用ssh命令', '如果用户使用远程连接的工具访问ECS实例,使用公网和私网IP的实例都能被远程连接', 'D', '4', 1);
INSERT INTO `question` VALUES (123, 'ACP云计算', '用户通过ECS管理控制台查看其拥有的某个区域拥有的所有ECS实例的步骤包括:a. 进入ECS管理控制台;b.选择地域;c.选择ECS实例管理,则正确的排列顺序是?', 'b;a;c', 'a;b;c', 'a;c;b', 'c;b;a', 'C', '4', 1);
INSERT INTO `question` VALUES (124, 'ACP云计算', '如果用户需要创建SLB实例,为了能够真正的实现应用的负载分担,那么要保证至少要有_________个ECS实例?', '5个', '2个', '3个', '1个', 'B', '4', 1);
INSERT INTO `question` VALUES (125, 'ACP云计算', '下列关于RDS内网连接说法错误的是_________?', '用户可以在RDS内网和外网间切换', 'RDS实例内外网切换不会影响其他与RDS实例的连接', 'ECS可以使用内网地址连接RDS实例', '用户可以在RDS管理控制台的实例基本信息中查看当前实例使用的连接方式', 'B', '4', 1);
INSERT INTO `question` VALUES (126, 'ACP云计算', '在OSS服务中,单个object的大小限制为?', '24.4TB', '50TB', '48.8TB', '没有容量上限', 'C', '4', 1);
INSERT INTO `question` VALUES (127, 'ACP云计算', '下列关于SLB API ServerCertificate的说法错误的是_________?', '一次可以上传多份证书', '证书和PrivateKey一定要对应', '一次只能上传一个PrivateKey', '返回结果为成功或者错误码', 'A', '4', 1);
