INSERT INTO `question` VALUES (129, 'ACP云计算', '关于OSS域名绑定描述正确的是_________?', 'OSS域名绑定(CNAME)功能目前仅支持OSS以二级域名访问方式进行绑定。', 'OSS域名绑定时的访问方式为(Bucket name).${zone}.aliyuncs.com', '以上都不对', 'OSS cname绑定的域名必须经过阿里云备案', 'C', '4', 1);
