INSERT INTO `question` VALUES (131, 'ACP云计算', '当ECS处于以下_________状态时可以挂载磁盘?', '更换系统中', '启动中', '重置中', '已停止', 'D', '4', 1);
INSERT INTO `question` VALUES (132, 'ACP云计算', '阿里云OSS图片处理服务,绑定域名名需要做线上验证,其中一步是下载验证文件并上传到您域名的_________下,此步骤为验证您对绑定域名的所有权。', '有效率', '根目录', '顶级目录', '子目录', 'B', '4', 1);
INSERT INTO `question` VALUES (133, 'ACP云计算', '实现VPC中ECS服务器切换/迁移到同VPC下的其他交换机,包括以下几步,请选择正确的顺序_________:1.打开云服务器管理控制台;2.找到对应的需要切换/迁移的云服务器;3. 选择您所需的交换机,同时指定新交换机下的IP;4. 修改云服务器的私网地址;', '1,2,3,4', '1,2,4,3', '1,3,2,4', '1,3,4,2', 'C', '4', 1);
INSERT INTO `question` VALUES (134, 'ACP云计算', '下列关于SLB说法错误的是_________?', '不同操作系统的ECS可以同时做为SLB服务的后端服务器', 'SLB的服务能力与后端ECS的公网宽带规格无关', '同一组ECS不可以搭建多个网站并同时进行负载均衡', '在使用SLB的过程中随时调整后端ECS的数目', 'C', '4', 1);
INSERT INTO `question` VALUES (135, 'ACP云计算', '当用户想以个人身份向OSS发送请求时,需要首先将发送的请求按照OSS指定的格式生成签名字符串;然后使用AccessKeySecret对签名字符串进行加密产生验证码。OSS收到请求以后,会通过AccessKeyID找到对应的AccessKeySecret,以同样的方法提取签名字符串和验证码,如果计算出来的验证码和提供的一样即认为该请求是有效的;否则,OSS将拒绝处理这次请求,并返回HTTP错误状态码是_________?', '错误状态码:405', '错误状态码:400', '错误状态码:403', '错误状态码:500', 'C', '4', 1);
INSERT INTO `question` VALUES (136, 'ACP云计算', '下列哪种_________状态表示负载均衡SLB后端服务器的状态表示未开启SLB健康检查?', 'unavailable', 'available', 'abnormal', 'normal', 'A', '4', 1);
INSERT INTO `question` VALUES (137, 'ACP云计算', '云服务器ECS服务的API服务地址是。', 'ecsapi.aliyuncs.com', 'ecs.aliyun-api.com', 'ecs.aliyuncs.com', 'ecs.aliyun.com', 'C', '4', 1);
INSERT INTO `question` VALUES (138, 'ACP云计算', '在阿里云ECS中,无论每次接口调用请求是否成功,系统都会返回一个唯一识别码(返回参数)_________给用户?', 'Version', 'AccessKeyId', 'Response', 'RequestId', 'D', '4', 1);
INSERT INTO `question` VALUES (139, 'ACP云计算', '如果ECS实例采用linux系统,关于能否开启NetWorkManager服务说法正确的是?', 'linux系统请不要开启NetWorkManager服务,该服务会跟系统内部网络服务出现冲突,导致网络异常', 'linux系统可以开启NetWorkManager服务', '以上都不对', 'linux系统请可以开启NetWorkManager服务,但需要进行相应的配置调整', 'A', '4', 1);
INSERT INTO `question` VALUES (140, 'ACP云计算', 'ECS外部系统可以通过API在请求时传入参数来指定返回的数据格式,默认为_________格式?', 'JSP', 'HTTP', 'JSON', 'XML', 'D', '4', 1);
INSERT INTO `question` VALUES (141, 'ACP云计算', '用户如果想要在OSS中模拟实现文件夹的创建操作,需要使用的API是_________?', 'Multipart Upload', 'putObject', 'Get Object', 'Copy Object', 'B', '4', 1);
INSERT INTO `question` VALUES (142, 'ACP云计算', '以下针对ECS地域和可用区的描述不正确的是_________?', '同账号下,同一地域不同可用区的ECS实例内网不能互通', '若购买的ECS需要和阿里其他服务搭配使用,建议选择相同地域', '一个地域拥有多个可用区', '在购买了ECS实例后,地域不可更改', 'A', '4', 1);
INSERT INTO `question` VALUES (143, 'ACP云计算', '除了通过PUT Object接口上传文件到OSS以外,OSS还提供了另外一种上传模式是?', 'Get Object', 'Put Bucket', 'Multipart Upload', 'Head Object', 'C', '4', 1);
INSERT INTO `question` VALUES (144, 'ACP云计算', '下面_________不是安骑士包含的功能?', '木马文件检查', '异地登录报警', '高危漏洞修复', '防WEB应用系统密码破解', 'D', '4', 1);
INSERT INTO `question` VALUES (145, 'ACP云计算', '在新建的Linux ECS实例中,启动OFBiz服务的时候,步骤如下:a. # chmod +x ant;b. # cd ofbiz- release12.04;c. # ./ant run; # ./ant start &amp; ;d. # ./ant;# ./ant run-install。则下列排序正确的是?', 'a;c;b;d', 'a;b;c;d', 'b;a;d;c', 'b;c;d;a', 'C', '4', 1);
INSERT INTO `question` VALUES (146, 'ACP云计算', '中国大陆范围内的网站都需要做ICP备案,在架构设计时应该告诉客户提前就需要准备提交备案?', '网站上线前一天', '网站上线当天', '网站上线前一周', '网站上线前一个月', 'D', '4', 1);
INSERT INTO `question` VALUES (147, 'ACP云计算', '弹性伸缩的伸缩规则指的是_________。', '定义了SLB的监听规则', '定义了具体的扩展或收缩操作', '定义了组内ECS实例数的最大值、最小值', '定义了用于弹性伸缩的ECS实例的配置信息', 'B', '4', 1);
INSERT INTO `question` VALUES (148, 'ACP云计算', '下列哪个阿里提供的操作系统是自带图形化界面的?', 'windows server2008', 'OpenSUSE', 'Ubuntu', 'CentOS', 'A', '4', 1);
INSERT INTO `question` VALUES (149, 'ACP云计算', '以下对ECS的带宽临时升级操作的描述错误的是_________?', '支持在当前生命周期内,设定时间段区间内临时增加带宽', '可以多次叠加操作,支持随时操作,不受任何操作影响', '可以按天进行升级,升级后如如云服务器ECS续费,仍然按照原基础带宽进行续费', '不支持按天升级,升级之后按升级之后的带宽进行续费', 'D', '4', 1);
INSERT INTO `question` VALUES (150, 'ACP云计算', '专有网络可以与_________产品结合使用实现与传统数据中心组成一个按需定制的网络环境,实现应用的平滑迁移上云?', 'SLB', 'VPN', 'ECS', 'OCS', 'B', '4', 1);
INSERT INTO `question` VALUES (151, 'ACP云计算', '在管理控制台,对阿里云CDN加速域名进行“停用”或“删除”前,需要做_________操作,以确保网站可以正常访问?', '保证源站服务器的带宽充足就够了', '不需要做什么', '保证源站服务器正常开启即可', '到该域名所在的域名解析服务商处恢复域名A记录', 'D', '4', 1);
INSERT INTO `question` VALUES (152, 'ACP云计算', '在OSS API中通过_________参数的操作可以实现设置object头?', 'Multipart Upload', 'Get Object', 'Get Bucket', 'Copy Object', 'D', '4', 1);
INSERT INTO `question` VALUES (153, 'ACP云计算', '为了防止因本地IP对阿里云服务器频繁访问而被屏蔽的情况,可以通过云盾的_________功能进行设置?', 'IP白名单', 'webshell云查杀', '安全网络', '常用登录地管理', 'A', '4', 1);
INSERT INTO `question` VALUES (154, 'ACP云计算', '渗透测试是通过模拟_________的攻击方法,来评估计算机网络系统安全的一种评估方法.', '网络专家', '高并发客户', '恶意黑客', '正常客户', 'C', '4', 1);
INSERT INTO `question` VALUES (155, 'ACP云计算', '有很多10或100开头的IP在频繁访问我SLB的后端ECS实例,而安全检查没有报告问题,可能的原因是?', 'ECS服务器故障', '正在遭受网络攻击', 'SLB系统的健康检查和可用性监控', '真实用户的访问', 'C', '4', 1);
INSERT INTO `question` VALUES (156, 'ACP云计算', '在使用阿里云负载均衡SLB的时候,经常会发现有很多100开头的IP(100.x.x.x)在访问负载均衡SLB实例的后端云服务器ECS实例,这可能是_________引起的?', '开启了健康检查选项,负载均衡SLB实例对ECS实例进行健康检查引起的', '是云监控搜集各种统计指标', '是阿里云飞天系统的Zookeeper守护进程', '肯定配置了阿里云弹性伸缩(Auto Scaling),这是弹性伸缩的弹性自愈进程在做ECS实例的状态检查', 'A', '4', 1);
INSERT INTO `question` VALUES (157, 'ACP云计算', '作为一个互联网初创企业的技术负责人,小A在早期选择了阿里云的云服务器ECS并将Java应用程序、数据库MySQL等部署在了一台云服务器ECS实例上。随着业务发展和用户量增加,发现部署在这台ECS实例上数据库性能出现瓶颈。为了最快速度解决该问题,您建议选择阿里云的_________产品。', '对象存储OSS', '云数据库RDS', '负载均衡SLB', '专有云VPC', 'B', '4', 1);
INSERT INTO `question` VALUES (158, 'ACP云计算', '阿里云对象存储OSS的一个突出特点就是提供了极高的数据可靠性,谈到可靠性自然离不开备份的技术和策略,以下关于OSS备份的说法,正确的是_________?', 'OSS提供三重备份(每份数据保留三个副本),出现故障时自动恢复', '用户需要自己考虑数据的安全性,OSS提供了丰富的备份接口,方便用户将数据进行异地备份', 'OSS提供三重备份(每份数据保留三个副本),出现故障时在人工参与下可快速恢复', 'OSS服务的备份是采用的物理机磁盘的RAID0+1技术方案,两块硬盘同时出现故障也不影响服务', 'A', '4', 1);
INSERT INTO `question` VALUES (159, 'ACP云计算', '防DDoS攻击是阿里云的云盾最主要的功能之一,很多网站都曾经受到过不同类型的DDoS攻击,准确理解DDoS对网站做好安全防护至关重要。以下有关DDoS攻击的描述最准确的是_________?', 'DDoS攻击通过大量的尝试破解的服务器的登录密码', 'DDoS攻击的目的是窃取机密信息', 'DDoS攻击的主要目标是数据库', 'DDoS攻击的主要目的是让被攻击的目标服务器无法提供正常的服务,是目前最强大、最难防御的网络攻击之一', 'D', '4', 1);
INSERT INTO `question` VALUES (160, 'ACP云计算', '您的两台阿里云的云服务器ECS实例之间内网不能通信,一定不是由于_________原因引起的。', '两台云服务器实例在不同地域', '其中一台云服务器ECS实例修改过私网IP', '两台ECS实例在同一个账号下,同一个地域的不同可用区', '两个云服务器ECS实例在不同的安全组', 'C', '4', 1);
INSERT INTO `question` VALUES (161, 'ACP云计算', '某企业采取阿里云负载均衡SLB和弹性伸缩(Auto Scaling)配合使用的方案,希望在拥有良好的稳定性、可靠性的同时,还能节省运维成本和资源使用成本。由于此前没有相关的使用经验,他们根据自己的理解列出了一些应该注意的事项,然后向您咨询意见。作为阿里云产品专家,您判断出只有_________是正确的,在该公司后续的项目建设过程中也逐一得到验证。', '负载均衡SLB实例下的所有的ECS实例操作系统一定相同', '负载均衡SLB实例下的所有后端服务器的配置必须相同', '负载均衡SLB实例必须开启健康检查,否则无法和弹性伸缩一起使用', '负载均衡SLB实例下的所有后端服务器必须都有相应的伸缩组', 'C', '4', 1);
INSERT INTO `question` VALUES (162, 'ACP云计算', '您已经在阿里云的一台经典网络类型的没有数据盘的云服务器ECS实例上运行所有的应用,现在出于业务发展的需要,您希望将这台云服务器ECS实例迁移到相同地域的专有网络VPC中去,以下_________操作是可行的。', '直接将云服务器ECS实例的类型转换为专有网络', '制作该云服务器ECS实例的镜像,基于该镜像制作专有网络类型的云服务器ECS实例,业务会短暂停顿', '将该云服务器ECS实例的安全组变更为专有网络类型', '无法迁移', 'B', '4', 1);
INSERT INTO `question` VALUES (163, 'ACP云计算', '与专有云相比,公共云天生就暴露在互联网上。共有云在为企业应用带来与生自来的互联网+的特性,同时也带来了来自互联网的安全威胁。由于云计算模式基于网络提供服务,面向相对开放的环境,在技术上,除了仍然面临传统IT系统在物理、网络、系统等方面的安全问题之外,也面临着一些新的和云计算特征结合紧密的安全问题。阿里云是通过_________产品来提供云计算服务的基础安全加固和防护的(比如常见的防DDoS攻击)?', '云数据库', '云服务器', '云监控', '云盾', 'D', '4', 1);
INSERT INTO `question` VALUES (164, 'ACP云计算', '阿里云账号的AccessKey有所有API访问权限。下列关于AccessKeyId和AccessKeySecret说法错误的是。', 'AccessKeySecret是用于加密签名字符串和服务器端验证签名字符串的密钥', 'AccessKeySecret可以通过控制台直接查看而无需做额外验证', 'AccessKeyId和AccessKeySecret是由阿里云官方发给访问者的', 'AccessKeyID是用于标识访问者的身份', 'B', '4', 1);
INSERT INTO `question` VALUES (165, 'ACP云计算', 'A电商平台近几年业务增长很快,访问量持续保持每年提升300%,平台运营团队因此获得了公司的年度特别奖励。在高兴之余运营团队发现平台系统带宽的支出是以每年500%的比例增长的,如果能有效降低这块成本可以提升整体的运营质量。此时,该公司选择阿里云的服务,效果会最明显。', '支持多可用区的云数据库RDS', '对象存储OSS', '内容分发网络CDN', '负载均衡SLB', 'C', '4', 1);
INSERT INTO `question` VALUES (166, 'ACP云计算', '当一个阿里云的云服务器ECS实例无法加入指定的安全组时,可能是原因造成的。', '云服务器ECS实例创建之后默认已经加入一个安全组,不能再加入其它安全组', '本云服务器ECS实例目前已经加入的安全组数量达到上限', '该云服务器ECS实例没有开启公网带宽,所以无法加入安全组', '该云服务器ECS实例的状态为运行中', 'B', '4', 1);
INSERT INTO `question` VALUES (167, 'ACP云计算', '某大型购物商城新上了一套视频监控系统,在商城所在一个5层高的大楼里共安装了35个高清摄像头,用于监控主要的出口通道。该系统的应用部署在一台阿里云的云服务器ECS实例上,该ECS实例上挂了4块2T的数据盘来存储视频数据。上线后一周后发现视频数据的增速过快,预计8T硬盘不出1个月就会用完。 您认为使用阿里云的_________产品可以最好地解决该客户的存储问题,同时在需要的时候快速读取到历史的视频数据。', '对象存储OSS', '归档存储(Archive Storage)', '内容分发网络CDN', '多台云服务器ECS', 'A', '4', 1);
INSERT INTO `question` VALUES (168, 'ACP云计算', '阿里云负载均衡SLB中提供了证书上传和管理的服务,在需要进行加密传输时,用户可以将证书上传到负载均衡SLB实例,在创建监听的时候绑定证书。证书是为了支持协议。', 'HTTP', 'HTTPS', 'TCP', 'PPPoE', 'B', '4', 1);
INSERT INTO `question` VALUES (169, 'ACP云计算', '购买了阿里云的云服务器ECS实例之后,对于您来说需要承担的维护工作是_________。', '磁盘设备的维护', '硬件服务器的维护', '应用软件的升级', '网络设备的维护', 'C', '4', 1);
INSERT INTO `question` VALUES (170, 'ACP云计算', '您已经创建好了一块新的块存储,想要通过阿里云的管理控制台进行磁盘挂载到云服务器ECS实例上的操作, 却找不到想挂载的云服务器实例,一定不是因为_________原因引起的。', '块存储与云服务器ECS实例在同一个可用区', '云服务器ECS实例已经释放', '块存储与云服务器ECS实例不在一个可用区', '块存储与云服务器ECS实例在同一个地域', 'A', '4', 1);
INSERT INTO `question` VALUES (171, 'ACP云计算', '阿里云的负载均衡SLB可以把用户请求分发到多台云服务器ECS实例上去,其中“加权轮询模式”的转发策略是指。', '将访问请求分发给目前负载最低的后端云服务器ECS实例', '将等待最久的访问请求分发给后端云服务器池中处理能力最强的ECS实例', '将访问请求按照后端云服务器ECS实例的权重依次分发', '将优先级最高的访问请求优先分发给后端云服务器ECS实例', 'C', '4', 1);
INSERT INTO `question` VALUES (172, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)提供了“弹性自愈”功能,能保持伸缩组中的云服务器ECS实例都处在正常状态,从而使业务始终保持正常的负载,提高系统的稳定性。关于“弹性自愈”,以下说法中正确的是_________?', '如果伸缩组中某台ECS实例状态不是运行中,阿里云弹性伸缩首先会自动重启该ECS,重启成功后继续保持在伸缩组中,如果重启失败,则会使用指定的镜像恢复该ECS实例到初始状态,继续保持在伸缩组中', '如果伸缩组中某台ECS实例状态不是运行中,则该ECS实例会被移出伸缩组,同时自动创建一台新的ECS实例,添加到伸缩组中来', '如果伸缩组中某台ECS实例的状态不是运行中,阿里云弹性伸缩会启动ECS实例的故障检查与修复进程, 对ECS实例进行自动修复', '如果伸缩组中某台ECS实例状态不是运行中,阿里云弹性伸缩首先会自动重启该ECS,重启成功后继续保持在伸缩组中,如果重启失败,则从伸缩组中移除,同时创建新的ECS实例键入到伸缩组中来', 'B', '4', 1);
INSERT INTO `question` VALUES (173, 'ACP云计算', '小张管理的网站选择部署在阿里云的云服务器ECS实例上,最近发现一个现象,网站首页的宣传视频在某些区域播放速度良好,但是在其它地区播放速度很慢。为了解决此问题,你会选择阿里云_________产品。', '负载均衡SLB', '对象存储OSS', '云数据库RDS', '内容分发网络CDN', 'D', '4', 1);
INSERT INTO `question` VALUES (174, 'ACP云计算', '某阿里云公共云用户,购买了一台云服务器ECS实例,部署了tomcat,用_________方法可以最简单的监控该实例的tomcat的8080端口是否访问正常。', '购买第三方的监控工具', '每小时间登录一次云服务器ECS实例,用命令行查看', '在阿里云的云监控的站点监控中创建探测点,监控该端口的状态', '自己写脚本探测,然后上报数据到云监控', 'C', '4', 1);
INSERT INTO `question` VALUES (175, 'ACP云计算', '云计算服务商都支持用户以Web方式自助获得计算资源并对其进行管理。为了提供更好的开发性,云计算平台都支持通过API开放服务,这些API最不可能通过哪个协议进行开放?', 'Restful', 'PPPoE', 'HTTP', 'Web Service', 'B', '4', 1);
INSERT INTO `question` VALUES (176, 'ACP云计算', '在一些业务场景下,可以通过购买按量付费的云服务器ECS实例来满足特定时间段内需要更多资源的需求, 可以通过设定自动释放时间的方式来保证按量付费的资源按预期释放。下列关于该操作说法准确的是。', '只能在云服务ECS实例创建的时候进行自动释放时间的设置', '只能设置一次ECS实例的自动释放时间', '可以在取消之前重新设置的ECS实例的自动释放时间', 'ECS实例的自动释放时间一旦设置不能取消', 'C', '4', 1);
INSERT INTO `question` VALUES (177, 'ACP云计算', '阿里云对象存储OSS中所有数据都在Bucket中,当一个OSS bucket设置成_________权限,意味着该OSS Bucket仅允许其他用户来访问(GET Object)属于该Bucket下的object。', '私有', '私有读', '公共读写', '公共读', 'D', '4', 1);
INSERT INTO `question` VALUES (178, 'ACP云计算', '使用阿里云OSS产品实现在线的音视频内容直播时,必须要和阿里云的_________产品一起配合实现。', '多媒体转码服务MTS', '内容分发网络CDN', '云数据库RDS', '负载均衡SLB', 'A', '4', 1);
INSERT INTO `question` VALUES (179, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。用OSS管理的文件可以很方便地对外提供分享,分享前点击文件后面的“获取地址”文字链接即可得到当前文件的地址,这个分享使用的是_________应用层(七层)协议。', 'HTTP', 'FTP', 'SMTP', 'TCP', 'A', '4', 1);
INSERT INTO `question` VALUES (180, 'ACP云计算', '如果您不再需要某个阿里云的按量付费的云服务器ECS实例,可以将其释放。关于按量付费的实例释放的操作中,错误的是_________。', '您可以再次设置自动释放的时间并覆盖前一次的设置', '通过定时释放设置为您的按量付费实例安排释放计划,选择一个未来的时间释放资源', '不可以随时释放按量付费的实例', '可以立即释放该按量付费的实例', 'C', '4', 1);
INSERT INTO `question` VALUES (181, 'ACP云计算', '某阿里云负载均衡SLB实例下,后端多台云服务器ECS实例配置的权重都一样,但是实际上ECS实例负载却不一样。可能是因为_________。', 'ECS实例在不同的地域(Region),网络延迟不同', '采用的转发规则不同', '开启了会话保持功能', '开启了获取真实IP的功能', 'C', '4', 1);
INSERT INTO `question` VALUES (182, 'ACP云计算', '您的阿里云的云服务器ECS实例的系统盘使用的SSD云盘,需要新增数据盘时不能使用磁盘类型。', '本地SSD盘', '高效云盘', 'SSD云盘', '普通云盘', 'A', '4', 1);
INSERT INTO `question` VALUES (183, 'ACP云计算', '作为某初创企业的技术负责人,阿明将Java应用程序、图片视频等静态文件都部署在了一台ECS实例上,提供服务。随着业务发展和用户量的增加,发现存储在ECS实例上的静态文件访问速度不能达到预期的要求。 您建议选择阿里云的产品可以快速解决静态文件的访问速度问题。', '云数据库RDS', '负载均衡SLB', '对象存储OSS', '弹性伸缩Auto Scaling', 'C', '4', 1);
INSERT INTO `question` VALUES (184, 'ACP云计算', '某O2O网站基于阿里云的云服务器ECS、负载均衡SLB以及弹性伸缩(Auto Scaling)搭建了线上系统,其中ECS用来处理用户响应,SLB用来分发流量,弹性伸缩负责动态分配资源以满足突发的业务变化,三个产品配合可以提供稳定、便捷、低成本的服务。现在想要增加云数据库RDS到该架构中来,用来保存用户访问时产生的数据。以下说法中正确的是_________?', '可以把RDS实例手工添加到伸缩组,RDS实例可以保存用户访问的数据', '如果在伸缩组中指定了RDS实例,则弹性伸缩会自动把伸缩组中的ECS实例的IP地址添加到RDS的白名单中去,无需手工设置', '对RDS实例所在的地域(Region)没有硬性规定,但是建议和弹性伸缩在同一个地域,可以减小网络传输的延迟,提升响应速度', '可以把RDS实例直接挂在SLB下,由SLB按照转发规则分配任务到RDS实例进行处理', 'B', '4', 1);
INSERT INTO `question` VALUES (185, 'ACP云计算', '使用阿里云的负载均衡SLB实例时,合理的会话保持配置可以实现与某个连接(Session)相关的所有应用请求能够由同一台后端云服务器ECS实例进行处理,维持业务的延续性。四层服务的会话保持是基于_________实现的。', '源IP', '负载均衡IP', 'cookie', '用户的UUID', 'A', '4', 1);
INSERT INTO `question` VALUES (186, 'ACP云计算', '使用阿里云的负载均衡SLB实例时,合理的会话保持配置可以实现与某个连接(Session)相关的所有应用请求能够由同一台后端云服务器ECS实例进行处理,维持业务的延续性。七层服务的会话保持是基于_________实现的。', 'cookie', '源IP', '负载均衡IP', '用户的UUID', 'A', '4', 1);
INSERT INTO `question` VALUES (187, 'ACP云计算', '阿里云的专有网络VPC可以帮助您基于阿里云的产品构建出一个隔离的网络环境。通过与_________连接方式结合,可以实现专有网络VPC与传统数据中心组成一个按需定制的网络环境。', 'VPN', '负载均衡SLB', '域名解析DNS', '云监控', 'A', '4', 1);
INSERT INTO `question` VALUES (188, 'ACP云计算', '阿里云的弹性公网IP支持与云服务器ECS实例的动态绑定和解绑。下列关于弹性公网IP说法中错误的是。', '弹性公网IP只能绑定在同一个地域的特定可用区的VPC类型的云服务器ECS实例上', '弹性公网IP不能绑定在经典网络的云服务器ECS实例上', '1个弹性公网IP只能绑定到1个ECS实例上', '弹性公网IP只能绑定在相同地域的VPC类型的云服务器ECS实例上', 'A', '4', 1);
INSERT INTO `question` VALUES (189, 'ACP云计算', '关于弹性IP说法错误的是_ 。', '可以单独购买一个弹性公网IP,而不是与其他计算资源或存储资源绑定购买', '弹性公网IP支持绑在经典网络的ECS实例上', '弹性公网IP能动态绑定到不同的ECS实例上,绑定和解绑时无需停机', '弹性公网IP只能绑定在相同Region的VPC类型的云产品实例上', 'B', '4', 1);
INSERT INTO `question` VALUES (190, 'ACP云计算', '您基于阿里云的云服务器ECS实例部署了Mysql数据库,随着业务量的不断上涨,您需要通过读写分离的方式来改善数据库的性能,可以采用阿里云的_________产品来快速解决该问题。', '对象存储OSS', '本读SSD盘', '云数据库RDS', '表格存储', 'C', '4', 1);
INSERT INTO `question` VALUES (191, 'ACP云计算', '您拥有香港地域可用区A、华北1(青岛)地域可用区B和华南1(深圳)可用区B的云服务器ECS实例,现在希望通过购买新的块存储挂载到已有的云服务器ECS实例上进行磁盘空间的扩容,肯定不能购买_________可用区的块存储。', '华北1(青岛)地域可用区A', '华北1(青岛)地域可用区B', '香港地域可用区A', '华南1(深圳)地域可用区B', 'A', '4', 1);
INSERT INTO `question` VALUES (192, 'ACP云计算', '您的应用系统中存在大量的非结构化数据,之前采用阿里云的云服务器ECS实例加云数据盘的方式进行存储, 随着数据的不断膨胀,维护成本和拥有成本越来愈高。您希望将目前存储在云服务器ECS实例的数据盘上的大量非结构化的数据转存到其他的服务上去,并使用新的服务提供后续的数据存储服务,从而实现更好的节省运维、管理及存储本身的成本,采用_________方式最为经济高效。', '将数据转移到表格存储中,提供低成本、高并发、低延时的海量数据存储与在线访问', '申请OSS bucket,使用闪电立方将磁盘上的数据转移到OSS服务中进行存储', '在云服务所在的地域申请OSS bucket,使用OssImport将磁盘上的数据转移到OSS服务中进行存储', '将磁盘数据存储下载到本地数据中心的磁带库中进行存储', 'C', '4', 1);
INSERT INTO `question` VALUES (193, 'ACP云计算', '您启动了一台新的阿里云的云服务器ECS实例,要求此实例只允许开放80端口的公网访问。可以通过以下哪个操作来实现此要求而不影响其它实例?', '在该实例所在的默认安全组添加一条公网访问规则仅允许80端口的公网入流量', '在该实例所在的默认安全组添加一条公网访问规则仅允许80端口的公网出访问,并添加一条公网访问规则不允许所有的公网出流量', '新建一个安全组,并新增一条安全组规则允许80端口的公网入流量,将该云服务器实例放入该安全组', '新建一个安全组,并新增一条安全组规则允许80端口的公网入流量,将该云服务器实例放入该安全组, 并从原先的默认安全组挪出', 'D', '4', 1);
INSERT INTO `question` VALUES (194, 'ACP云计算', '您在使用阿里云的云服务器ECS实例的过程中,如果进行_________操作一定不会导致云服务器ECS实例出现异常。', '修改上层应用的网络协议', '自行编译系统内核', '在ECS实例上部署虚拟化程序', '随意更改系统分区下目录的权限', 'A', '4', 1);
INSERT INTO `question` VALUES (195, 'ACP云计算', '公网IP可以实现阿里云的云服务器ECS实例与Internet之间互访。下列对于云服务器ECS实例的公网IP描述错误的是_________。', '无论您选择何种计费方式,在购买实例的时候,您都需要选择公网的带宽上限', '手工修改ECS实例的公网IP后,该ECS实例的网络通信会受到影响', '公网IP可用于不同地域的云服务器之间互访', '云服务器ECS实例更换系统盘时,公网IP地址也会随之变更', 'D', '4', 1);
INSERT INTO `question` VALUES (196, 'ACP云计算', '您发现某台阿里云的云服务器ECS实例无法连接到互联网,不可能是_________原因造成的。', '您修改了云服务器ECS实例访问公网的安全组策略', '自行修改过公网IP', '在云服务器ECS实例中将公网IP对应的网卡关闭了', '云服务器ECS实例的网口松了', 'D', '4', 1);
INSERT INTO `question` VALUES (197, 'ACP云计算', '您的应用系统部署在阿里云的云服务器ECS实例上,系统每天晚上会进行当天运营数据的报表统计,在凌晨3点钟会出现短暂的统计服务响应变慢的情况,但很快会自动消失,很可能是云服务器ECS实例自身的操作引起的。', '云服务器ECS实例自动休眠了', '云服务器ECS实例的自动快照的时间设置在每天凌晨3点', '云服务器ECS实例的自动镜像服务设置在凌晨3点', '互联网网络不稳定', 'B', '4', 1);
INSERT INTO `question` VALUES (198, 'ACP云计算', '作为某初创企业的技术负责人,阿明将Java应用程序、图片视频等静态文件都部署在了一台ECS实例上,提供服务。随着业务发展和用户量的增加,发现存储在ECS实例上的静态文件访问速度不能达到预期的要求。 您建议选择阿里云的_________产品可以快速解决静态文件的访问速度问题。', '负载均衡SLB', '云数据库RDS', '对象存储OSS', '弹性伸缩Auto Scaling', 'C', '4', 1);
INSERT INTO `question` VALUES (199, 'ACP云计算', '您基于阿里云的云服务器ECS实例部署了Mysql数据库,随着业务量的不断上涨,您的运维工作不断增加, 包括数据库的备份、主备数据库之间的数据同步等问题,可以采用阿里云的_________产品来快速解决该问题。', '开放搜索服务OpenSearch', '云数据库RDS', '对象存储OSS', '表格存储', 'B', '4', 1);
INSERT INTO `question` VALUES (200, 'ACP云计算', '某视频公司,使用负载均衡SLB将用户的访问请求分发到30台云服务器ECS实例上去,由这些配置相同的ECS实例来响应用户的请求。每天晚上20:00到次日凌晨2:00期间,业务量会大幅增加,基本上会比其他时间段增加1倍左右,为了能正常响应用户的请求,从成本和容易实现的角度,以下哪种解决办法最可取?', '使用阿里云弹性伸缩(Auto Scaling),通过合适的配置,自动增加ECS实例或者减少ECS实例', '使用自定义的镜像,手工创建30台云服务器ECS实例,在每天晚上20:00前,手工增加到SLB中去,次日凌晨2:00再从SLB中移除', '基于自定义的镜像创建30台云服务器ECS实例,添加到SLB中去,通过设置权重,让这些ECS实例在每天晚上20:00到次日凌晨2:00之间可以响应用户需求,其他时间段权重修改为0', '使用阿里云弹性伸缩(Auto Scaling),通过合适的配置,自动提升每台ECS实例的规格', 'A', '4', 1);
INSERT INTO `question` VALUES (201, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)可以通过设置定时任务来定时地增加或减少ECS实例,那么如果多个定时任务在同一时间内触发执行同一个伸缩组的伸缩规则,下面关于定时任务执行的说法正确是?', '会选取最近创建的定时任务执行,排在后面的定时任务会在LaunchExpirationTime内自动重试定时触发', '会选取最早创建的定时任务执行,排在后面的定时任务会在LaunchExpirationTime内自动重试定时触发', '会选取最近创建的定时任务执行,排在后面的定时任务则会取消', '多个定时任务部分先后将同时执行', 'B', '4', 1);
INSERT INTO `question` VALUES (202, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)是根据用户的业务需求和策略,自动调整其弹性计算资源的管理服务,可以自动调整_________。', '云服务器ECS实例的个数', '云服务器ECS实例的CPU个数', '云服务器ECS实例的内存大小', '云服务器ECS实例的带宽', 'A', '4', 1);
INSERT INTO `question` VALUES (203, 'ACP云计算', '阿里云对象存储OSS中所有数据都保存在Bucket中,当一个OSS bucket设置成权限,意味着该OSS Bucket仅允许其他用户来访问(GET Object)属于该Bucket下的object。', '公共读写', '公共读', '私有', '私有读', 'B', '4', 1);
INSERT INTO `question` VALUES (204, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。在OSS Bucket中所有元素都是以_________来存储的。', 'key', 'value', 'object', 'bucket', 'C', '4', 1);
INSERT INTO `question` VALUES (205, 'ACP云计算', '如果阿里云对象存储OSS客户想对访问OSS的IP进行限制,只允许有限的几个IP地址可以下载OSS上的文件,可以通过_________配置方法实现。', '直接使用OSS的IP白名单功能', 'OSS本身不提供IP黑白名单,可以先使用CDN加速,然后在CDN上配置IP白名单', '直接使用OSS的IP黑名单功能', 'OSS本身不提供IP黑白名单,可以在OSS前面部署SLB,然后在SLB上配置IP白名单', 'A', '4', 1);
INSERT INTO `question` VALUES (206, 'ACP云计算', '开通阿里云对象存储OSS服务后,您首先需要使用OSS管理控制台或OpenAPI创建存储空间Bucket以用来存储文件,以下有关Bucket的说法_________是正确的。', 'Bucket的名称要求全局唯一,且创建后不支持更改', 'Bucket的名称要求全局唯一,创建后支持更改', 'Bucket的名称只要求在本帐号内唯一,创建后支持更改', 'Bucket的名称要求在本帐号内唯一,且创建后不支持更改', 'A', '4', 1);
INSERT INTO `question` VALUES (207, 'ACP云计算', '在使用阿里云的负载均衡SLB实例时,可以对后端云服务器ECS实例配置不同的权重,权重越大的ECS实例, 会被分到越多的负载。某个SLB实例的后端服务器池中有5个ECS实例,都处在健康状态,其中一台实例ecs_inst1权重被设置成了100,以下说法中正确的是。', '由于不确定其他4台ECS实例的权重配置,所以无法判断ecs_inst1会被分到多大比例的负载', '100%的负载都会被分发到ecs_inst1上去,其余4台ECS实例会处于空闲状态', '根据SLB的工作原理,ecs_inst1将会被分到大约20%左右的负载', '根据外部请求中的请求级别参数判断,所有请求级别参数为100的,都会被转发至ecs_inst1', 'A', '4', 1);
INSERT INTO `question` VALUES (208, 'ACP云计算', '某在线考试网站提供在线的认证考试服务,用户登录网站后,需要在120分钟时间内完成答卷。该网站使用了阿里云的云服务器ECS以及负载均衡SLB实现,上述场景中,如果要保持120分钟之内用户和服务器之间的连接不间断,可以通过负载均衡SLB的_________功能实现。', '会话保持', '加权轮询', '弹性自愈', '会话同步', 'A', '4', 1);
INSERT INTO `question` VALUES (209, 'ACP云计算', '负载均衡通过设置虚拟服务地址,将添加的ECS实例虚拟成一个高性能、高可用的应用服务池,并根据转发规则,将来自客户端的请求分发给云服务器池中的ECS实例。下列关于SLB转发规则说法错误的是?', 'SLB支持“轮询模式”转发规则', 'SLB支持“最小连接数模式“转发规则', 'SLB四层流量走默认路由', '一般情况下,默认路由在有内网的情况下,先走内网,无内网再走外网', 'D', '4', 1);
INSERT INTO `question` VALUES (210, 'ACP云计算', '使用阿里云的负载均衡SLB实例时,可以通过健康检查来判断后端云服务器ECS实例上的可用性。针对7层服务,负载均衡SLB实例的健康检查机制为:默认由负载均衡SLB实例向后端云服务器ECS实例的内网IP地址的应用服务器配置的缺省首页发起请求,如果返回“200 OK”,将认为后端云服务器ECS实例运行正常,否则视为运行异常。', 'http', 'ssh', 'telnet', 'ICMP', 'A', '4', 1);
INSERT INTO `question` VALUES (211, 'ACP云计算', '阿里云提供公网和私网两种类型的负载均衡服务。下列关于SLB实例属性配置说法错误的是?', '如果用户不指定一个容易识别的SLB名称,则系统会自行分配一个名称', 'LoadBalancerId是用于识别用户SLB实例的唯一标识', '系统自动选择公网或者私网访问', '不同类型的SLB,系统会分配不同的服务地址给用户', 'C', '4', 1);
INSERT INTO `question` VALUES (212, 'ACP云计算', '在阿里云的负载均衡SLB的管理控制台中,可以根据一些监控的统计指标来设置报警信息,常见的如流量、数据包、连接数等,设置报警阈值,一旦触发了报警条件,就会通过多种方式进行报警。目前还不支持_________的报警方式。', '电话报警', '邮件报警', '短信报警', '钉钉报警', 'A', '4', 1);
INSERT INTO `question` VALUES (213, 'ACP云计算', '阿里云的负载均衡SLB可以通过流量分发扩展应用系统对外的服务能力,通过消除单点故障提升应用系统的可用性。以下关于负载均衡SLB特点的说法中错误的是。', '跨地域容灾:支持同一个负载均衡SLB实例分流负载到跨多个地域(Region)的云服务器ECS实例,可用性更高', '扩展性:支持删除和添加后端云服务器,实现无缝伸缩', '安全:结合阿里云的安全产品提供多种安全防护,使应用更加安全可靠', '成本:和传统硬件负载均衡器相比,成本大幅下降', 'A', '4', 1);
INSERT INTO `question` VALUES (214, 'ACP云计算', '阿里云的负载均衡SLB对应的后端服务器池中包含多台云服务器ECS实例时,若ECS实例对外服务能力不同,为了更充分的利用ECS实例的处理能力,以下说法中正确的是。', '给处理能力强的ECS实例配置更高的权重,权重越高意味着将有机会被分配到更多的访问请求', '按照轮询的模式分发任务,能力强的ECS实例一定能分配到更多的请求', '按照最小连接数的模式分发任务,能力强的ECS实例一定能分配到更多的请求', '负载均衡SLB的目的就是为了平衡后台ECS实例间的负载,无法让某些ECS实例分配到更多的请求', 'A', '4', 1);
INSERT INTO `question` VALUES (215, 'ACP云计算', '假设网络管理员需要随时随地通过公网对阿里云专有网络VPC下的多台云服务器ECS实例进行管理,通过_________方式可以既安全又低成本的实现。', '修改VPC的安全组策略,允许公网的访问', '其他答案都不对', '为每一台云服务器ECS实例绑定一个EIP,登录各个云服务器ECS实例进行管理', '在VPC内选择一台云服务器ECS实例作为堡垒机,申请一个EIP绑定到该ECS实例,通过该堡垒机进行其他云服务器ECS实例的管理', 'D', '4', 1);
INSERT INTO `question` VALUES (216, 'ACP云计算', '您希望通过管理控制台进行阿里云的专有网络VPC内的云服务器ECS实例的私网IP地址的修改,但发现操作的菜单不可用,是_________原因引起的。', '该云服务器ECS实例没有绑定EIP', '该云服务器ECS实例所在的VPC类型是默认专有网络', '该云服务器ECS实例不是运行状态', '没有针对该云服务器ECS实例进行停止操作,且该云服务器ECS实例不是停止状态', 'D', '4', 1);
INSERT INTO `question` VALUES (217, 'ACP云计算', '阿里云的专有网络VPC中用于连接VPC内的各个交换机的设备是_________。', '路由器', '路由表', '云服务器ECS.', '负载均衡SLB.', 'A', '4', 1);
INSERT INTO `question` VALUES (218, 'ACP云计算', '阿里云的安骑士是云盾提供的用来保护服务器的产品,以下_________不属于安骑士的功能。', '木马文件检查', '防WEB应用系统的密码破解', '异地登录报警', '防操作系统密码暴力破解', 'B', '4', 1);
INSERT INTO `question` VALUES (219, 'ACP云计算', '阿里云的云盾加密服务通过在阿里云上使用经国家密码管理局检测认证的硬件密码机,帮助用户满足数据安全方面的监管合规要求,保护云上业务数据的隐私性和机密性。云盾加密服务的密钥由_________负责保管。', '阿里云的系统管理员', '客户', '客户和阿里云的系统管理员', '阿里云的第三方合作伙伴', 'B', '4', 1);
INSERT INTO `question` VALUES (220, 'ACP云计算', '在服务器上手动安装阿里云的云盾客户端安骑士时需要_________权限。', '系统管理员权限', '系统普通用户权限', 'FTP服务权限', '数据库权限', 'A', '4', 1);
INSERT INTO `question` VALUES (221, 'ACP云计算', '阿里云的云监控(CloudMonitor)是一项针对阿里云资源和互联网应用进行监控的服务。下列关于云监控的说法准确的是?', '需要单独购买并付费,才可以开通', '不能通过管理控制台使用云监控服务', '支持监控未部署在阿里云产品上的网站', '不支持监控未部署在阿里云产品上的网站', 'C', '4', 1);
INSERT INTO `question` VALUES (222, 'ACP云计算', '某企业托管到传统IDC机房的服务器(包括windows和Linux两种操作系统),每天晚上有来自很多国家的IP地址对其管理密码进行尝试破解。为此管理员小王很是担心,因为工作需要,远程管理端口必须对公网开放。小王听说阿里云的云盾可以帮助客户提升安全防护的能力,想把系统迁到阿里云上。在阿里云上开通_________功能可以减少密码被暴力破解的可能。', '阿里绿网', '安骑士', 'DDoS基础防护', '态势感知', 'B', '4', 1);
INSERT INTO `question` VALUES (223, 'ACP云计算', 'D公司在线视频业务全国排名前五,整个业务系统全部建设在阿里云之上,使用的云产品有云服务器ECS、对象存储OSS、内容分发网络CDN。 最近发现有黑客恶意下载公司的正版视频,在国外社区里分发。 假设你是D公司的系统管理员,从技术上可以如何解决这个问题?', '启用阿里云CDN的IP黑名单功能', '启用阿里云CDN的URL鉴权功能', '启用阿里云OSS的Refer黑名单功能', '启用云服务顺的安全组功能', 'B', '4', 1);
INSERT INTO `question` VALUES (224, 'ACP云计算', '某公司很多系统都架构在阿里云上,使用了多个阿里云弹性伸缩(Auto Scaling)的伸缩组来实现动态资源管理。现有一台包年包月的云服务器ECS实例还未到期,且处于闲置状态,出于避免资源浪费的考虑,想把它更好的利用起来。以下说法中正确的是?', '可以把它手工添加到同一个地域(Region)的任意一个伸缩组中去', '如果该ECS的处理能力很强的话,可以加入到同一个可用区(Zone)的多个伸缩组中去,前提是要部署多个应用处理的环境', '只能把加到同一个可用区(Zone)的某一个伸缩组中去,前提是它的规格要符合该伸缩组伸缩配置规定的   规格', '只能把加到同一个地域(Region)的某一个伸缩组中去,前提是它的规格要符合该伸缩组伸缩配置规定的规格', 'A', '4', 1);
INSERT INTO `question` VALUES (225, 'ACP云计算', '您部署在阿里云云服务器ECS上的服务,希望能够通过阿里云的产品来提升该服务的可用性,以下哪种方案的可用性最高?', '在同一个地域的多个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', '在同一个地域的同一个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', '在不同地域的多个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', '在不同地域的同一个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', 'A', '4', 1);
INSERT INTO `question` VALUES (226, 'ACP云计算', '阿里云的云服务器ECS产品的应用非常广泛,既可以单独的作为Web服务器,也可以与其他阿里云产品配合提供多媒体解决方案。在多媒体、大流量的APP或网站应用场景下,云服务器ECS实例与阿里云的_________产品,可以实现在业务量增长时自动增加ECS实例,并在业务量下降时自动减少ECS实例,保证满足访问量达到峰值时对资源的要求,同时降低了系统资源的拥有成本。', '对象存储OSS', '内容分发网络CDN', '弹性伸缩Auto Scaling', '云监控', 'C', '4', 1);
INSERT INTO `question` VALUES (227, 'ACP云计算', '阿里云的云服务器ECS相对于传统物理服务器的一个重要特征是,可以更为便捷的进行服务器的配置升级。下列关于云服务器ECS无缝升级的说法错误的是_________。', '云服务器ECS支持变更CPU、内存和宽带升级', '云服务器ECS不支持新增数据盘', '升级带宽不需要云服务器ECS进行重启', '云服务器ECS的配置升级时,用户不需要重新部署环境或者迁移数据', 'B', '4', 1);
INSERT INTO `question` VALUES (228, 'ACP云计算', '某视频公司的服务部署在阿里云的云服务器ECS上,春晚或每周五热门节目来临时,如临大敌,又不想长期采购大量的云服务器ECS实例提供服务,可以采用阿里云提供的产品来低成本地解决这个问题。', '弹性伸缩Auto Scaling', '表格存储', '专有网络VPC', '对象存储OSS', 'A', '4', 1);
INSERT INTO `question` VALUES (229, 'ACP云计算', '随着业务的增长,您现有的阿里云的云服务器ECS实例的系统盘出现了存储资源不足的问题,阿里云提供了系统盘扩容的功能帮您解决该问题。下列关于扩容系统盘的操作说法错误的是。', '通过更换ECS实例的系统盘操作来实现系统盘的扩容', '扩容ECS实例的系统盘时需要停止您的实例,因此会短暂中断您的业务', '扩容系统盘之后您的ECS实例的IP地址会发生变化', '扩容系统盘后,用户创建的快照会保留', 'C', '4', 1);
INSERT INTO `question` VALUES (230, 'ACP云计算', '某新媒体公司通过手机APP提供新闻资讯服务,采用阿里云弹性伸缩(Auto Scaling)来实现动态添加或者减少云服务器ECS实例,来应对业务量的变化。该公司的业务人员和技术人员相互配合,估算出他们所需要的资源为闲时10台ECS,但是有几个时间段业务量会比较多,基本上集中在7:30到9:00,18:30到20:00, 随着时间点的不同,所需的ECS实例在10到20个之间动态变化。他们在伸缩组中将最小实例个数设置为10, 最大实例个数设置为20。从容易部署以及节省成本的角度来考虑,他们还需要配置一个什么模式的任务?', '动态模式', '定时模式', '固定数量模式', 'lazy模式', 'A', '4', 1);
INSERT INTO `question` VALUES (231, 'ACP云计算', '某视频直播公司采用阿里云弹性伸缩(Auto Scaling)来实现动态添加或者减少云服务器ECS实例,以应对业务量的变化。由于该公司的系统刚上线不久,没有历史数据做参考,同时也不能预估业务量的变化,他们希望通过ECS实例资源的使用情况(比如CPU利用率、系统负载Load等)来弹性伸缩计算资源。他们应该选择以下哪种伸缩模式?', '动态模式', '定时模式', '固定数量模式', 'lazy模式', 'A', '4', 1);
INSERT INTO `question` VALUES (232, 'ACP云计算', '某企业使用负载均衡SLB+云服务器ECS+弹性伸缩(Auto Scaling)的架构搭建网站,已经创建了伸缩组, 冷却时间设置为缺省值300秒,最小ECS数设置为1,最大ECS数设置为100,并设定了伸缩规则为“每次增加1台ECS”,同时配置报警任务:“统计周期”设为2分钟,“重复几次后报警”设置为3,每当CPU使用率平均值超过60%时,触发伸缩规则定义的伸缩活动,当前伸缩组中有10台ECS实例。该网站业务在长达1小时的时间段内发生了井喷式的爆发增长,CPU的使用率会一直高于60%,这一小时期间,每两次触发该报警任务的时间间隔最少为_________加上创建一台ECS实例的消耗时间。', '11分钟', '5分钟', '6分钟', '9分钟', 'C', '4', 1);
INSERT INTO `question` VALUES (233, 'ACP云计算', '小明是一个社交网站的架构师,创业初期用户量较少,每个注册客户上传的图片文件直接保存在了一台阿里云的云服务器ECS实例上。最近用户量爆增,图片存储的空间达到了3.5T,WEB服务器由原来的1台ECS实例扩展到了5台ECS实例,性能问题得到了解决,可是保存在ECS实例的图片无法在多台ECS之间共享读写。 阿里云的_________产品非常适合解决这个问题。', '对象存储OSS', '内容分发网络CDN', '归档存储(Archive Storage)', '负载均衡SLB', 'A', '4', 1);
INSERT INTO `question` VALUES (234, 'ACP云计算', '阿里云对象存储OSS提供了丰富的文件上传和下载接口,用户可以通过API方便地实现大容量存储空间的管理。如果您想建立一个大型图片分享站点,该网站允许大量用户通过WEB方式进行图片的上传和分享,可以使用阿里云的_________产品与OSS配合实现。', '负载均衡SLB', '云服务器ECS', '云数据RDS', '内容分发网络CDN', 'B', '4', 1);
INSERT INTO `question` VALUES (235, 'ACP云计算', '开通阿里云对象存储OSS服务后,您首先需要使用OSS管理控制台或OpenAPI创建Bucket来存储文件。创建Bucket时“所属地域”这个属性必须要指定,特别是您的云服务器ECS实例需要通过内网访问OSS Bucket时,必须选择与ECS实例相同的地域,以下有关“所属地域”的说法_________是正确的。', 'Bucket创建后,“所属地域”属性可随时更改', 'Bucket创建后,“所属地域”属性不可更改', 'Bucket创建后,如果未上传任何文件,则“所属地域”属性可以更改', 'Bucket创建后,要想修改“所属地域”属性,需申请特殊权限', 'B', '4', 1);
INSERT INTO `question` VALUES (236, 'ACP云计算', 'C公司基于阿里云对象存储OSS和云服务器ECS实例构建了一个网站,ECS实例与OSS之间有频繁的文件上传和下载需求。系统管理员小赵最近通过阿里云管理控制台中的费用中心发现OSS的流量费用增长很快,但是从ECS实例上部署的应用系统日志里没有发现网站访问量有明显增长。 请您帮小赵判断一下有可能是_________原因导致的。', '系统架构中缺少云数据库RDS', '可能是ECS实例通过公网地址调用OSS的API', 'ECS实例的系统配置不足,需要增加内存', '系统架构中缺少负载均衡SLB', 'B', '4', 1);
INSERT INTO `question` VALUES (237, 'ACP云计算', '阿里云OSS图片处理服务(Image Service) 是阿里云OSS对外提供的图片处理服务。用户将原始图片上传保存在OSS上,通过简单的RESTful接口,可以在任何时间、任何地点、任何互联网设备上对图片进行处理。 以下有关OSS图片处理服务说法正确的。', '可以处理来自于阿里云OSS以外的图片', '处理完的图片会保存在OSS中', '处理的图片是OSS用户上传到Bucket里的Object', '其它答案都正确', 'C', '4', 1);
INSERT INTO `question` VALUES (238, 'ACP云计算', '阿里云对象存储OSS支持分块上传操作,即可以将大文件进行分块处理后再分别上传,该操作适合在上传大文件或者网络较差的情况下使用。在分块上传的所有任务完成之后,如果没有调用Complete Multipart Upload接口,就会产生一些不完整的文件,这些不完整的文件会占用用户bucket的空间。为了方便用户删除这些不完整的文件,OSS专门提供了_________功能。', '在线批量删除', 'OpenAPI批量删除', 'Bucket清空', '碎片管理', 'D', '4', 1);
INSERT INTO `question` VALUES (239, 'ACP云计算', '某企业购买了一个负载均衡SLB实例,该实例下挂载了多台云服务器ECS实例,每个ECS实例上都部署了多个不同的站点,并使用不同域名访问。现在负载均衡SLB实例可以通过配置_________来实现对同一台ECS实例上不同站点的健康检查。', '主机头(Http Host)', 'ECS实例的IP和端口号', '会话保持使用的协议', '站点首页的URL', 'A', '4', 1);
INSERT INTO `question` VALUES (240, 'ACP云计算', '某企业的网站构建在阿里云的云服务器ECS实例、负载均衡SLB实例以及弹性伸缩(Auto Scaling)之上, 其中负载均衡SLB实例已和弹性伸缩绑定,且已开启健康检查。负载均衡检SLB实例检查出后端云服务器ECS实例ecs_inst1处于不健康状态,且ecs_inst1在弹性伸缩的伸缩组中。以下说法中正确的是?', '弹性伸缩的弹性自愈功能将会把ecs_inst1移出当前伸缩组,且会新创建一个ECS实例添加进来,该新添加实例会被SLB实例分配负载', '负载均衡SLB实例会将ecs_inst1的权重置为0,暂时不分配负载给它,但是会继续通过健康检查监控ecs_inst1的状态,一旦恢复,则将其重新启用,正常分配负载', '弹性伸缩的弹性自愈功能将会针对ecs_inst1启动错误检查和自动恢复功能,如果修复成功,ecs_inst1状态恢复正常,则会继续留在伸缩组中,且会被负载均衡SLB实例分配负载', '弹性伸缩会自动创建一个新的ECS实例,并将该新实例添加到伸缩组,接收负载均衡SLB实例分配的负载,ecs_inst1将会被保留在伸缩组中,等其状态恢复正常后,将会被继续加入SLB实例的后台服务器池', 'A', '4', 1);
INSERT INTO `question` VALUES (241, 'ACP云计算', '在使用阿里云负载均衡SLB的管理控制台时,如果需要添加某台云服务器ECS实例到某负载均衡SLB实例的后端服务器池中,ECS实例必须处于_________状态。', '运行中', '启动中', '已停止', '已创建', 'A', '4', 1);
INSERT INTO `question` VALUES (242, 'ACP云计算', 'SLB是将访问流量根据转发策略分发到后端多台云服务器(ECS实例)的流量分发控制服务。下列关于SLB说法错误的是?', 'SLB的服务能力与后端ECS的公网宽带规格无关', '同一组ECS不可以搭建多个网站并同时进行负载均衡', '在使用SLB的过程中随时调整后端ECS的数目', '不同操作系统的ECS可以同时做为SLB服务的后端服务器', 'B', '4', 1);
INSERT INTO `question` VALUES (243, 'ACP云计算', '负载均衡支持按带宽计费的负载均衡实例下的所有监听共享实例的总带宽,假如您购买了一个带宽峰值为10MB的负载均衡实例,并在该实例下创建了三个监听(监听A、监听B和监听C)。监听A的带宽峰值设置为4MB,另外两个监听没有设置带宽峰值,如果监听A和监听C一直没有流量进入,那么关于监听B的说法,以下说法正确的是。', '监听B最多也只能跑满6MB带宽', '监听B最多也只能跑满4MB带宽', '监听B最多也只能跑满10MB带宽', '监听B最多也只能跑满14MB带宽', 'A', '4', 1);
INSERT INTO `question` VALUES (244, 'ACP云计算', '阿里云的负载均衡SLB提供对多台后端服务器进行流量分发的服务。对于后端服务器的选择,以下说法中正确的是_________。', '只能是阿里云的云服务器ECS实例', '所有能通过公网访问的服务器均可', '所有阿里云的云产品实例', '支持AWS的EC2', 'A', '4', 1);
INSERT INTO `question` VALUES (245, 'ACP云计算', '某创业团队刚刚成立,几个核心的程序员分布在5个不同的城市异地办公,新发布的线上系统使用了阿里云的云数据库RDS。 由于程序员需要在异地用动态IP连接RDS,所以管理员只能打开RDS的公网连接地址和端口,并每天多次从管理控制台查看RDS的连接信息,防止黑客远程登录RDS。 管理员通过_________方法可以最简单地降低这种担忧和人工防护的成本。', '开通云盾态势感知服务,一旦有非常用IP登录数据库rds即可收到报警信息', '开通云盾DDoS基础防护,可以防止RDS被非法登录', '开启阿里云云监控,一旦发现RDS外网流量特别大就立即登录控制台查看是否是非法连接', '内容安全(原名:绿网),可以自动过滤非法的连接', 'A', '4', 1);
INSERT INTO `question` VALUES (246, 'ACP云计算', '采用云计算服务与传统自建IT系统不同,相比传统自建方式,云计算带来了巨大的便利性。以阿里云服务器ECS为例,这些便利性中不包括?', '用户无需再去维护和管理硬件', '获得服务器实例在几分钟内而不是数天数周', '用户无需参与任何安全管理工作', '用户按照需要获得计算量而不是按照峰值设计', 'C', '4', 1);
INSERT INTO `question` VALUES (247, 'ACP云计算', '阿里云的云盾安骑士可以记录远程访问服务器的源IP地址,并对频繁连接的可疑IP进行屏蔽,日常运维过程中需要通过_________功能对系统管理员常用的IP进行设置。', '安全组', 'webshell云查杀', 'IP白名单', '常用登录地管理', 'C', '4', 1);
INSERT INTO `question` VALUES (248, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)是根据用户的业务需求和策略,自动调整其弹性计算资源的管理服务。为了在弹性伸缩创建的ECS实中可以直接正常处理用户请求,无需任何额外的系统调度和配置,弹性伸缩的云服务器ECS实例中部署的应用必须满足_________。', '无状态且可横向扩展的', '有状态', '无状态且符合幂等性', '所有数据保存在ECS实例磁盘上', 'A', '4', 1);
INSERT INTO `question` VALUES (249, 'ACP云计算', '在使用阿里云弹性伸缩(Auto Scaling)时,需要先创建伸缩组,然后会基于伸缩组提供按照策略或者业务需求弹性伸缩计算资源的服务。当某个伸缩组不再使用时,可以将其删除,其中_________不依赖于伸缩组的生命周期,即不会随着伸缩组的删除而被删除。', '定时任务', '伸缩配置', '伸缩规则', '伸缩活动', 'A', '4', 1);
INSERT INTO `question` VALUES (250, 'ACP云计算', '您基于阿里云的云服务器ECS实例部署了Mysql数据库,随着业务量的不断上涨,您的运维工作不断增加,包括数据库的备份、主备数据库之间的数据同步等问题,可以采用阿里云的_________产品来快速解决该问题。', '表格存储', '云数据库RDS', '开放搜索服务OpenSearch', '对象存储OSS', 'B', '4', 1);
INSERT INTO `question` VALUES (251, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。可以广泛应用于各种场景。例如:图片和音视频类应用的海量存储、静态网站、应用中的静态网页。以下_场景适合使用OSS服务。', '客户A是一个网店老板,每天产生10Gb的高清图片,需要稳定可靠的存储', '客户B的一个在线应用,采用自建MySQL数据库保存订单信息,该应用每天产生2000万条订单数据,预计2周后存储容量达到服务器上限,希望通过阿里云产品解决订单存储问题', '客户C想对当前业务系统积累了5年的数据进行数据挖掘,为新财年的营销策略制定提供数据支持', '客户D的小型电子商务系统由于业务量爆增,计算能力明显不足,每天业务高峰期时服务器的CPU利用率都超过90%,正在寻找优化方案', 'A', '4', 1);
INSERT INTO `question` VALUES (252, 'ACP云计算', '云计算的一个显著特点是提供按需供应、按需付费的服务。所有的资源以资源池的方式存在,提供配置化的访问方式,资源类型包括网络、服务器、存储、应用和服务。关于资源池的说法错误的是?', '资源池无法突破单台物理机所拥有的资源', '资源池包含了多个相同类型的资源', '资源池是实现计算服务化的基础', '不同的资源池有不同的资源池优化方法', 'A', '4', 1);
INSERT INTO `question` VALUES (253, 'ACP云计算', '共享存储是指是多台服务器访问(读写)同一个存储设备的同一个分区,即系统允许多个用户共享同一份文件。 如阿里云的文件存储(Network Attached Storage,简称NAS)就提供了共享存储的服务,以下说法错误的是?', '多个云服务器实例可以同时访问同一个共享文件系统', '公共云计算中使用的存储都是共享存储', '适合跨多个云服务器实例部署的应用程序访问相同数据来源的应用场景', '支持主流的应用程序及工作负载可无缝配合使用,无需任何修改即可读写数据', 'B', '4', 1);
INSERT INTO `question` VALUES (254, 'ACP云计算', '阿里云的云盾态势感知用大数据分析技术,按攻击者的视角来建立安全防御体系,颠覆了传统单一的入侵和漏洞检测,让用户对安全“可见、可控、可管”。 以下关于态势感知功能的描述错误的是_________。', '可以发现webshell,恶意病毒攻击', '利用大数据,可对高级攻击者使用的0day漏洞攻击进行防御', '实时捕捉云服务器ECS实例中的网络连接,利用情报信息做异常识别,发现僵尸网络', '是一个针对阿里云服务器ECS的托管服务', 'D', '4', 1);
INSERT INTO `question` VALUES (255, 'ACP云计算', '阿里云的负载均衡SLB提供对多台云服务器ECS实例进行流量分发的服务,可以扩展应用系统对外的服务能力,并且可以消除单点故障提升应用系统的可用性。结合共同使用,可以提供WAF及防DDoS攻击能力,使应用环境更安全。', '云盾的DDoS高防IP', '云监控', '云盾的态势感知', '云盾的先知计划', 'A', '4', 1);
INSERT INTO `question` VALUES (256, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling),是根据用户的业务需要和策略,自动调整其弹性计算资源的管理服务。支持用户添加已有的云服务器ECS实例到伸缩组中去,但是对云服务器ECS实例所在地域有一定要求。以下说法中正确的是?', '该实例和伸缩组必须在同一个地域(Region)中,对可用区(Zone)没有要求', '该实例和伸缩组必须在同一个地域(Region)的同一个可用区(Zone)中', '对该实例和伸缩组的地域(Region)和可用区(Zone)没有要求,', '一个伸缩组最多可以跨两个地域(Region),添加完ECS实例后,要保证伸缩组中的所有ECS实例所在的区域不超过两个', 'A', '4', 1);
INSERT INTO `question` VALUES (257, 'ACP云计算', '某大型互联网公司,原来的业务都运行在物理机上,并且有自建的监控和报警系统,最近准备把一部分业务迁到阿里云上,并通过云监控来监控云服务器ECS实例并设置报警。用户需要才能快速将云监控现有的报警通知对接到自建的报警系统,并在自建的报警系统中综合处理物理机和云服务器ECS实例的报警信息。', '无法实现', '使用云监控提供的事件订阅功能,从消息队列消费报警信息', '调用云监控提供的查询数据的接口,定时查询,然后自己写code判断指标是否异常', '调用查询旺旺信息的API,获取发送到旺旺的报警通知', 'C', '4', 1);
INSERT INTO `question` VALUES (258, 'ACP云计算', '在使用阿里云弹性伸缩(Auto Scaling)时,希望能根据计算资源的使用情况来增加或者减少云服务器ECS实例,如当CPU利用率小于等于30%时则减少一台云服务器ECS实例,首先配置伸缩规则为“减少1台ECS”,然后还需要_________来实现。', '创建报警任务', '设置伸缩组的属性', '创建定时任务', '创建伸缩配置', 'A', '4', 1);
INSERT INTO `question` VALUES (259, 'ACP云计算', '在众多的网络攻击类型中\"钓鱼网站\"是最常见的攻击之一。钓鱼网站的贞面看上去与真实站界面完全一致,要求访问者提交账号和密码,以此来骗取用户银行或信用卡账号、密码等私人资料。以有关钓鱼网站的传播手段,说法错误的是', '通过QQ、旺旺等客户端聊天工具发送传播钓鱼网站链接', '在搜索引擎、中小网站投放广告,吸引用户点击钓鱼网站链接', '银行在官方网站的显著位置发布钓鱼网站链接', '通过Email、论坛、博客、SNS网站批量发布钓鱼网站链接', 'C', '4', 1);
INSERT INTO `question` VALUES (260, 'ACP云计算', '您成功地创建了一台阿里云的云服务器ECS实例,准备进行应用的部署。在部署应用之前,如何保证该云服务器ECS实例具备防御500M以内的DDoS攻击的能力?', '阿里云的云盾产品提供免费的基础DDoS防护服务,不需要单独购买', '需要另行购买DDoS防护服务才可以具备防护功能', '需要您自己架设防DDoS服务', '其他都不对', 'A', '4', 1);
INSERT INTO `question` VALUES (261, 'ACP云计算', '您需要进行某台云服务ECS实例的磁盘快照的创建,只有该云服务器ECS实例处于状态下时才可以进行快照创建的操作。', '启动中', '创建中', '运行中', '停止中', 'C', '4', 1);
INSERT INTO `question` VALUES (262, 'ACP云计算', '通过_________操作,可以实现在阿里云的同一个专有网络VPC下的三个交换机下的云服务器ECS实例之间内网互相不能访问,但并不影响该专有网络VPC的内网其他流量?', '创建三个安全组,分别包含三个交换机下的所有云服务器ECS实例,先为每个安全组设置允许所有网段访问的规则,再设置禁止另外两个交换机所在网段访问的规则(优先级要高于前者)', '创建三个安全组,分别包含三个交换机下的所有云服务器ECS实例,先为每个安全组设置允许所有网段访问的规则,再设置禁止另外两个交换机所在网段访问的规则(优先级要低于前者)', '创建一个安全组包含三个交换机下的所有云服务器ECS实例,先为该安全组设置允许所有网段访问的规则,再设置禁止另外两个交换机所在网段访问的规则(优先级要高于前者)', '其他答案都不对', 'A', '4', 1);
INSERT INTO `question` VALUES (263, 'ACP云计算', 'A公司将最近两年的内部培训录像都保存在阿里云对象存储OSS中,以方便员工在需要的时候学习。随着智能手机的普及,很多员工希望通过手机来观看视频,但是保存在OSS中的视频格式无法在手机上直接播放,遇到这种问题时客户可以使用阿里云的产品很高效地解决。', '云服务器ECS', '阿里云媒体转码(Media Transcoding,简称MTS)', '云数据库RDS', '负载均衡SLB', 'B', '4', 1);
INSERT INTO `question` VALUES (264, 'ACP云计算', '服务器安全托管是阿里云云盾的一项高级服务,服务内容包括专家人工安全体检、清除木马,VIP技术支持等, 此服务是针对阿里云_________产品提供的。', '云数据库RDS', '云服务器ECS', '负载均衡SLB', '专有网络VPC', 'B', '4', 1);
INSERT INTO `question` VALUES (265, 'ACP云计算', '跨站脚本攻击(Cross-site scripting,通常简称为XSS)可被用于进行窃取隐私、钓鱼欺骗、偷取密码、传播恶意代码等攻击行为。请问XSS攻击主要发生在_________。', '用户浏览器', 'WEB应用的服务器', '数据库', '缓存服务器', 'A', '4', 1);
INSERT INTO `question` VALUES (266, 'ACP云计算', '某公司属于传统行业,一年前刚刚发展互联网线上业务,用了5台云服务器ECS实例,没有专门的系统管理员,最近半年来线上系统总是出现各种安全问题,几次高危漏洞被黑客利用,差点将核心数据泄露。使用阿里云的_________可以快速解决这个问题。', '安骑士', '安全管家', '内容安全(原名:绿网)', 'DDoS高防IP', 'B', '4', 1);
INSERT INTO `question` VALUES (267, 'ACP云计算', '小王是某新闻网站的技术负责人,该网站目前部署在一台阿里云的云服务器ECS实例上。随着用户访问量的增加,小王发现用户访问网站的响应速度在变慢,通过分析发现是应用服务器的Web会话数量太多且CPU利用率超过75%。为了快速缓解该应用服务器的压力,您认为最紧要的事情是_________。', '增加一台云服务器ECS实例,并采用负载均衡SLB进行负载分担', '采用云数据库RDS进行新闻数据存储', '采用对象存储OSS进行内容的存储', '采用专有云VPC提升运维效率', 'A', '4', 1);
INSERT INTO `question` VALUES (268, 'ACP云计算', '您在阿里云的云服务器ECS实例的使用中,要创建新的安全组规则时,不可以规定类型的授权。', 'IP授权', '端口授权', 'MAC授权', '安全组授权', 'C', '4', 1);
INSERT INTO `question` VALUES (269, 'ACP云计算', '阿里云的云服务器ECS实例的快照是指某一个时间点上某一块磁盘的数据拷贝。下列关于云服务器ECS实例快照的说法错误的是_________。', '用户可以手动创建快照和也可以设置自动快照策略', '可以保留自动快照到用户自己的OSS Bucket中', '系统盘和数据盘都可以制作快照', '云服务器ECS实例到期后或手动释放磁盘时,手动创建的快照不会同时释放', 'B', '4', 1);
INSERT INTO `question` VALUES (270, 'ACP云计算', '如果您需要对某个域名(如abc.com)通过阿里云内容分发网络CDN加速,必须要在CDN服务中将该域名(如abc.com)添加到“加速域名”中。添加完成之后您会获得一个CNAME地址A,接下来需要进行域名解析的修改,将该加速域名(如abc.com)指向这个CNAME地址A。以下有关修改域名解析的说法中错误的是?', '该加速域名(如abc.com)所使用的DNS系统必须支持“智能解析”功能', '“加速域名”使用的解析服务商可以是阿里云以外的公司', '如果您的“加速域名”提前转移到阿里云,且在同一个阿里云帐号下管理,可以实现一键修改解析', '为“加速域名”分配的CNAME地址指向的具体IP地址,由阿里云CDN系统智能控制', 'A', '4', 1);
INSERT INTO `question` VALUES (271, 'ACP云计算', '当您的云服务器ECS实例的镜像选择了Aliyun Linux,可以兼容以下_________操作系统。', 'CentOS', 'Windows Server 2008', 'OpenSUSE', 'UBUNTU', 'A', '4', 1);
INSERT INTO `question` VALUES (272, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)中,伸缩组是一组部署了相同应用的云服务器ECS实例的集合。以下关于伸缩组的说法中正确的是?', '当伸缩组对应的伸缩规则的条件触发后,弹性伸缩会根据设定的伸缩规则添加或者减少伸缩组中的云服务器ECS实例', '伸缩组是云服务器ECS实例的资源池,当实际应用中需要添加计算资源时,会从伸缩组中获取可用的ECS实例,添加到应用中去', '伸缩组是云服务器ECS实例的资源池,弹性伸缩会预先创建一定数量的云服务器ECS实例放在伸缩组中', '伸缩组是监控的目标,其中的云服务器ECS实例,必须是由阿里云弹性伸缩基于相同的镜像自动创建的', 'A', '4', 1);
INSERT INTO `question` VALUES (273, 'ACP云计算', '阿里云对象存储OSS提供了海量的文件存储能力,对于文件管理除了支持简单上传、断点续传和大文件分片上传功能外,还提供了多种删除文件的功能。如果需要删除的Object数目很多,而且删除的Object是有一定的规律,比如定期删除某些天之前的Object,或者干脆是要清空整个Bucket,特别适合使用哪个功能来完成?', '管理控制台逐个删除', '管理控制台批量删除', '生命周期管理', '通过OpenAPI逐个删除', 'C', '4', 1);
INSERT INTO `question` VALUES (274, 'ACP云计算', '阿里云OSS产品的账单费用由三部分组成,包括:存储空间费用、流量费用和OSS API请求费用。 每一次对OSS的操作都算作一次API请求,上传文件是PUT请求,下载文件是GET请求。OSS API请求费用的付费方式支持_________。', '包年包月和按量付费', '只有按量付费', '只有包年包月', '按天计费和按量付费', 'B', '4', 1);
INSERT INTO `question` VALUES (275, 'ACP云计算', '阿里云OSS产品的账单费用由三部分组成,包括:存储空间费用、流量费用和OSS API请求费用。其中存储费用是根据应用所占用的存储空间的大小计费,存储费用的付费方式支持_________。', '包年包月和按量付费', '只有按量付费', '只有包年包月', '按天计费和按量付费', 'A', '4', 1);
INSERT INTO `question` VALUES (276, 'ACP云计算', '某创业团队基于阿里云的产品构建了一个网盘应用,允许用户通过WEB方式进行文件的上传和下载。当前版本的实现方式是所有文件都保存在多台云服务器ECS实例的数据盘上。随着业务量的增长,ECS实例的数量也同步增加,系统整体的运维成本越来越高。如果用户开通阿里云的_________产品与ECS实例搭配,可以很好地降低运维成本。', '负载均衡SLB', '阿里云对象存储OSS', '内容分发网络CDN', '关系型云数据库RDS', 'B', '4', 1);
INSERT INTO `question` VALUES (277, 'ACP云计算', '阿里云的负载均衡SLB是对多台后端服务器进行流量分发的服务。以下关于负载均衡SLB的说法正确的是。', '通过集群提供服务,具有高可靠性', '通过LVS提供七层负载均衡', '通过Tengine提供四层负载均衡', '负载均衡后端ECS实例数不得少于2', 'A', '4', 1);
INSERT INTO `question` VALUES (278, 'ACP云计算', '阿里云负载均衡服务SLB支持用户可以同时设置实例维度上的“后端服务器”、监听维度上的“虚拟服务器组” 和“转发规则”,那么当用户流量经过负载均衡某端口时,关于SLB判断流量转发的顺序,以下说法正确的是。', '先判断其是否能够匹配上某条“转发规则”,如果匹配,则将流量转发到该规则的后端服务器组上;', '先判断该监听上是否设置了虚拟服务器组,如何设置,那么将流量转发到该虚拟服务器组上;', '先判断是否有实例维度上的“后端服务器”,如果存在,那么即将流量转发到实例级别添加的各后端服务器中', '支持用户同时在这三个维度上的设置,但三者添加的服务器之间有约束关系,所以无需考虑转发顺序', 'A', '4', 1);
INSERT INTO `question` VALUES (279, 'ACP云计算', '阿里云负载均衡SLB在四层(TCP)转发时,设置了会话保持,在会话保持的超时时间内,以下说法中正确的是_________。', '来源IP地址相同的连接都会转发到同一台后端云服务器ECS实例上', '负载均衡SLB实例将自动以Session为单位进行转发,无需IP地址判断或者cookie信息辅助', '客户端的请求中带有负载均衡SLB实例设置的cookie信息,负载均衡SLB实例会根据cookie中的信息判断是否来自同一会话,如果是,则转发到相同的ECS实例', '采用test-and-set机制,负载均衡SLB实例先去检查后端ECS实例的内存中是否存在相同Session信息,如果某台ECS实例中存在,则转发至该ECS实例,如果所有ECS实例中都没有相关信息,则该请求被认为是某个Session的第一次连接,会按照转发规则转发给任意一台ECS实例', 'A', '4', 1);
INSERT INTO `question` VALUES (280, 'ACP云计算', '新建阿里云的专有网络VPC时会自动创建一个路由器,路由器自带1个路由表。路由表的操作中说法错误的是_________。', '删除VPC时,系统会自动删除对应的路由表', '不支持创建和删除路由表', '每个路由器可以有多个路由表', '路由表的路由条目会影响VPC中的所有云产品实例', 'C', '4', 1);
INSERT INTO `question` VALUES (281, 'ACP云计算', '您在阿里云的华东1(杭州)跟华北2(北京)都创建了专有网络VPC,通过_________可以最快速的把华东1(杭州)跟华北2(北京)的VPC通过内网连接起来实现两地云服务器ECS实例可以互相访问。', 'VPN', '高速通道', '专线', '其他答案都对', 'D', '4', 1);
INSERT INTO `question` VALUES (282, 'ACP云计算', '关于用户使用阿里云专有网络VPC产品的费用,以下说法中错误的是_________。', '专有网络VPC是一个免费的网络容器', 'VPC中使用的弹性公网IP是免费的', '交换机是免费的', '路由器是免费的', 'B', '4', 1);
INSERT INTO `question` VALUES (283, 'ACP云计算', '在创建阿里云的云产品实例时,可以选择默认类型的专有网络VPC和交换机,这两项配置确定了专有网络类型的云产品创建时在相应地域和可用区下唯一的默认网络位置,与非默认的专有网络VPC和交换机之间的区别主要在于默认类型的VPC和交换机只能由阿里云为您创建。下列关于默认专有网络VPC的描述错误的是_________。', '每个地域的默认专有网络VPC唯一', '默认专有网络VPC与非默认专有网络VPC的操作方式与规格限制不一致', '默认专有网络VPC由阿里云为您创建,您自行创建的均为非默认专有网络VPC', '默认专有网络VPC不占用阿里云为您分配的专有网络VPC配额', 'B', '4', 1);
INSERT INTO `question` VALUES (284, 'ACP云计算', '您的应用部署在阿里云的云服务器ECS实例上,想自己采集指标做应用层面的监控。阿里云提供的云监控服务的_________功能可以提供指标收集、聚合和报警的服务。', '自定义监控', '站点监控', '云服务监控', '云监控无法满足', 'A', '4', 1);
INSERT INTO `question` VALUES (285, 'ACP云计算', '防DDoS攻击是阿里云的云盾最主要的功能之一,为了帮助客户抵御不同规模的网络攻击,云盾提供了多种抗DDoS攻击的产品,其中“DDoS基础防护”的开通方法是。', '联系客户经理线下开通', '自动开通', '需要从管理控制台申请开通', '提交工单,24小时之内开通', 'B', '4', 1);
INSERT INTO `question` VALUES (286, 'ACP云计算', '用户A是公司的系统管理员,经常去上海出差,每当他在上海远程登录服务器时系统就会发出报警信息,提示“有人异地登录,请注意服务器安全”。通过_________方法可以快速、自助解决这个问题。', '立即提交工单,咨询阿里云工程师', '向公司领导请求帮助', '登录阿里云管理控制台,在安骑士配置项里填加“常用登录地点”', '向朋友电话求助,他是业界有名的黑客高手', 'C', '4', 1);
INSERT INTO `question` VALUES (287, 'ACP云计算', '虚拟化(英语:Virtualization)是云计算的底层结构。它是一种资源管理技术,是将计算机的各种实体资源,如服务器、网络、内存及存储等,予以抽象、转换后呈现出来,打破实体结构间的不可切割的障碍,使用户可以比原本的组态更好的方式来应用这些资源。虚拟化架构描述错误的是以下哪个?', '虚拟化技术可以将单个CPU模拟成多个CPU', '虚拟机中的操作系统和物理机上的操作系统有着本质的区别', '虚拟化软件层模拟出来的每台虚拟机都是一个完整的系统', '相比物理机,虚拟化可以提供更高的IT资源利用率和性能', 'B', '4', 1);
INSERT INTO `question` VALUES (288, 'ACP云计算', 'B公司运营着一个手机APP应用商店,类似Apple的AppStore,创业初期所有的软件包都存储在云服务器ECS实例的数据盘上。 从上个月开始,公司做了大量的推广,下载量增长了200倍,用户覆盖范围也从之前的一个省扩展到了全国。为了节约存储和带宽成本,同时又能提高用户下载速度,B公司选择阿里云的_________产品组合,可达到最佳效果。', '弹性伸缩(Auto Scaling)+云服务器ECS', '对象存储OSS和内容分发网络CDN', '对象存储OSS和弹性伸缩(Auto Scaling)', '对象存储OSS和云数据库RDS', 'B', '4', 1);
INSERT INTO `question` VALUES (289, 'ACP云计算', '阿里云的云盾加密服务采用_________机制来保证密钥的安全。', '符合国家密码管理局要求的算法', '符合银监会对密钥强度的要求', '防篡改硬件密码机', '国际通用密码算法', 'A', '4', 1);
INSERT INTO `question` VALUES (290, 'ACP云计算', '游戏行业有个明显的特点是业务流量很难预估,波动很大,例如今天上线1台云服务器ECS实例就足够处理所有的业务需求,明天可能就需要10台云服务器ECS实例来处理所有的需求。这样的场景下,您不知道所需的云服务器ECS实例的数量,准备的少无法应对业务峰值,投入的多又造成大量的资源浪费。在阿里云上, 您认为应对游戏行业这种场景的最佳阿里云产品的组合方式是_________。', '负载均衡SLB+云服务器ECS+云数据库RDS+弹性伸缩Auto Scaling', '负载均衡SLB+云服务器ECS+云数据库RDS', '云服务器ECS+弹性伸缩Auto Scaling', '云服务器ECS', 'A', '4', 1);
INSERT INTO `question` VALUES (291, 'ACP云计算', '如果您在创建Windows操作系统的阿里云的云服务器ECS实例时选择了数据盘,在登录实例后要完成_________操作才可以正常使用该数据盘。', '格式化数据盘', '挂载数据盘', '对数据盘进行多分区配置', '不需要任何操作', 'A', '4', 1);
INSERT INTO `question` VALUES (292, 'ACP云计算', 'ECS服务器会对每个访问的请求进行身份验证,以下哪一个是用于标识访问者身份的?', 'Access Key Secret', 'RequestId', 'Access Key Id', 'User name', 'C', '4', 1);
INSERT INTO `question` VALUES (293, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。与自建存储对比,OSS在可靠性、安全、成本和数据处理能力等几个方面都具有优势。以下_________是OSS在可靠性方面表现出来的优势。', '受限于硬件可靠性,易出问题,一旦出现磁盘坏道,容易出现不可逆转的数据丢失', '数据可靠性不低于99.9999999999%。数据自动多重冗余备份', '数据需要客户手工备份、耗时、耗力', '提供多种鉴权和授权机制及白名单、防盗链、主子账号功能', 'B', '4', 1);
INSERT INTO `question` VALUES (294, 'ACP云计算', '在传统IT解决方案中,要想对海量数据存储实现高于99.99999999%的数据可靠性,并且在单台物理硬件发生损坏的情况下不影响正常服务,至少需要购买多台专业的存储服务器,配合双电源+双控制器+磁盘冗余+故障报警等功能才有可能完成。阿里云的_________服务天然就具备这个特性,即开即用。', '负载均衡SLB', '云数据库RDS', '对象存储OSS', '内容分发网络CDN', 'C', '4', 1);
INSERT INTO `question` VALUES (295, 'ACP云计算', '阿里云对象存储OSS出于安全考虑,直接在浏览器地址栏输入OSS通信域名(oss.aliyuncs.com),如访问:http://bucketname.oss.aliyuncs.com/a.jpg (文件类型包括:txt、html、htm、图片格式、视频格式、音频格式等安全敏感文件),均限制在浏览器客户端以“另存为”下载的方式打开文件。若需要直接在浏览器打开这类文件,需要如何配置?', '在OSS的安全管理菜单中修改ACL,允许浏览器直接访问,设置时需要短信验证', '绑定用户自定义的域名(可以是三级域名)', '在OSS管理控制台--&gt;Object管理中,修改文件的HTTP头信息', 'OSS不直接提供这样的功能,需要二次开发', 'B', '4', 1);
INSERT INTO `question` VALUES (296, 'ACP云计算', '某社区网站每天的PV量高达2000万次,应用程序部署在阿里云的云服务器ECS实例上,日志记录在单台ECS实例的数据盘。现在业务部门提出新的需求,网站的访问日志要保存24个月,并且在需要的时候可以按照日期快速下载,单台ECS实例的数据盘扩到最大容量,可以保存存三个月的日志数据。您认为在该系统上增加阿里云的产品可以一次性解决日志的保存问题。', '对象存储OSS', '归档存储(Archive Storage)', '云数据库RDS', '内容分发网络CDN', 'A', '4', 1);
INSERT INTO `question` VALUES (297, 'ACP云计算', '在使用阿里云负载均衡SLB时后端服务器可以设置主备服务器组,当主机工作正常时,流量将直接走主机;当主机宕机时,流量将走到备机。关于主备服务器组支持的协议,以下说法正确的是_________。', '主备服务器组只支持四层监听(TCP和UDP协议)', '主备服务器组只支持七层监听(HTTP和HTTPS协议)', '主备服务器组只支持七层监听(HTTP协议)', '主备服务器组支持四层监听(TCP和UDP协议)和七层监听(HTTP协议)', 'A', '4', 1);
INSERT INTO `question` VALUES (298, 'ACP云计算', '阿里云负载均衡服务SLB支持配置域名或者URL转发策略,将来自不同域名或者URL的请求转发给不同的ECS处理,关于域名或者URL转发策略支持的协议,以下说法正确的是。', '只有4层监听(TCP和UDP协议)支持配置转发策略', '只有7层监听(HTTP协议)支持配置转发策略', '只有7层监听(HTTP和HTTPS协议)支持配置转发策略', '4层监听(TCP和UDP协议)和7层监听(HTTP和HTTPS协议)都支持配置转发策略', 'C', '4', 1);
INSERT INTO `question` VALUES (299, 'ACP云计算', '使用阿里云的负载均衡SLB实例时,可以通过健康检查来判断后端云服务器ECS实例的可用性。针对7层服务(HTTP协议),负载均衡SLB实例通过来判断云服务器ECS实例是否可用。', '端口检查是否超时', '云服务器ECS实例的服务器端返回的报文内容中的特殊字符', '云服务器ECS实例的服务器端返回的状态码', '云服务器ECS实例的服务器端返回的字节数', 'C', '4', 1);
INSERT INTO `question` VALUES (300, 'ACP云计算', '使用阿里云的负载均衡SLB实例时,可以通过健康检查来判断后端云服务器ECS实例的可用性。针对4层服务(TCP协议),负载均衡SLB实例通过_________来判断云服务器ECS实例是否可用。', '端口检查是否超时', '云服务器ECS实例的服务器端返回的报文内容中的特殊字符', '云服务器ECS实例的服务器端返回的状态码', '云服务器ECS实例的服务器端返回的字节数', 'A', '4', 1);
INSERT INTO `question` VALUES (301, 'ACP云计算', '使用集群是解决高并发,海量数据的通常手段,对于这种类型的网站而言,不管多么强大的服务器都满足不了持续增长的业务需求。这种情况下,需要增加服务器来分担原有系统的压力,我们可以开通阿里云上哪个服务来实现该请求的分发?', 'ECS', 'SLB', 'RDS', 'DRDS', 'B', '4', 1);
INSERT INTO `question` VALUES (302, 'ACP云计算', '您所在的公司半年前在自建机房内刚刚构建了一套财务系统,计划上线的客户管理系统准备基于阿里云的产品构建,同时财务系统与客户管理系统之间需要通过内网互联互通,_________方案可以最大限度的使用已有的系统投资并满足新的业务需求。', '基于经典网络的云产品实例构建客户管理系统,结合专线来实现两个系统之间的内网互联互通', '基于阿里云专有网络VPC购买云产品实例来构建客户管理系统,不需要任何额外的操作,就可以直接实现两个系统的安全内网互联', '将财务系统迁移到阿里云上', '基于阿里云专有网络VPC购买云产品实例来构建客户管理系统,结合专线或者VPN,实现两个系统之间的内网互联互通', 'D', '4', 1);
INSERT INTO `question` VALUES (303, 'ACP云计算', '阿里云的专有网络VPC下的云服务器ECS实例如果绑定了EIP,就可以实现该服务器ECS实例对公网的服务。 出于安全的考虑要限制某台绑定了EIP的云服务器ECS实例的3389远程端口的访问,可以通过_________方法来实现。', '在这台云服务器实例的安全组规则中添加一条内网规则,拒绝TCP协议来自3389端口的入方向访问', '在这台云服务器实例的安全组规则中添加一条外网规则,拒绝TCP协议来自3389端口的入方向访问', '在这台云服务器实例的安全组规则中添加一条内网规则,拒绝TCP协议来自3389端口的出方向访问', '在这台云服务器实例的安全组规则中添加一条外网规则,拒绝TCP协议来自3389端口的出方向访问', 'A', '4', 1);
INSERT INTO `question` VALUES (304, 'ACP云计算', '如果您希望在阿里云专有网络VPC内创建不同可用区的云服务器ECS实例,获得更高的容灾能力,以下方式是可行的。', '可以通过将处于不同可用区的云服务器ECS实例部署到同一台交换机下,从而实现多可用区容灾', '可以通过将云服务器ECS实例部署在处于不同可用区的交换机下,从而实现多可用区容灾', '可以通过将云服务器ECS实例部署在处于不同可用区的路由器下,从而实现多可用区容灾', '可以通过将处于不同地域的云服务器ECS实例部署到同一台路由器下,从而实现多地域的多可用区容灾', 'B', '4', 1);
INSERT INTO `question` VALUES (305, 'ACP云计算', '在互联网上存在着各种以盈利为目的的黑客组织,他们控制着大量的服务器资源,可以随时对目标服务器发起网络攻击,其中有一种很常见的,也是破坏性比较强的攻击,可以让目标服务器的资源耗尽,使正常客户无法连接到服务器。以下_________属于此类攻击。', 'DDoS攻击', 'XSS攻击', 'SQL注入', 'WebShell攻击', 'A', '4', 1);
INSERT INTO `question` VALUES (306, 'ACP云计算', '阿里云的云监控产品的监控数据可以采用_________方式下载。', '从管理控制台直接下载', '通过OpenAPI下载', '只能查看报表,无法下载', '支持管理控制台和OpenAPI两种方式下载', 'C', '4', 1);
INSERT INTO `question` VALUES (307, 'ACP云计算', '日常工作和生活中,很多用户在不同网站使用的是相同的帐号和密码,因此黑客可以通过获取用户在A网站的账户从而尝试登录B网址,这种攻击行为被称作“撞库攻击”。当您在云盾态势感知中发现有撞库攻击的威胁时,应该采取_________应对。', '立即修改云服务器ECS实例的所有密码,并检查登录日志', '立即修改数据库密码,新密码采用8位以上的复杂密码', '通知自己业务平台的所有用户立即修改密码,并通过技术手段杜绝简单密码', '撞库攻击产生的风险由用户自己负责,不需要做任何操作', 'C', '4', 1);
INSERT INTO `question` VALUES (308, 'ACP云计算', '硬件虚拟化是使用软件的方法重新定义划分IT资源,可以实现IT资源的动态分配、灵活调度、跨域共享, 提高IT资源利用率,是云计算的基础技术。如果一个虚拟机因为错误而崩溃,同一物理主机上的其他虚拟机不会受到影响,这是描述虚拟化技术的哪种特性?', '隔离性', '兼容性', '独立于硬件', '统一性', 'A', '4', 1);
INSERT INTO `question` VALUES (309, 'ACP云计算', 'G公司开发了一款购物的APP,全部系统都基于阿里云的产品构建,用到了华北1(青岛)地域的云服务器ECS实例和对象存储OSS,静态网页和图片全部放在OSS中。上线后在中国大陆范围内访问速度良好。最近公司想拓展美国市场,希望美国用户使用APP时也能实现较快的速度。G公司使用阿里云的_________方案是最佳的。', '启用华东1(杭州)和华北2(北京)双地域的对象存储OSS和内容分发网络CDN', '启用美西(硅谷)地域的对象存储OSS和内容分发网络CDN', '启用弹性伸缩(Auto Scaling)和更多的云服务器ECS实例', '启用弹性伸缩(Auto Scaling)和内容分发网络CDN', 'B', '4', 1);
INSERT INTO `question` VALUES (310, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)的冷却时间是指在同一个伸缩组内,当有一个伸缩活动被成功执行后的一段锁定时间内,该伸缩组不执行其他的伸缩活动。以下关于冷却时间的开始计时的时间点的说法正确的是?', '当伸缩组加入或移出多个ECS实例时,最后一个ECS实例加入或移出完成后,整个伸缩组冷却时间才开始计时', '当伸缩组加入或移出多个ECS实例时,第一个ECS实例加入或移出完成后,整个伸缩组冷却时间才开始计时', '伸缩活动结束后,冷却时间马上开始计时', '伸缩活动开始后,冷却时间马上开始计时', 'A', '4', 1);
INSERT INTO `question` VALUES (311, 'ACP云计算', '阿里云的云盾_________是通过领先的行为收集技术和机器学习模型,解决用户的WEB应用和手机APP的账号、活动、支付等关键业务环节存在的欺诈威胁,最终实现降低企业损失。比如:帮助客户发现代金券套现行为,恶意刷单行为,恶意注册片区新用户奖励的行为等。', '态势感知', '数据风控', '绿网', '加密服务', 'B', '4', 1);
INSERT INTO `question` VALUES (312, 'ACP云计算', '阿里云的云服务器ECS产品的应用非常广泛,既可以单独的作为Web服务器,也可以与其他阿里云产品配合提供多媒体解决方案。在多媒体、大流量的APP或网站应用场景下,云服务器ECS实例与阿里云的_________产品搭配,可以存储静态图片、视频、下载包等来降低存储费用。', '对象存储', '云数据库', '弹性伸缩', '块存储', 'A', '4', 1);
INSERT INTO `question` VALUES (313, 'ACP云计算', '创建阿里云弹性伸缩(Auto Scaling)伸缩组的时候,可以配置“移出策略”。现将移出策略配置为“最早伸缩配置对应的实例”,以下说法中正确的是?', '只移除通过弹性规则创建的ECS实例,并且是按照伸缩配置创建ECS实例的时间先后顺序优先移出先建的ECS实例', '根据ECS实例的创建时间判断,优先移出先建的ECS实例', '只移除手工添加的ECS实例,并且是按照伸缩配置创建ECS实例的时间先后顺序优先移出先建的ECS实例', '根据ECS实例添加到伸缩组的时间判断,优先移出先添加ECS实例', 'A', '4', 1);
INSERT INTO `question` VALUES (314, 'ACP云计算', '阿里云OSS产品的账单费用由三部分组成,包括:存储空间费用、流量费用和OSS API请求费用。其中流量费用是根据用户使用OSS的下行流量多少收费,上行流量和通过内网访问的流量免费,以下使用OSS的场景中哪一个是不产生OSS流量费用的?', '在阿里云ECS上静态网站直接引用了OSS中的图片,该网站来自公网的访问量是每天1000次', '某系管理员将OSS用作数据备份,从ECS上每天晚上用内网上传200G的数据到OSS', '某淘宝卖家为了提高店铺的访问速度,将店铺中的所有图片上传到了OSS,然后在店铺中直接引用', '管理员在自己的办公室内浏览自己OSS Bucket里的图片', 'B', '4', 1);
INSERT INTO `question` VALUES (315, 'ACP云计算', '阿里云对象存储OSS里存储的文件可以进行分享和下载,当文件所在的Bucket的读写权限为“私有”时,OSS分享链接采用的安全机制是。', '在管理控制台中获取文件访问URL时设置分享链接的访问密码,访客下载文件时凭密码访问', '“私有”权限的文件对外享时,访客只能看到文件的一部分内容(前500K)', '在管理控制台中获取文件访问URL时设置分享链接有效的时间,超过设定时间就无法下载', '“私有”权限的文件对外分享时,系统会检查文件的大小,超过2M的文件不允许分享', 'C', '4', 1);
INSERT INTO `question` VALUES (316, 'ACP云计算', '阿里云负载均衡SLB实例中的后端服务器ECS实例需要设置权重,可以给对外服务能力更强的ECS实例设置更大的权重,这些ECS实例就会有机会处理更多的访问请求。在提供稳定服务的同时,充分的利用了已有资源。如果将一台ECS实例的权重设置为0,以下说法正确的是。', '该ECS实例不会再得到负载均衡SLB实例分发的外部访问请求', '所有外部访问请求都被分发到这台ECS实例上', '该ECS实例会被认为状态异常,负载均衡SLB会把它移出后端服务器池', '负载均衡SLB会分发外部请求给这台ECS实例,分发量的大小等于总请求数除以后端服务器ECS实例个数', 'A', '4', 1);
INSERT INTO `question` VALUES (317, 'ACP云计算', '阿里云的弹性伸缩主要可以提供的功能,下面说法错误的是?', '根据客户业务需求横向扩展ECS实例的容量,即自动增加和减少ECS实例,数量是有限制的。', '支持SLB负载均衡配置:在增加或减少ECS实例时,自动向SLB实例中添加或移除相应的ECS实例', '支持RDS访问白名单:在增加或减少ECS实例时,自动向RDS访问白名单中添加或移出该ECS实例的IP', '多模式兼容,可同时配置定时、动态、自定义、固定、健康模式,可通过API方便对接外在监控系统', 'A', '4', 1);
INSERT INTO `question` VALUES (318, 'ACP云计算', '云监控的http提交指令是哪一个?', 'post', 'copy', 'write', 'put', 'A', '4', 1);
INSERT INTO `question` VALUES (319, 'ACP云计算', '下面哪种镜像可以进行复制?', '自定义镜像', '公共镜像', '镜像市场镜像', '共享的镜像', 'A', '4', 1);
INSERT INTO `question` VALUES (320, 'ACP云计算', 'F公司新推出一款手机APP,将安装包链接挂在公司的官网(基于云服务器ECS实例)进行下载(安装包大小:6Mb),上线前两个月下载速度还能接受。 自从上周公司在CCTV做了一次广告后,下载量持续爆增,不仅用户体验下降了,官网ECS实例的带宽成本也急剧上升。F公司可以选择阿里云的_________产品组合,同时解决下载速度和带宽成本的问题。', '对象存储OSS和内容分发网络CDN', '对象存储OSS和弹性伸缩(Auto Scaling)', '对象存储OSS和云数据库RDS', '弹性伸缩(Auto Scaling)和云服务器ECS实例', 'A', '4', 1);
INSERT INTO `question` VALUES (321, 'ACP云计算', '您在阿里云购买了一台云服务器ECS实例,部署好Python环境后,通过_________能最简单快捷的监控Python进程是否持续运行,当进程不存在时发出报警信息。', '自己写脚本监控', '使用ECS监控中的进程监控', '使用站点监控', '去ECS控制台查看', 'B', '4', 1);
INSERT INTO `question` VALUES (322, 'ACP云计算', '阿里云的云盾_________是通过领先的行为收集技术和机器学习模型,解决用户的WEB应用和手机APP的账号、活动、支付等关键业务环节存在的欺诈威胁,最终实现降低企业损失。比如:帮助客户发现代金券套现行为,恶意刷单行为、恶意注册骗取新用户奖励的行为等。', '态势感知', '数据风控', '内容安全(原名:绿网)', '加密服务', 'B', '4', 1);
INSERT INTO `question` VALUES (323, 'ACP云计算', '在阿里云的云盾的网络防护体系中,有一种服务默认为所有客户开通,可提供最高5G以下的小规模DDoS防御功能。该项服务的名称是_________。', '态势感知', 'WAF防火墙', 'DDoS基础防护', 'DDoS高防IP', 'C', '4', 1);
INSERT INTO `question` VALUES (324, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling) 的一个伸缩活动在12:00执行完成,设置的冷却时间是15分钟。假如这时用户停用再启用伸缩组,在12:05伸缩组完成启动,如果在12:10云监控触发伸缩活动,当前伸缩组将如何处理该请求?', '接受执行该请求,无需等待', '仍需等待5分钟后执行', '仍需等待10分钟后执行', '仍需等待15分钟后执行', 'A', '4', 1);
INSERT INTO `question` VALUES (325, 'ACP云计算', '阿里云的云盾加密服务通过在阿里云上使用经国家密码管理局检测认证的硬件密码机,帮助用户满足数据安全方面的监管合规要求,保护云上业务数据的隐私性和机密性。管理云盾加密服务的密钥时,必须通过_________方式的认证。', '身份卡(USB Key)', '阿里云官网帐号的AK', '用户名和密码', '用户自己上传的SSL证书', 'A', '4', 1);
INSERT INTO `question` VALUES (326, 'ACP云计算', '在使用阿里云弹性伸缩(AutoScaling)时如果在伸缩组中指定了RDS实例,伸缩组会自动将加入伸缩组的ECS实例的内网IP添加到指定的RDS实例的访问白名单当中,下面说法错误的是?', '如果ECS实例无法加入到RDS访问白名单,该ECS实例不会被回滚操作并释放', '指定的RDS实例必须是运行中状态。', '指定的RDS实例访问白名单的IP个数不能达到上限。', '伸缩组创建成功后,伸缩组不会立即生效,只有启用伸缩组,才能接受伸缩规则的触发和执行相关的伸缩活动。', 'A', '4', 1);
INSERT INTO `question` VALUES (327, 'ACP云计算', '阿里云的云盾的先知计划,是调动众多网络安全专家的力量一起维护互联网平台和业务的安全,降低客户的安全损失。先知计划为企业提供的是_________服务。', '为企业收集情报(漏洞)', '为企业保障服务器安全', '为企业保障网络安全', '用大数据的方式为企业进行安全分析', 'A', '4', 1);
INSERT INTO `question` VALUES (328, 'ACP云计算', '云计算在极大地方便用户和企业廉价使用存储资源、软件资源、计算资源的同时,同样也面临着安全方面的挑战。上云并不会增加安全风险,同时云计算厂商也不会帮助客户解决一切安全问题,需要用户积极参与。目前普遍遵循“三不变”原则,不包括以下哪项?', '数据归属关系不变', '安全管理责任不变', '运维管理方式不变', '安全管理标准不变', 'C', '4', 1);
INSERT INTO `question` VALUES (329, 'ACP云计算', 'A公司管理员小牛近期发现公司的网站访问速度越来越慢,偶尔会出现无法打开的现象,从web日志中发现了数据被恶意爬取的行为,且并发访问量较大,这种情况下可以使用阿里云的_________安全产品进行防护。', '安全管家', '态势感知', 'Web应用防火墙(WAF)', '云安全中心(安骑士)', 'C', '4', 1);
INSERT INTO `question` VALUES (330, 'ACP云计算', '阿里云的云盾先知计划的目的是帮助企业建立私有的_________,以较低的成本发现企业自身企业的安全风险,并得到解决方案。', '私有数据中心', '应急响应中心', '病毒防护中心', '远程备份中心', 'B', '4', 1);
INSERT INTO `question` VALUES (331, 'ACP云计算', '用户在使用阿里云云服务器ECS的时候,经常收到密码被暴力破解的报警信息,虽然都没有成功,但是很担心某一天会被攻破。为了提升系统的安全性,并不再接收到该ECS实例的密码暴力破解报警信息,以下_________是最安全的措施。', '关闭云监控的异常报警机制', '开通安全众测,查找系统中的潜在漏洞', '将ECS密码修改为8位以上的复杂密码,同时用安全组关闭远程管理端口', '使用WAF来提升系统的安全性', 'C', '4', 1);
INSERT INTO `question` VALUES (332, 'ACP云计算', '以下_________安全事件是不能由阿里云态势感知集中管理的。', '发现主机有异常进程', '发现主机成为肉鸡', '机房断电', '发现网站被DDoS攻击成功', 'C', '4', 1);
INSERT INTO `question` VALUES (333, 'ACP云计算', '云计算领域的三篇分别关于MapReduce/GFS/BigTable三大技术的经典论文是由哪家公司发表的?', 'Amazon(亚马逊)', 'Google(谷歌)', 'Microsoft(微软)', 'Facebook(脸书)', 'B', '4', 1);
INSERT INTO `question` VALUES (334, 'ACP云计算', '阿里云内容分发网络是建立并覆盖在承载网之上,由分布在不同区域的边缘节点服务器群组成的分布式网络。阿里云CDN分担源站压力,避免网络拥塞,确保在不同区域、不同场景下加速网站内容的分发,提高资源访问速度。CDN可加速下列哪些阿里云产品?', 'OSS', 'RDS', 'MongoDB', 'redis', 'A', '4', 1);
INSERT INTO `question` VALUES (335, 'ACP云计算', '如果负载均衡实例下添加多台ECS实例,当其中一部分ECS实例发生故障后,负载均衡会自动屏蔽故障的ECS实例,将请求分发给正常运行的ECS实例,保证应用系统仍能正常工作,这说的是SLB负载均衡的_________场景?', '消除单点故障', '应用于高访问量的业务', '扩展应用程序', '同城容灾(多可用区容灾)', 'A', '4', 1);
INSERT INTO `question` VALUES (336, 'ACP云计算', 'ECS监控指标分为基础监控和操作系统级别指标监控。基础监控无需安装任何插件,您购买一个ECS实例后,无需任何操作便可查看监控指标。操作系统级别指标需要安装插件才能查看监控数据,下面属于操作系统级别监控指标是哪一个?', '内存使用率(%);', 'CPU使用率(%);', '网络流入速率(bps);', '系统盘IOPS;', 'A', '4', 1);
INSERT INTO `question` VALUES (337, 'ACP云计算', '为了提升阿里云ECS的安全性,管理员小王不希望公网用户通过ping命令检查到ECS是否在线,以下是他自己设计的加固方案,您认为_________是不可行的。', '启用安全组,拒绝“公网入”的ICMP协议', '启用ECS内操作系统的防火墙,拒绝“公网入”的ICMP协议', '先将ECS IP解析到一个不常用的四级域名,然后将对外推广的域名通过CNAME指向以上四级域名', '启用安全组,只允许公网TCP 80端口和25端口访问', 'D', '4', 1);
INSERT INTO `question` VALUES (338, 'ACP云计算', '阿里云云安全中心是一个大数据安全分析平台,能对您云上所有资产进行安全告警,并用机器学习来发现潜在的入侵和高隐蔽性攻击,回溯攻击历史,预测即将发生的安全事件。阿里云云安全中心提供的是一种_________类型的服务。', 'IaaS', 'SaaS', 'PaaS', 'SOA', 'B', '4', 1);
INSERT INTO `question` VALUES (339, 'ACP云计算', '目前客户的应用急速发展,用户遍布全国。过去3个月内频繁受到10G以上的DDoS攻击,适合选择以下_________产品进行防护。', '云安全中心', '新BGP高防(DDoS高防)', 'Web应用防火墙', 'DDoS基础防护', 'B', '4', 1);
INSERT INTO `question` VALUES (340, 'ACP云计算', '如果您在非阿里云的服务器上安装了云安全中心客户端,通过_________可以确保在您的管理控制台中能查到此服务器的相关报告。', '非阿里云服务器不支持云安全中心', '通过阿里云官网帐号关联', '报告无法在阿里云的管理控制台查看', '在安装云安全中心的过程中需要输入“安装验证key”,通过此Key关联用户的管理控制台', 'D', '4', 1);
INSERT INTO `question` VALUES (341, 'ACP云计算', '以下哪种服务可以用来发现潜在的入侵和高隐蔽性攻击,回溯攻击历史,预测即将发生的安全事件?', '安骑士', 'Web应用防火墙', '态势感知', '云监控', 'C', '4', 1);
INSERT INTO `question` VALUES (342, 'ACP云计算', '伸缩活动的生命周期为:判断伸缩组的健康状态和边界条件步骤与启动cooldown步骤之间的所有活动。 手工加入既有的ECS实例,还包括A:判断ECS实例的状态、类型;B:分配ActivityId和执行伸缩活动;C:修改Total Capacity;D:加入ECS实例等活动,这些活动正确的顺序是_________。', 'A-B-D-C', 'B-A-D-C', 'A-D-B-C', 'B-C-A-D', 'A', '4', 1);
INSERT INTO `question` VALUES (343, 'ACP云计算', '某用户在阿里云控制台手动创建了一台ECS实例并加入到弹性伸缩组(已知该伸缩组配置了负载均衡)中。由于用户业务发生变化,该用户将此台ECS实例转为备用状态,则_________。', '该ECS实例负载均衡权重不变', '弹性伸缩仍管理该ECS生命周期', '伸缩组发生伸缩活动时,优先移出该ECS实例', '用户需从伸缩组内移出该ECS实例才能将其释放', 'D', '4', 1);
INSERT INTO `question` VALUES (344, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)是根据用户的业务需求和策略,自动调整其弹性计算资源的管理服务,必须和_________配合使用。', '云服务器ECS', '云数据库RDS', '云监控', '块存储', 'A', '4', 1);
INSERT INTO `question` VALUES (345, 'ACP云计算', '阿里云提供了多种链接ECS实例的方式,包括Workbench、VNC等,使用VNC方式登录的典型场景不包括_________。', '实例启动自检等原因导致启动速度慢', '忘记SSH密码,无法使用远程连接工具链接ECS', '实例误开启操作系统防火墙', '云服务器失陷,CPU和带宽消耗高', 'B', '4', 1);
INSERT INTO `question` VALUES (346, 'ACP云计算', '某用户在阿里云控制台查看到某台ECS实例状态为_________,这种状态下,ECS实例正常提供服务,弹性伸缩不会检查ECS实例健康状态,也不会释放ECS实例,需要用户手动管理该台ECS实例生命周期。', '备用中', '使用中', '保护中', '挂起中', 'C', '4', 1);
INSERT INTO `question` VALUES (347, 'ACP云计算', '某大型企业客户将关键业务系统部署在阿里云上,使用弹性伸缩服务应对不可预测的高峰流量。该客户发现,由于阿里云不同区域的资源种类、资源数目是有限的,且是动态变化的,在使用弹性伸缩服务进行动态扩展时,有可能出现因资源不足而扩展失败的情况,为了尽可能避免这种情况,建议其采用的措施为          。', '选择多个地域创建ECS,加入同一伸缩组', '选择多个地域创建伸缩组,关联同一个SLB实例', '为伸缩活动设置报警策略,当收到伸缩活动失败的报警时,手动创建ECS实例', '在伸缩配置中设置多个实例规格,当特定实例规格库存不足时,会切换到至备选实例规格,提高弹性扩张的成功率', 'D', '4', 1);
INSERT INTO `question` VALUES (348, 'ACP云计算', '某中小企业打算将一个业务系统快速移至阿里云,其原来是租用了IDC机房的一台物理机部署,包含括应用、数据库(mysql)以及存储文件(约100G),以下方案中,最经济快捷的是          。', '使用两台ECS进行部署,一台部署应用及存放数据,另一台部署mysql', '使用一台ECS部署应用和存储文件,使用RDS(mysql)实例快速完成数据库迁移', '使用一台ECS部署应用和数据库,使用对象存储OSS节约文件存储成本', '使用云服务器ECS、云数据库RDS(mysql)、对象存储OSS进行部署,形成弹性、可扩展的架构', 'D', '4', 1);
INSERT INTO `question` VALUES (349, 'ACP云计算', '阿里云云服务器ECS提供众多实例规格组,满足不同用户、不同场景的应用,例如:通用性、计算型、突发性、共享型等。以下_________场景不适合选用共享型实例。', 'Web应用前端机', '开发环境', '代码存储库', '数据库', 'D', '4', 1);
INSERT INTO `question` VALUES (350, 'ACP云计算', '执行伸缩规则、手动添加或移除已有ECS实例时,均会触发_________实现扩张或收缩。', '伸缩活动', '定时任务', '告警任务', '自动伸缩', 'A', '4', 1);
INSERT INTO `question` VALUES (351, 'ACP云计算', '实例启动自动模板可用于快速创建ECS实例,模板中包含了用户自主选择的用于创建实例的配置信息,所有配置为可选,能够满足不同场景的个性化需求。下列选项中_________不属于实例启动模板可包含的配置信息。', '密码', 'RAM角色', '实例类型', '秘钥对', 'A', '4', 1);
INSERT INTO `question` VALUES (352, 'ACP云计算', '如果公网带宽占有率达到百分之百,EIP未及时扩容,易出现业务流量限速丢包的情况,进而引发客户端访问质量恶化和用户体验的直线下降。可以使用EIP的_________功能帮助用户实时监测流量波动情况。', '秒级监控', '云监控', '自动扩容', '弹性IP', 'A', '4', 1);
INSERT INTO `question` VALUES (353, 'ACP云计算', '在创建ECS实例后,如果需要更换ECS实例的操作系统,用户可以通过更换系统盘来更换操作系统。用户进行更换系统盘操作后,下列选项中不一定发生的是_________。', '旧系统盘会被释放', '系统盘ID会更新', '原系统盘自动快照被自动删除', '原自动快照策略失效', 'C', '4', 1);
INSERT INTO `question` VALUES (354, 'ACP云计算', '对于不同类型协议的监听,负载均衡服务的健康检查功能发送不同的探测请求。针对四层UDP监听,健康检查通过_________探测来获取ECS实例的状态信息。', 'UDP HEAD请求', 'UDP报文', 'UDP SYN数据包', 'UDP RST数据包', 'B', '4', 1);
INSERT INTO `question` VALUES (355, 'ACP云计算', '网络安全是一项系统工程,必须从整体上采取措施。阿里云为用户提供全方位的安全服务,其中,堡垒机是云盾提供的一个核心系统运维和安全审计管控平台,堡垒机的功能不包括', '权限控制', '实人认证', '高效运维', '操作审计', 'B', '4', 1);
INSERT INTO `question` VALUES (356, 'ACP云计算', '以下关于SLB使用场景的描述,是使用于4层(TCP)协议配置SLB监听规则。', '后端服务器应用采用HTTP协议的负载均衡场景', '后端服务器应用采用HTTP, HTTPS, TCP协议的负载均衡场景', '后端服务器应用采用HTTPs协议的负载均衡场景', '后端服务器应用采用TCP协议的负载均衡场景', 'D', '4', 1);
INSERT INTO `question` VALUES (357, 'ACP云计算', '在创建VPC和交换机时,必须以无类域间路由块(CIDR block)的形式为专有网络划分私网网段,以下关于网段划分的实践中,错误的是', '可以使用192.168.0.0/16, 172.16.0.0/12, 10.0.0.0/8这三个私网网段及其子网作为VPC的私网地址范围', '交换机的网段的大小在16位网络掩码与29位网络掩码之间,可提供8—65536个地址。16位掩码能支持65532个ECS实例,而小于29位掩码又太小,没有意义。', '每个交换机的第一个和最后两个IP地址为系统保留地址。以192.168.1.0/24为例, 192.168.1.0, 192.168.1.254和192.168.1.255这些地址是系统保留地址。', '当有VPC与VPC互通或VPC与本地IDC互通的需求时,需要确保VPC的网段和要互通的网络的网段都不冲突。', 'C', '4', 1);
INSERT INTO `question` VALUES (358, 'ACP云计算', '某全球性访问论坛使用对象存储服务OSS存储论坛的静态资源,并使用了CDN服务实现资源的就近访问。但该用户发现论坛的下载文件速度大幅提升,部分客户会因数据传输距离较远上传文件速度很慢,造成了不好的访问体验。为提升该论坛用户上传文件的速度,让不同地域的用户都能有很好的访问体验,建议该论坛', '开启OSS服务的传输加速服务', '开通PCDN加速服务', '开通不同地域的OSS服务作为文件存储,不同地域OSS之间通过跨区域复制功能实时同步数据', '开通OSS服务静态网站托管', 'A', '4', 1);
INSERT INTO `question` VALUES (359, 'ACP云计算', '对象存储服务OSS是一种海量、安全、低成本、高可靠的云存储服务,支持多种类型的文件访问权限(ACL) ,_________不是OSS Object ACL支持的方式。', '公共读', '私有', '继承Object', '继承Bucket', 'C', '4', 1);
INSERT INTO `question` VALUES (360, 'ACP云计算', '某用户需要对OSS服务中的多张图片进行相同的操作,该用户通过创建一个图片样式将这些操作封装起来,然后使用创建的样式对多张图片进行操作。用户可以使用指定处理参数的方式新建图片样式,多个参数之间用连接。', '空格', '正斜线(/)', '顿号(、)', '短横线(-)', 'B', '4', 1);
INSERT INTO `question` VALUES (361, 'ACP云计算', '某些企业由于安全机制,需要在出口防火墙配置策略,以限制内部员工和业务系统只能访问指定的公网IP,但是OSS的Bucket访问IP会随机变换,导致需要经常修改防火墙策略,在这种情况下,可通过来解决。', 'ECS实例上配置OSS的反向代理,实现通过固定IP地址访问OSS的存储空间', '发工单询问OSS的内网IP段,加入ECS的访问白名单中', 'OSS控制台开启反向代理访问功能', '通过CLI或SDK定时获取OSS的最新访问地址,通过脚本或程序更新访问IP', 'A', '4', 1);
INSERT INTO `question` VALUES (362, 'ACP云计算', '已知某个VPC网络附加IPv4网段为172.16.0.0/16,该VPC路由表中已有云企业网(已开启路由重叠功能)路由, 目标网段为172.16.0.0/24,如果当前用户使用附加网段来创建交换机,用户可以选择的网段有', '172.16.0.0/16', '172.16.0.0/17', '172.16.0.0/24', '172.16.0.0/25', 'D', '4', 1);
INSERT INTO `question` VALUES (363, 'ACP云计算', '专有网络VPC是用户独有的云上虚拟网络,用户可以将云资源部署在自定义的专有网络中。在VPC内部,用户依然可以采取多种措施,以实现细粒度的访问控制,以下对VPC网络ACL功能的描述,错误的是', '网络ACL的规则是有状态的:返回数据流会被自动允许,不受任何规则的影响。', '网络ACL与交换机绑定,不过滤同一交换机内的ECS间的流量', '网络ACL规则仅过滤绑定的交换机中的ECS流量', '新创建的网络ACL,默认会在出方向和入方向分别生成一条规则,表示允许所有出、入方向流量。', 'A', '4', 1);
INSERT INTO `question` VALUES (364, 'ACP云计算', '专有网络VPC是用户独有的云上虚拟网络,用户可以将云资源部署在自定义的专有网中。在VPC内部,用户依然可以采取多种措施,以实现细粒度的访问控制,以下不是在VPC内部进行访问控制的工具。', '安全组', '网络ACL', 'RDS白名单', 'VPN网关', 'D', '4', 1);
INSERT INTO `question` VALUES (365, 'ACP云计算', '使用CDN加速访问URL时,出现空白页面,使用Chrome浏览器访问源站,按F12键打开开发者工具,单击NetWork,查看Content-Length配置项显示为0,这个问题应', '清理浏览器的缓存,并重新开启浏览器访问该网站', '登录CDN控制台,检查该域名的配置,查看CNAME是否匹配正确', '打开CDN控制台,单击域名管理。在对应的域名上单击配置,在配置页面找到Content-Length参数并关闭', '查看源站是否返回Transfer-Encoding:chunked头信息。 CDN不支持此头信息,去除此头信息即可', 'D', '4', 1);
INSERT INTO `question` VALUES (366, 'ACP云计算', '阿里云OSS通过Restful API方式对外提供服务,最终用户通过OSS默认域名或者绑定的自定义域名方式访问。但是,某企业由于安全机制,需要在出口防火墙配置策略,以限制内部员工和业务系统只能访问指定的公网IP,但是OSS的Bucket访问IP会随机变换,导致需要经常修改防火墙策略。建议其采用的方案来解决这个问题。', '使用文件存储代替OSS', '在ECS实例上配置OSS的反向代理', '前端增加一个负载均衡SLB实例,使用SLB的固定IP访问', '使用阿里云NAT网关服务,进行IP地址转换', 'B', '4', 1);
INSERT INTO `question` VALUES (367, 'ACP云计算', '网络安全是一项系统工程,必须从整体上采取措施,阿里云为用户提供全方位的安全服务。某公司的业务系统涉及敏感操作,需要在操作前验证当前操作执行人与该账号的实名认证信息是否一致,其适合选用阿里云的_________服务。', '双因素认证', 'web应用防火墙', '实人认证', '操作审计', 'C', '4', 1);
INSERT INTO `question` VALUES (368, 'ACP云计算', '某用户在对其账号下的ECS实例进行高危操作(更换系统盘)时,为了避免数据丢失,需要对磁盘数据进行提前备份,同时因为业务需要要求缩短制作快照的等待时间,这种情况下,该用户可选择创建', '普通快照', '本地快照', '加密快照', '自动快照', 'B', '4', 1);
INSERT INTO `question` VALUES (369, 'ACP云计算', '用户通过配置安全组规则,可以控制安全组内一台或多台ECS实例的入流量和出流量。建议用户设置简洁的安全组规则。如果一台ECS实例被分配了多个安全组,该ECS实例很可能会同时遵循数百条安全组规则,任何规则变更都可能引起网络不通的故障。在创建安全组规则时,必填字段不包括', '授权策略', '授权对象', '授权时限', '端口范围', 'C', '4', 1);
INSERT INTO `question` VALUES (370, 'ACP云计算', '某用户在云上创建了VPC网络,其VPC内的某些ECS实例用于需要对外提供服务绑定了EIP实例,其他的ECS实例通过NAT网关的SNAT功能代理访问互联网。由于绑定EIP的ECS实例会优先通过EIP访问互联网,造成了该用户VPC内ECS实例的公网出口IP不一致的情况。为了统—VPC内ECS实例的公网出口IP (对外提供服务的ECS实例仍需要以不同的访问地址继续提供服务) ,便于用户更高效地管理互联网业务,用户可以', '将绑定了EIP的ECS实例解绑EIP', '为原绑定EIP的ECS实例单独分配一块弹性网卡,并将EIP绑定到弹性网卡', '为原绑定EIP的ECS实例ECS实例设置DNAT IP映射', '为所有ECS实例自建SNAT网关,将原绑定EIP的ECS实例解绑EIP', 'B', '4', 1);
INSERT INTO `question` VALUES (371, 'ACP云计算', '在使用云服务器ECS的过程中,我们非常关心ECS的运行状态。在ECS管理控制台,对ECS实例状态的监控分为两种,一种是直接可以监控到的,一种是需要安装云监控插件才能监控到的。下列选项中需要安装插件才能监控到的指标是', 'CPU使用率', '内存使用率', '系统磁盘每秒读取次数', '公网入流量平均速率', 'B', '4', 1);
INSERT INTO `question` VALUES (372, 'ACP云计算', 'ECS是阿里云提供的一种即开即用、可弹性伸缩的云服务器。它包含CPU、内存、操作系统、网络配置、磁盘等基础的计算组件。对于服务器计算性能以及内存性能的指标需要参考_________来选择,具体指标包括VCPU核数,内存大小等。', '实际业务需求', '规格族', '存储容量', '网络', 'B', '4', 1);
INSERT INTO `question` VALUES (373, 'ACP云计算', '某用户为其SLB实例的一个HTTP监听配置了多条域名转发策略,包括:精确域名:www.aliyun.com,精确域名:market.aliyun.com,泛域名:*.aliyun.com,泛域名:*.market.aliyun.com,已知当前某个请求访问域名为info.market.aliyun.com,该条请求匹配的域名转发对应________。', '精确域名:www.aliyun.com,', '精确域名:market.aliyun.com', '泛域名:*.aliyun.com', '泛域名:*.market.aliyun.com', 'D', '4', 1);
INSERT INTO `question` VALUES (374, 'ACP云计算', '云服务器ECS的功能组件主要包含实例、镜像、块存储、快照、安全组、网络。想要恢复某一时间点一块数据状态的文件,可以使用云服务器ECS________功能。', '实例', '镜像', '块存储', '快照', 'D', '4', 1);
INSERT INTO `question` VALUES (375, 'ACP云计算', '弹性伸缩服务使用户可以根据业务需求和策略设置伸缩规则,适合业务量不断波动的应用程序。关于弹性伸缩服务,下列说法不正确的是________。', '不支持纵向扩展', 'ECS实例上的应用必须是无状态并且可横向扩展的', '伸缩组内ECS不适合保存会话记录、应用数据、日志等信息', '不支持自动在RDS访问白名单中添加或移除ECS实例的IP', 'D', '4', 1);
INSERT INTO `question` VALUES (376, 'ACP云计算', '阿里云弹性伸缩服务AutoScaling可以根据业务需求和策略自动调整弹性计算资源。在业务需求增长时无缝增加ECS实例,满足计算需要;在业务需求下降时,自动减少ECS实例节约成本。在创建弹性伸缩组时,需要填写的信息不包括________。', '伸缩组名称', '组内最大实例数', '组内最小实例数', '伸缩配置', 'D', '4', 1);
INSERT INTO `question` VALUES (377, 'ACP云计算', '弹性扩张时,弹性伸缩使用伸缩组的组内实例配置信息自动创建ECS实例,实例配置信息支持实例的规格、________、用户自定义数据等。', '操作系统', '自定义公网IP', '地域', '自定义内网IP', 'A', '4', 1);
INSERT INTO `question` VALUES (378, 'ACP云计算', '弹性伸缩可以根据业务需求和策略设置________在业务需求增长时自动增加ECS实例以保证计算能力,在业务需求下降时自动减少ECS实例以节约成本。', '伸缩规则', '弹性扩张', '伸缩配置', '触发任务', 'A', '4', 1);
INSERT INTO `question` VALUES (379, 'ACP云计算', 'ECS在使用的过程中如果数据盘空间不够,可以使用扩容功能,扩容云盘只是扩大存储容量,但不会扩容________。', '文件系统', '数据盘', '操作系统', '共享块存储', 'A', '4', 1);
INSERT INTO `question` VALUES (380, 'ACP云计算', '用户在添加安全组规则时,必须指定通信协议和端口或端口范围,然后安全组根据允许或拒绝策略决定是百转发数据到ECS实例。某用户需要使用Xshell客户端远程连接Linux系统的ECS实例,设置的安全组规则应放行________协议和________端口。', 'Telnet, 23', 'SSH, 22', 'SMTP, 25', 'HTTPS, 443', 'B', '4', 1);
INSERT INTO `question` VALUES (381, 'ACP云计算', '云防火墙是用户上云后的首个安全组件,支持全网流量识别、统一策略管控、________、日志等核心功能。', '组件自动检测', '流量清洗', '宕机自动重启', '入侵检测', 'D', '4', 1);
INSERT INTO `question` VALUES (382, 'ACP云计算', '如果用户不再需要ECS实例上挂载的某块云盘,用户可以将其手动释放,存储在云盘上的数据会被全部释放,云盘停止计费。用户在手动释放云盘时,云盘状态必须为________。', '使用中', '待挂载', '已删除', '初始化中', 'B', '4', 1);
INSERT INTO `question` VALUES (383, 'ACP云计算', '某公司有两个业务,要购买阿里云产品ECS服务器,要求两个业务之间的网络延时较低,可以将两个业务实例创建在________。', '不同地域', '同一地域的不同可用区内', '同一可用区内', '同一个安全组', 'C', '4', 1);
INSERT INTO `question` VALUES (384, 'ACP云计算', '某用户的业务系统在云上搭建了ECS和VPC服务架构。该用户在为Windows系统的ECS实例配置IPv6地址时,搭建好IPv6 VPC后的步骤如下:1. 开通网络带宽,2. 分配IPv6地址,3. 添加安全组规则,4. 配置IPv6地址。请为用户选择正确的步骤顺序为________。', '2-1-3-4', '2-1-4-3', '3-2-1-4', '3-2-4-1', 'B', '4', 1);
INSERT INTO `question` VALUES (385, 'ACP云计算', '在跨国公司在美国硅谷和中国上海均有办公点,且该跨国公司在美国(硅谷)和华东2(上海)地域分别创建了VPC1和VPC2,并在两个VPC中部署了应用系统。因业务发展,需要美国硅谷的两个办公点、中国上海的两个办公点、VPC1、VPC2全互通(已知业务之间数据日传输量在100M以内)。如果你是阿里云产品经理,你会建议用户采用的最为高效经济的方案是________。', '使用高速通道对办公地点和VPC进行数据打通', '使用VPN连接办公地点与所在地VPC,将两个VPC加载到同一云企业网中', '为VPC内的ECS实例绑定弹性公网IP,办公点与VPC以及VPC间通过公网访问', '为VPC网络搭配负载均衡服务,配合云解析实现网络流量互访', 'B', '4', 1);
INSERT INTO `question` VALUES (386, 'ACP云计算', '在创建阿里云ECS时,每台服务器必须要包含________用来存储操作系统和核心配置。', '镜像', '系统盘', '实例', '数据盘', 'B', '4', 1);
INSERT INTO `question` VALUES (387, 'ACP云计算', 'ECS实例区分网络类型支持经典网络和专有网络VPC,实例在相同的网络中,如同一个VPC,可以使用私网IP地址通过内网通信,经典网络类型的云产品,统一部署在阿里云公共基础设施内,规划和管理由阿里云负责,更适合对网络易用性要求比较高的用户,经典网络支持_________功能。', '私网互通', '自定义路由器', '自建NAT网关', '隧道技术', 'A', '4', 1);
INSERT INTO `question` VALUES (388, 'ACP云计算', '精准访问规则指对常见的HTTP字段进行条件组合,支持业务场景的定制化防护策略,可用于盗链防护、网站管理后台保护等。以下不属于精准防护支持的字段是________。', 'Params、URL', 'IP、Referer', 'User-Agent', 'Source-Address', 'D', '4', 1);
INSERT INTO `question` VALUES (389, 'ACP云计算', '安全组是ECS实例的虚拟防火墙,用于设置单个或多个ECS实例的网络访问控制。下列关于安全组网络隔离的描述,________是不正确的。', '安全组内网络隔离是ECS实例之间的隔离', '安全组内网络隔离是一种自定义的网络连通策略,对于默认安全组和新建的安全组无效', '设置了组内网络隔离的安全组,仅在安全组内没有任何自定义规则的情况下保证安全组内实例之间网络隔离。', '安全组内既设置了组内隔离,又设置了让组内实例之间可以互相访问的ACL,所以这种情况设置了组内网络隔离但实例仍然互通', 'A', '4', 1);
INSERT INTO `question` VALUES (390, 'ACP云计算', '专有网络VPC类型的ECS实例有两种IP地址,分为私有IP地址和公网IP地址。专有网络VPC类型ECS实例一经创建即被分配一个私有IP地址。下列选项中________不属于私有IP地址适用的场景。', '负载均衡', '不在同一局域网内的ECS实例与其他云服务之间互访', '同一局域网内ECS实例之间内网互访', '同一局域网内ECS实例与其他云服务之间内网互访', 'B', '4', 1);
INSERT INTO `question` VALUES (391, 'ACP云计算', '\"对象存储服务OSS的某个Bucket同时配置了三条生命周期规则,分别是:Object保留x天后转换到低频访问Object保留y天后转换到归档冷备Object保留z天后删除下列选项中,x、y、z的取值满足规则要求的是\"', '30,15,60', '30,60,90', '90,60,30', '30,30,30', 'B', '4', 1);
INSERT INTO `question` VALUES (392, 'ACP云计算', '网络ACL是专有网络VPC中的网络访问控制功能。用户可以自定义设置网络ACL规则,并将网络ACL与交换机绑定,实现对交换机中ECS的流量的访问控制。下列关于网络ACL功能特性说法错误的是________。', '仅过滤绑定的交换机中的ECS流量', '规则是无状态的', '不过滤同一交换机内的ECS间的流量', '无任何规则时,会允许所有出入方向的访问', 'D', '4', 1);
INSERT INTO `question` VALUES (393, 'ACP云计算', '创建专有网络后,可以通过创建交换机为专有网络划分一个或多个子网.同一专有网络内的不同交换机之间________,云资源必须部署在交换机内,可以将应用部署在不同可用区的交换机,提高应用的可用', '互不干扰', '内网互通', '相互隔离', '可以指定互通', 'B', '4', 1);
INSERT INTO `question` VALUES (394, 'ACP云计算', '虽然OSS默认对每个存储的对象在物理盘上有多份副本,但合规性要求所规定的数据需要跨一定距离保存一份副本。通过________功能,可以在远距离的OSS数据中心之间复制数据以满足这些合规性要求。', '跨可用区复制', '数据传输服务', '跨区域复制', '数据备份服务', 'C', '4', 1);
INSERT INTO `question` VALUES (395, 'ACP云计算', '已知在OSS服务上的存储空间test123中存有一个名为abc.png的图片文件。如果用户在相同存储空间下本品又上传了同名abc.png图片文件,则________。', '提示用户是否确定上传同名文件', '覆盖原有图片文件', '以abc(1)-png保存上传文件', '根据文件大小确认保留哪个图片文件', 'B', '4', 1);
INSERT INTO `question` VALUES (396, 'ACP云计算', '在阿里云的专有网络VPC中,________是用于连接VPC内的各个交换机的设备。', '路由器', '路由表', '云服务器ECS', '负载均衡SLB', 'A', '4', 1);
INSERT INTO `question` VALUES (397, 'ACP云计算', '________是阿里云提供的海量、安全、低成本、高可靠的云存储服务。其数据设计持久性不低于99.9999999999%(12个9),服务可用性(或业务连续性)不低于99.995%。', '对象存储OSS', '负载均衡SLB', '云数据库RDS', 'ESSD云盘', 'A', '4', 1);
INSERT INTO `question` VALUES (398, 'ACP云计算', '对象是OSS存储数据的基本单元,也被称为OSS的文件。对象由________、Data和Key组成。', 'Region', 'Bucket', 'Object Meta', 'Endpoint', 'C', '4', 1);
INSERT INTO `question` VALUES (399, 'ACP云计算', '用户在使用SLB负载均衡时,发现在HTTP请求的头部增加了Transfer-Encoding:chunked字段,但是本地主机直接访问后端服务器时是没有这个字段的是因为________', '由于七层负载均衡基于Tengine反向代理实现', '在四层负载均衡服务中,负载均转发流量增加了该字段', '通过设置可以不增加该字段', 'Transfer-Encoding:chunked表示Web服务器对响应消息体做了整体传输', 'A', '4', 1);
INSERT INTO `question` VALUES (400, 'ACP云计算', '一个负载均衡实例是一个运行的负载均衡服务,用来接收流量并将其分配给后端服务器。要使用负载均衡服务,必须创建一个负载均衡实例,并至少添加一个________和两台ECS实例。', '公司IP', 'EIP', '数据盘', '监听', 'D', '4', 1);
INSERT INTO `question` VALUES (401, 'ACP云计算', '负载均衡SLB是对多台云服务器进行流量分发的负载均衡服务,________功能是阿里云SLB不支持的。', '会话保持', '健康检查', '生命周期挂钩', '域名URL转发', 'C', '4', 1);
INSERT INTO `question` VALUES (402, 'ACP云计算', '购买的块存储无法挂载到指定的ECS上时,可能的原因是________。', '该ECS已停止状态', '购买的块存储和ECS不在同一个可用区', 'ECS是按量付费', 'ECS没有官网IP', 'B', '4', 1);
INSERT INTO `question` VALUES (403, 'ACP云计算', '伴随着层出不穷的网络安全事件,人们越来越重视云上系统的安全。某大型酒饮服务平台,提供线上线下一体化的酒饮服务,该业务的安全、合规是对运营者的巨大挑战,以下方案中,不推荐的是________。', '通过云安全中心对日常安全漏洞、安全事件进行响应处理,防勒索、防病毒、防篡改、合规检查', '采用实人认证服务,核验平台用户的自然人真实身份', '针对线上电商促销活动场景,采用风险识别服务,防护羊毛党、网络爬虫等业务安全威胁,降低业务营销的损失', '采用应急响应服务,在重大活动期间进行安全保障', 'B', '4', 1);
INSERT INTO `question` VALUES (404, 'ACP云计算', '伴随着层出不穷的网络安全事件,人们越来越重视云上系统的安全,某大型互联网用户,现有网上资产比较多:但是不清楚自己哪些资产联网了,无法及时对这些资产进行清理和关停,存在较大的安全隐患,阿里云的________服务最适合解决这个问题。', '漏洞扫描', '安全管家', '安全审计', '安全众测', 'B', '4', 1);
INSERT INTO `question` VALUES (405, 'ACP云计算', '负载均衡SLB是台云服务器进行流量分发的负载均衡服务,支持4层和7层流量的转发,其中,________功能是7层负载均衡服务不支持的。', '域名URL转发', 'SNI支持', '一致性哈希(CH)调度算法', '重定向', 'C', '4', 1);
INSERT INTO `question` VALUES (406, 'ACP云计算', '以下关于SLB使用场景的描述,________是使用于4层(TCP)协议配置SLB监听规则。', '后端服务器应用采用HTTP协议的负载均衡场景', '后端服务器应用采用HTTP、HTTPs、TCP协议的负载均衡场景', '后端服务器应用采用HTTPs协议的负载均衡场景', '后端服务器应用采用TCP协议的负载均衡场景', 'D', '4', 1);
INSERT INTO `question` VALUES (407, 'ACP云计算', 'ECS的更换系统盘是指为ECS实例重新分配一块系统盘,系统盘ID会更新,旧系统盘会被释放。系统盘的________、实例IP地址以及弹性网卡MAC地址保持不变。', '云盘类型', '系统类型', '原有业务', '原系统盘存在的数据', 'A', '4', 1);
INSERT INTO `question` VALUES (408, 'ACP云计算', '某个SLB实例的健康检查配置如下:响应超时时间:5秒,成功响应时间:1秒,健康检查间隔:2秒,健康阈值:3次,不健康阈值:3次。该SLB实例的健康检查失败的间隔时间和健康检查成功间隔时间分别是________和________秒。', '21,7', '19,7', '21,9', '19,9', 'B', '4', 1);
INSERT INTO `question` VALUES (409, 'ACP云计算', '在购买ECS的时候必须设置安全组,安全组在ECS中是非常重要的服务,其中安全组的主要使用方式为________。', '白名单的使用', '黑名单的使用', '黑名单出入网规则限制', '内网入流量限制', 'A', '4', 1);
INSERT INTO `question` VALUES (410, 'ACP云计算', '云服务器ECS是一种简单高效、处理能力可弹性伸缩的计算服务。在使用云服务器ECS的过程中,有时难免会出现数据被误删除的情况,在阿里云上恢复被误删除的数据有多种方式,其中不包括________。', '通过ECS管理控制台回滚已创建的快照', '通过ECS管理控制台恢复该磁盘对应的数据块副本', '使用开源工具ext3grep恢复误删的数据', '使用开源工具Extundelete恢复误删的数据', 'B', '4', 1);
INSERT INTO `question` VALUES (411, 'ACP云计算', '在云安全中心,有些合法程序、正常行为会被识别成安全威胁或告警事件,针对该场景,云安全中心提供了________的功能,开启后云安全中心将不再提示该告警。', '添加白名单', '添加黑名单', '告警收敛', '态势感知', 'A', '4', 1);
INSERT INTO `question` VALUES (412, 'ACP云计算', '弹性公网IP EIP (Elastic IP Address)是可以独立购买和持有的公网IP地址资源。下列________产品无法绑定EIP、', '云数据库RDS', '云服务器ECS', '负载均衡SLB', 'NAT网关', 'A', '4', 1);
INSERT INTO `question` VALUES (413, 'ACP云计算', '网安法提出:“网络运营者应当采取技术措施和其他必要措施,确保其收集的个人信息安全,防止信息泄露、毁损、丢失\"。小李是某网站站长,为了保护网站页面中出现的网站用户手机号等敏感隐私数据,小李可以使用WAF防火墙提供的_________功能。', 'HTTPS高级配置', '网页防篡改', '防敏感信息池露', '黑白名单配置', 'C', '4', 1);
INSERT INTO `question` VALUES (414, 'ACP云计算', '云计算最大的优势在于弹性与灵活性。阿里云的弹性体现在纵向的弹性和横向的弹性、存储的弹性、网络的弹性以及对于业务架构重新规划的弹性,普通IDC模式下, 很难做到对单台服务器进行变更配置。而对于阿里云的弹性满足了购买云服务器ECS或者存储的容量后,可以根据业务量的增减________自由变更配置。', '纵向', '横向', '存储', '网络', 'A', '4', 1);
INSERT INTO `question` VALUES (415, 'ACP云计算', '云服务器ECS持续提供多种服务器规格,解决用户的多种业务需求。某公司计划采用云服务器ECS部署SQL Server数据库,最推荐其使用的是', '高效云盘', 'SSD云盘', 'ESSD云盘', '普通云盘', 'C', '4', 1);
INSERT INTO `question` VALUES (416, 'ACP云计算', '如下_________是一种云战略,用于以及时,受保护和负担得起的方式促进业务关键型数据的可访问性,它依赖于这样的原则:无论消费者和提供者之间是否存在任何组织或地理分离,都可以按需向用户提供指定的有用数据。', 'IAAS', 'PAAS', 'SAAS', 'DAAS', 'D', '4', 1);
INSERT INTO `question` VALUES (417, 'ACP云计算', '网络安全是指网络系统的硬件、软件及其系统中的数据受到保护,不因偶然的或者恶意的原因而遭受到破坏、更改、泄露,系统连续可靠正常地运行,网络服务不中断。对于网络上常见的CC攻击主要破坏了网络安全的_________方面', '保密性', '可用性', '完整性', '不可抵赖性', 'B', '4', 1);
INSERT INTO `question` VALUES (418, 'ACP云计算', '将基础设施作为服务的云计算服务类型是_________服务。', 'laas', 'Paas', 'Saas', 'Daas', 'A', '4', 1);
INSERT INTO `question` VALUES (419, 'ACP云计算', '内容安全提供内容检测API, OSS违规检测和站点检测功能, OSS违规检测使用人工智能技术帮助检测里云对象存储服务OSS中的图片、视频是否包含色情、涉政等违规内容,并支持自动冻结(禁止公网访问)检测出的违规内容。下列关于OSS违规检测说法不正确的是', '使用OSS违规检测前,需要在内容安全控制台通过RAM授权内容安全服务读取OSS Bucket的权限。', 'OSS违规检测支持检测OSS Bucket中的增量内容和存量内容。增量内容(图片、视频)不支持自动检测,存量内容(图片、视频)支持自动检测。', 'OSS违规检测支持对图片和视频对象应用多种场景的违规检测。支持的图片检测场景如下:涉黄、涉政、图文违规、不良场景', 'OSS违规检测支持检测特定格式后缀的文件。支持检测的视频格式如下:avi, mp4. 3gp. mkv等。', 'B', '4', 1);
INSERT INTO `question` VALUES (420, 'ACP云计算', '在启动弹性伸缩前,需要设置_________条件,可实现在业务需求增长时自动增加ECS实例以保证计算能力,在业务需求下降时自动减少ECS实例以节约成本。', '伸缩配置', '触发条件', '伸缩规则', '伸缩策略', 'B', '4', 1);
INSERT INTO `question` VALUES (421, 'ACP云计算', '想要通过设置虚拟服务地址,将添加的同一地域的多台ECS实例虚拟成一个高性能、高可用的后端服务池,并根据转发规则,将来自客户端的请求分发给后端服务器池中的ECS实例,可以使用阿里云的______来实现。', '弹性伸缩AutoScaling', '负载均衡SLB', '云数据库RDS', '内容分发网络CDN', 'B', '4', 1);
INSERT INTO `question` VALUES (422, 'ACP云计算', '某用户使用负载均衡SLB和云服务器ECS搭建网站,出于安全考虑,网站已部署SSL证书,使用HTTPS协议访问,现在,其希望将用户的HTTP访问重定向至HTTPS,最简单的实现方式是', '开启SLB的证书托管功能', '添加一个新的监听,并开启监听转发功能', '为现有七层监听添加域名转发策略', '为现有七层监听添加路径转发策略', 'B', '4', 1);
INSERT INTO `question` VALUES (423, 'ACP云计算', '某用户使用负载均衡SLB和云服务器ECS搭建服务集群,搭建完成时,运维人员进行测试,发现通过配置在SLB内的云服务器去telnet SLB的VIP却不通,导致这种现象的原因是', 'SLB健康检查异常', '服务器内网防火墙设置没有放行相应端口', '创建负载均衡实例后,没有添加监听', '目前负载均衡不支持同时作为客户端和服务端,因为返回的数据包只在云服务器内部转发,不经过SLB', 'D', '4', 1);
INSERT INTO `question` VALUES (424, 'ACP云计算', '负载均衡实例采用集群部署,可实现会话同步,以消除服务器_________,提升冗余,保证服务的稳定性。', '资源浪费', '单点故障', '性能瓶颈', '存储成本高', 'B', '4', 1);
INSERT INTO `question` VALUES (425, 'ACP云计算', '阿里云提供了丰富的解决方案以满足VPC内的云产品实例与公网(Internet)、其他VPC、或本地数据中心(IDC)互连的需求。客户可以通过_________将专有网络和公网(Internet)打通,通过_________连接两个VPC,通过_________将本地IDC和云上专有网络打通。', '云企业网、云企业网、ECS固定公网IP', '弹性公网IP (EIP) 、智能接入网关、高速通道', '负载均衡、云企业网、智能接入网关', '云企业网、VPN网关、高速通道', 'C', '4', 1);
INSERT INTO `question` VALUES (426, 'ACP云计算', '阿里云对象存储服务OSS支持多种访问控制策略,若某bucket的访问权限是私有,其中某个object的访问权限是公共读写,那么,以下关于该bucket和object权限说法正确的是', '所有用户都有该object的读写权限', '所有用户都有该bucket的读写权限', '只有该Bucket的拥有者可以读写该object', '只有被RAM Policy授权的用户可以读写该object', 'A', '4', 1);
INSERT INTO `question` VALUES (427, 'ACP云计算', '某摄影网站购买了阿里云OSS产品来存放和处理大量的摄影图片,但是发现大量的图片下载量产生了巨额的外网流量费用,而且无法保证远距离传输的资源质量,经咨询阿里运维人员又购买了CDN产品,又有用户反映手机端携带了图片处理参数访问经过CDN加速的图片时变成空白图片,刷新后可以访问,电脑访问正常。针对此问题,以下排查方法不正确的是', '使用手机直接访问OSS查看图片访问是否正常。若直接访问OSS查看图片正常,而通过CDN访问异常,说明CDN节点网络问题导致加载失败或CDN缓存了错误内容。', '使用手机直接访问OSS查看图片访问是否正常。若直接访问OSS也异常, CDN访问正常,说明OSS节点网络问题导致加载失败', '使用电脑端直接访问OSS查看图片访问是否正常。若电脑端访问正常,则问题出在手机端。', '使用电脑端直接访问OSS查看图片访问是否正常。若电脑端访问也异常,可能是图片出问题了。', 'B', '4', 1);
INSERT INTO `question` VALUES (428, 'ACP云计算', '基于已有ECS实例创建伸缩组后,系统会根据业务情况自动添加或减少一组具有相同配置的ECS实例。某用户在创建伸缩组时设置期望实例数为8,并添加3台已有实例,伸缩组创建完成后,已有实例添加至伸缩组,此时该伸缩组期望实例数为_________。', '8', '3', '11', '5', 'A', '4', 1);
INSERT INTO `question` VALUES (429, 'ACP云计算', '某电商平台为吸引用户,除定期推出优惠活动外,还会在节假日、会员日、购物节开展大促。为保证顺利承载活动带来的流量,运维人员可以使用产品组合既能达到更好的效果又降低工作成本。', '云服务器+负载均衡+弹性伸缩+云数据库+对象存储', '云服务器+弹性伸缩+云数据库', '云服务器+弹性伸缩', '云数据库+弹性伸缩', 'A', '4', 1);
INSERT INTO `question` VALUES (430, 'ACP云计算', '作为架构师,您在给某公司设计架构方案,首先手机APP客户端的请求最终使用云服务器ECS实例来进行处理,其次使用负载均衡SLB实例来做流量分发,使得后台各台ECS实例的负载大致均匀,为了应对业务量的大幅波动(周末访问量比较大),您还建议他们使用弹性伸缩(Auto Scaling)动态的增加或减少计算资源。他们对您的方案比较满意,作为一个特别看重用户体验的创业型公司,他们有一个比较担心的问题:在后端云服务器ECS实例相对空闲时,可能会触发减少ECS实例的伸缩活动,这时候运行在即将移出伸缩组的ECS实例上的应用会被终止,那么发起这些应用的用户的体验会变差。您应该向客户推荐以下哪种解决方法来消除客户的后顾之忧?', '弹性伸缩中,伸缩组对应的伸缩配置使用的自定义镜像里存放执行脚本,并设置命令在操作系统关机时自动执行该脚本,其中该脚本中存放了此种状况下的处理逻辑', '在负载均衡SLB实例的后端服务器池中,找到将要被移出的ECS实例,自动设定该ECS实例的权重为0,则该实例将不会被分到新的请求,在处理完成已有的需求后,将被自动移出后端服务器池', '在负载均衡SLB实例的后端服务器池中,找到将要被移出的ECS实例,手工将该实例从后端服务器池中移出,则正在此ECS实例上运行的应用会正常返回结果,但是不会再被分到新的请求', '在负载均衡SLB实例中,开启后端服务器的数据同步选项,同时开启后端服务器的应用漂移选项,则在伸缩活动将某台ECS实例从伸缩组中上的数据和正在运行的应用将由同一个伸缩组中的另一台ECS实例接管', 'A', '4', 1);
INSERT INTO `question` VALUES (431, 'ACP云计算', '阿里云的负载均衡SLB中,可以通过健康检查来判断后端云服务器ECS实例的可用性。健康检查可以识别出后端服务器池中是否有不可用的云服务器ECS实例,如果有则不给这些异常的云服务器ECS实例分配任务,直到这些云服务器ECS实例恢复正常,某些特殊应用场景下需要关闭健康检查。以下说法中正确的是', '七层服务可以关闭健康检查,四层服务无法关闭健康检查', '四层服务可以关闭健康检查,七层服务无法关闭健康检查', '无论四层和七层均无法关闭健康检查', '无论四层和七层均可以关闭健康检查', 'A', '4', 1);
INSERT INTO `question` VALUES (432, 'ACP云计算', '阿里云负载均衡服务SLB支持配置域名或者URL转发策略,将来自不同域名或者URL的请求转发给不同的ECS处理,关于域名或者URL转发策略支持的协议,以下说法正确的是', '只有4层监听(TCP和UDP协议)支持配置转发策略', '只有7层监听(HTTP协议)支持配置转发策略', '只有7层监听(HTTP和HTTPS协议)支持配置转发策略', '4层监听(TCP和UDP协议)和7层监听(HTTP和HTTPS协议)都支持配置转发策略', 'C', '4', 1);
INSERT INTO `question` VALUES (433, 'ACP云计算', '在OSS中,用户操作的基本数据单元是Object, Object中不包含哪个元素?', 'key', 'id', 'meta', 'data', 'B', '4', 1);
INSERT INTO `question` VALUES (434, 'ACP云计算', '关于阿里云云盾”DDoS高防IP”服务支持的”线路类型”说法正确的是', '仅支持BGP+电信', '仅支持电信+联通', '仅支持联通+BGP+电信', '仅支持电信+联通+BGP+HK线路', 'D', '4', 1);
INSERT INTO `question` VALUES (435, 'ACP云计算', '开放系统互连参考模型(Open System Interconnect,简称OSI)是国际标准化组织(ISO)和国际电报电话咨询委员会(CCITT)联合制定的开放系统互连参考模型,为开放式互连信息系统提供了一种功能结构的框架。我们常用到的第七层是指', '应用层', '表示层', '会话层', '传输层', 'A', '4', 1);
INSERT INTO `question` VALUES (436, 'ACP云计算', '在使用阿里云弹性伸缩(Auto Scaling)时如果在伸缩组中指定了RDS实例,伸缩组会自动将加入伸缩组的ECS实例的内网IP添加到指定的RDS实例的访问白名单当中,下面说法错误的是?', '如果ECS实例无法加入到RDS访问白名单,那么该ECS实例将被回滚操作并释放', '指定的RDS实例必须是运行中状态。', '指定的RDS实例访问白名单的IP个数不能达到上限。', '伸缩组创建成功后,伸缩组不会立即生效,只有启用伸缩组,才能接受伸缩规则的触发和执行相关的伸缩活动。', 'A', '4', 1);
INSERT INTO `question` VALUES (437, 'ACP云计算', '您已经在阿里云的一台经典网络类型的没有数据盘的云服务器ECS实例上运行所有的应用,现基于业务发展的需要,您希望将这台云服务器ECS实例迁移到相同地域的专有网VPC中去,以下_________是可行的。', '直接将云服务器ECS实例的类型转换为专有网络', '将该云服务器ECS实例的安全组变更为专有网络类型', '制作该云服务器ECS实例的镜像,基于该镜像制作专有网络类型的云服务ECS实例', '无法迁移', 'C', '4', 1);
INSERT INTO `question` VALUES (438, 'ACP云计算', '弹性伸缩(Auto Scaling)支持使用生命周期挂钩挂起创建或移出的ECS实例,并在挂起瞬间对ECS实例进行自定义操作。生命周期挂钩被触发时,通过指定的方式发出通知或者自动执行更多动作,生命周期挂钩支持配置的通知方式不包括_________', 'MNS主题', 'MNS队列', 'OOS模板', 'OOS主题', 'D', '4', 1);
INSERT INTO `question` VALUES (439, 'ACP云计算', '在创建阿里云的云产品实例时,可以选择默认类型的专有网络VPC和交换机,这两项配置确定了专有网络类型的云产品创建时在相应地域和可用区下唯一的默认网络位置,与非默认的专有网各和交换机之区别主要在于默认类型的VPC和交换机只能由阿里云为您创建。下列关于默认交换机的描述错说的是_________', '默认交换机只能创建在默认专有网络中', '如果您有已经创建的专有网络VPC和交换机,在创建专有网络VPC类型的云产品实例时,云产品实例就不会创建在默认交换机中', '默认交换机与非默认交换机支持相同操作方式,且规格限制一致', '默认交换机由阿里云为您创建,您自行创建的均为非默认交换机', 'B', '4', 1);
INSERT INTO `question` VALUES (440, 'ACP云计算', '很多用户将系统迁移上云时,期待能够大幅节约成本,但是,在上云的过程中,导致最终的云上成本大于线下每年的平摊成本。对导致该现象的原因分析,不正确的是', '线下很多库是单机库,到云上采用的是RDS高可用版本,相对来说成本会增加', '计算成本时,只计算了购买租用设备的成本,忽略了线下的维护成本、人员成本、机房和电力成本等等', '线下使用的虚拟化方案由于采用超额预定,在同等效果下,比云服务器的资源利用率更高', '云服务器的配置完全按照云下配置1:1选型,实际利用率较低,不符合云上架构最佳实践', 'C', '4', 1);
INSERT INTO `question` VALUES (441, 'ACP云计算', '某用户需要根据云监控指标(ECS实例的CPU平均利用率)目标值来触发弹性伸缩活动,由弹性伸缩自动计算所需的实例数量并进行扩缩容,从而将云监控指标维持在目标值附近,该用户需要创建', '简单规则', '目标追踪规则', '预测规则', '步进规则', 'B', '4', 1);
INSERT INTO `question` VALUES (442, 'ACP云计算', '根据ECS实例加入伸缩组的方式, ECS实例可以分为自动创建的ECS实例和手动创建的ECS实例。对于手动创建的ECS实例,如果某用户想要设置由伸缩组在弹性收缩时负责停止和释放ECS实例,用户需要设置ECS实例', '转为备用状态', '移出保护状态', '托管给伸缩组', '托管给SLB实例', 'C', '4', 1);
INSERT INTO `question` VALUES (443, 'ACP云计算', 'E公司是一个原创网络小说的发布和交流平台,基于阿里云平台建设(使用了云服务器ECS和内容分发网络CDN的组合) ,上线3年来积累了大量的原创作品,访问量每季度增长50%。两天前有同事发现来自某一段IP的爬虫每天晚上自动来抓取平台里的原创内容,并在多个社区里发布,造成作者权益的损害。为此管理员果断在ECS的安全组里屏蔽了那段IP,但是问题并没有解决。请你为管理员提供一个可行的方案。', '启用阿里云CDN的Refer白名单功能', '开启云服务器ECS内部的防火墙', '启用阿里云CDN的Refer黑名单功能', '启用阿里云CDN的IP黑名单功能', 'D', '4', 1);
INSERT INTO `question` VALUES (444, 'ACP云计算', '如果ECS实例发生了实例自动恢复事件,例如物理机非预期宕机或进行主动运维,阿里云默认自动重启恢复实例。用户可以通过修改实例维护属性,自定义实例自动恢复方式。如果实例挂载了本地盘,用户可选择的恢复方式不包括', '自动重启恢复', '禁止重启恢复', '自动重新部署', '部分重启恢复', 'D', '4', 1);
INSERT INTO `question` VALUES (445, 'ACP云计算', '用户可以配置_________实时关注ECS实例使用情况,当_________检测到伸缩组内的ECS实例vCPU使用率突破80%时,_________根据用户配置的伸缩规则弹性扩张ECS资源,自动创建合适数量的ECS实例。', '实例健康状况、实例健康状况、弹性伸缩', '弹性伸缩、弹性伸缩、实例健康状况', '云监控、云监控、弹性伸缩', '安全组、云监控、弹性伸缩', 'C', '4', 1);
INSERT INTO `question` VALUES (446, 'ACP云计算', '如果发现ECS中了木马病毒,首先要做什么?', '修改远程登录端口', '修改系统管理员账号和密码', '查看是否有异常进程运行', '安装杀毒软件,并进行全盘检查和杀毒', 'B', '4', 1);
INSERT INTO `question` VALUES (447, 'ACP云计算', '阿里云对象存储OSS为每个存储空间Bucket自动分配一个内网地址和一个外网地址,如果正确使用内网地址,一方面可以实现OSS Bucket与云服务器ECS实例之间的流量免费,另一方面云服务器ECS实例通过内网访问OSS Bucket的网络质量较好,能够有效的提升部署在ECS实例上的应用的上传和下载质量。以下关于OSS Bucket内网地址的描述正确的是', '如果ECS实例需要访问OSS Bucket的内网地址,只能访问与ECS实例在同一地域的Bucket', '从公共云上的任意一台ECS实例都可以通过内网访问任何地域的OSS Bucket', '只要ECS实例和OSS Bucket属于同一用户, ECS实例就可以通过内网访问OSS Bucket', '如果ECS实例使用OSS Bucket内网地址,只能访问与ECS实例在同一地域且在同一可用区的OSS Bucket', 'A', '4', 1);
INSERT INTO `question` VALUES (448, 'ACP云计算', '使用阿里云弹性伸缩(Auto Scaling)来实现计算资源的弹性配置时,做了如下设置:伸缩组的属性中设置MinSize=6, MaxSize=8,伸缩规则为调整至10台\",伸缩配置也进行了正常的配置。该伸缩组当前的云服务器ECS实例数为6台,通过设置定时任务来执行,执行一次后,该伸缩组中的云服务器ECS实例的个数为_________', '8台', '10台', '6台', '5台', 'A', '4', 1);
INSERT INTO `question` VALUES (449, 'ACP云计算', '某用户在创建ECS实例(分配了公网IP地址)后的六小时内,想要更换该ECS实例的公网IP地址,但在控制台操作界面找不到更换公网IP选项,这种问题的原因可能是', '创建的ECS实例为经典网络实例', 'ECS实例处于Stopped状态', 'ECS实例为VPC网络,停止实例时选择\"停机不收费\"', 'ECS实例为VPC网络,且分配了私网IP地址', 'C', '4', 1);
INSERT INTO `question` VALUES (450, 'ACP云计算', '云产品的可用区指的是一套独立的基础设施,不同的可用区之间基础设施(网络,电力和空调等)相互独立,即一个可用区出现基础设施故障不影响另外一个可用区。下列关于阿里云ECS的可用区说法正确的是?', '可用区是在用户购买时指定的,购买后不支持更改', '可用区之间内网互通', '可用区内网络延时最高不超过1s', '若要提高应用的容灾能力则可以将实例创建在同一可用区', 'A', '4', 1);
INSERT INTO `question` VALUES (451, 'ACP云计算', '在OSS中,用户操作的基本数据单元是文件(Object),单个文件的最大允许大小根据上传数据方式不同而不同。下列哪种描述不属于OSS分块上传?', '支持断点上传', '网络条件较差,经常连接断开', '上传超过100MB文件', '上传最大不得超过5GE', 'C', '4', 1);
INSERT INTO `question` VALUES (452, 'ACP云计算', '针对阿里云专有网络VPC内的云服务器ECS实例,可以通过安全组来进行ECS实例的访问控制配置。以下说法中正确的是', '一个安全组中可以包含同一个可用区的不同VPC里的云服务器ECS实例', '一个安全组中可以包含同一个地域的不同VPC里的云服务器ECS实例', '一个安全组中仅可以包含同一个可用区的同一个VPC内的云服务器ECS实例', '一个安全组中可以包含同一个地域的同一个专有网络VPC内的云服务器ECS实例', 'D', '4', 1);
INSERT INTO `question` VALUES (453, 'ACP云计算', '阿里云弹性伸缩服务AutoScaling可以根据业务需求和策略自动调整弹性计算资源。在业务需求增长时无缝增加ECS实例,满足计算需要。在创建伸缩配置时,不需要填写什么信息。', '镜像', '计费方式', '登录密码', '安全组', 'C', '4', 1);
INSERT INTO `question` VALUES (454, 'ACP云计算', '负载均衡服务健康检查机制的引入,有效提高了业务服务的可用性,但健康检查的状态可能影响到前端客户端的访问请求结果。下列选项中,会导致前端访问请求失败。', '目标ECS健康检查成功', '目标ECS健康检查失败', '目标ECS异常,处于健康检查失败时间窗', '目标ECS无异常,处于健康检查成功时间窗', 'C', '4', 1);
INSERT INTO `question` VALUES (455, 'ACP云计算', 'SLB系统后端的ECS权重值一样,但是实际负载却不一样,可能是因为原因造成的。', '开启了获取真实IP功能', 'SLB后端服务器端口配置错误', '开启了会话保持功能', '权重值没有生效', 'C', '4', 1);
INSERT INTO `question` VALUES (456, 'ACP云计算', '云盾_________是一款多媒体内容智能识别服务,支持对图片、视频、文本、语音等对象进行多样化场景检测,帮助企业、开发者在复杂多变的互联网环境下快速发现文本、图片、视频、语音的各类风险。', '安全管家', '内容安全', '云安全中心', '内容审计', 'B', '4', 1);
INSERT INTO `question` VALUES (457, 'ACP云计算', '安全大屏将安全攻防数据转化并呈现到安全大屏上,秒级更新实时数据,可实时展现资产、漏洞、基线情况,以下关于安全大屏的描述,错误的是', '安全大屏支持展示资产、漏洞、基线、告警、安全运营、业务运营和云平台配置检查相关的数据', '为满足不同业务领域展示不同场景数据的需要,安全大屏支持自定义场景', '荷鲁斯之眼提供云上资产安全态势全景、网络拓扑和安全风险处理入口,帮助用户统一管控云上资产进行安全运营', '为保证访问安全,大屏不能直接免密访问,可单击免登配置创建大屏免登地址,方便用户在不登录云安全中心控制台的情况下直接通过免登链接打开安全大屏页面', 'C', '4', 1);
INSERT INTO `question` VALUES (458, 'ACP云计算', '某云上用户A的ECS实例上部署的应用程序在云产品通信中,通过云账号的AccessKey访问其业务数据库RDS的API,为了方便和快速地调用,某云上用户A直接把AccessKey写在配置文件中,但这种方式存在权限过高、泄露信息和难以维护等问题,如果你是阿里云产品经理,你会建议该用户开通服务来解决此问题。', 'RAM服务', '云防火墙服务', 'VPC服务', 'SLB服务', 'A', '4', 1);
INSERT INTO `question` VALUES (459, 'ACP云计算', '某电商平台包含众多线上系统和环节,如用户注册、登录、浏览商品、购物结算等。由于站点的动静态资源混杂、跨运营商访问网络不稳定、突发流量造成网络拥塞等问题,经常会出现在线支付、秒杀、促销推广时响应时间慢、服务不稳定等问题。如果你是阿里云产品经理,你会建议用户开通阿里云的服务来解决以上问题。', '云解析DNS', 'PCDN', '全站加速CDN', '高速通道', 'C', '4', 1);
INSERT INTO `question` VALUES (460, 'ACP云计算', '以下关于SLB使用场景的描述,是使用于4层(TCP)协议配置SLB监听规则。', '后端服务器应用采用HTTP协议的负载均衡场景', '后端服务器应用采用HTTP, HTTPS, TCP协议的负载均衡场景', '后端服务器应用采用HTTPs协议的负载均衡场景', '后端服务器应用采用TCP协议的负载均衡场景', 'D', '4', 1);
INSERT INTO `question` VALUES (461, 'ACP云计算', '某用户使用负载均衡SLB和云服务器ECS搭建FTP服务(监听端口99) ,搭建完成后,通过filezilla client访问SLB公网IP的99端口,能够登陆FTP,但是读取目录失败,导致该错误的关键原因是', '后端服务器负载过高', 'ECS的安全组规则入方向配置错误', '创建负载均衡实例后,没有添加监听', '负载均衡SLB不支持搭建FTP服务', 'B', '4', 1);
INSERT INTO `question` VALUES (462, 'ACP云计算', '负载均衡实例采用集群部署,可实现会话同步,以消除服务器单点故障,提升冗余,保证服务的稳定性。来自公网的请求通过等价多路径路由到达LVS集群, LVS集群内的每台LVS通过_________将会话同步到该集群内的其它LVS机器上,从而实现LVS集群内各台机器间的会话同步。', '四层转发', '组播报文', '七层转发', 'nginx', 'B', '4', 1);
INSERT INTO `question` VALUES (463, 'ACP云计算', '某客户在阿里云购买了几台ECS实例后,为了增强应用的可用性,实现高可用架构,想要通过负载均衡实现对多台ECS实例进行流量分发的服务,下列选选项中可能会造成流量分发失败的选项是', '负载均衡SLB实例和后端提供服务的云服务器ECS实例规格配置可以不同', '负载均衡SLB实例和后端提供服务的云服务器ECS实例可以不在同一可用区', '负载均衡SLB实例和后端提供服务的云服务器ECS实例可以不在同一地域。', '负载均衡SLB实例和后端提供服务的云服务器ECS实例带宽设置可以不同', 'C', '4', 1);
INSERT INTO `question` VALUES (464, 'ACP云计算', '负载均衡SLB是对多台云服务器进行流量分发的负载均衡服务,提供丰富的功能,满足各种业务场景的需要。某用户使用SLB和多台后端云服务部署高并发网站,在使用过程中,发现某段时间客户端访问延迟,想要快速定位异常后端服务器,推荐其采用的方式是', '通过SLB健康检查功能,定位健康检查异常的ECS', '通过弹性伸缩服务配合云监控,在客户端访问延迟时快速增加云服务器实例', '结合阿里云日志服务,通过仪表盘巡检,分析负载均衡的响应时间', '结合阿里云云监控服务,查看负载均衡的云监控数据', 'C', '4', 1);
INSERT INTO `question` VALUES (465, 'ACP云计算', '负载均衡提供性能共享型实例和性能保障型实例,性能保障型实例提供可靠的性能指标。下列选项中不属于性能保障型实例的关键指标。', '最大连接数', '每秒新建连接数', '每秒新增查询数', '每秒查询数', 'C', '4', 1);
INSERT INTO `question` VALUES (466, 'ACP云计算', '某公司将重要业务部署在阿里云华北2地域,使用的架构是负载均衡SLB+多台ECS, SLB作为公网流量的统一入口,非常关键,为了避免因该SLB实例出现问题导致业务中断,以下措施可行的是', '在华北2区域不同可用区再创建一个SLB实例作为备实例,出现故障时, SLB支持主备实例自动切换', '在华东1地域再创建一个SLB实例作为备实例,出现故障时, SLB支持主备实例自动切换', '对SLB实例开启实例删除保护,防止其被运维人员误删除', '使用安骑士对SLB实例进行安全防护', 'A', '4', 1);
INSERT INTO `question` VALUES (467, 'ACP云计算', '对于入网流量,负载均衡会根据用户在控制台或API上配置的转发策略,对来自前端的访问请求进行转发和处理,如果相应的负载均衡实例服务端口使用的是<span style=\"border-bottom:1px solid;\">,</span>那么LVS集群内每个节点都会根据负载均衡实例负载均衡策略,将其承载的服务请求按策略直接分发到后端ECS服务器。', '二层协议', '三层协议', '四层协议', '七层协议', 'C', '4', 1);
INSERT INTO `question` VALUES (468, 'ACP云计算', '什么是组成专有网络的基础网络设备,用来连接不同的云资源实例。专有网络是地域级别的资源,专有网络不可以跨地域,但包含所属地域的所有可用区。可以在每个可用区内创建一个或多个_________来划分子网。', '交换机、交换机', '交换机、路由器', '路由器、交换机', '路由器。路由器', 'A', '4', 1);
INSERT INTO `question` VALUES (469, 'ACP云计算', '18存储空间的名称在OSS范围内必须是全局唯一的,一旦创建之后无法修改名称。下列选项中名称符合存储空间名称规范的是', '12-test', '-test12', 'Test-12', '12Test-', 'A', '4', 1);
INSERT INTO `question` VALUES (470, 'ACP云计算', 'object操作在OSS上具有_________,操作要么成功要么失败,不会存在有中间状态的object. OSS保证用户一旦上传完成之后读到的Object是完整的, OSS不会返回给用户一个部分上传成功的Object', '隔离性', '一致性', '原子性', '持久性', 'C', '4', 1);
INSERT INTO `question` VALUES (471, 'ACP云计算', 'OSS提供标准、低频访问、归档三种存储类型,全面覆盖从热到冷的各种数据存储场景。其中_________类型提供高可靠、高可用、高性能的对象存储服务,能够支持频繁的数据访问。', '标准存储', '低频访问', '归档存储', '标准和归档存储', 'A', '4', 1);
INSERT INTO `question` VALUES (472, 'ACP云计算', '可以配合CDN产品,提供静态内容存储、分发到边缘节点的解决方案。利用CDN边缘节点缓存的数据,提升同一个文件,被同一地区客户大量重复并发下载的体验。', 'SLB', 'RDS', 'VPN', 'OSS', 'D', '4', 1);
INSERT INTO `question` VALUES (473, 'ACP云计算', '对象存储服务OSS是一种海量、安全、低成本、高可靠的云存储服务,适合存放任意类型的文件。容量和处理能力弹性扩展,多种存储类型供选择,全面优化存储成本。对象存储OSS中的低频访问存储类型适合保存_________类型的数据。', '每小时访问频率1—2次', '每天访问频率1—2次', '每月访问频率1—2次', '每年访问频率1—2次', 'C', '4', 1);
INSERT INTO `question` VALUES (474, 'ACP云计算', 'OSS基于_________架构设计,消除单点故障,确保数据业务的持续性。', '高性能', '高并发', '高可用', '高安全', 'C', '4', 1);
INSERT INTO `question` VALUES (475, 'ACP云计算', 'OSS是目前中国国内唯一通过Cohasset Associates审计认证的云服务,可满足严格的电子记录保留要求;允许用户以“不可删除、不可篡改\"方式保存和使用数据,符合美国证券交易委员会(SEC)和金融业监查局(FINRA)的合规要求。用户可设置_________的合规保留策略。', '基于对象', '基于时间', '基于用户', '多维度', 'B', '4', 1);
INSERT INTO `question` VALUES (476, 'ACP云计算', '在阿里云的专有网络VPC中,_________是用于连接VPC内的各个交换机的设备', '路由器', '路由表', '云服务器ECS', '负载均衡SLB', 'A', '4', 1);
INSERT INTO `question` VALUES (477, 'ACP云计算', '专有网络VPC是用户独有的云上虚拟网络,用户可以将云资源部署在自定义的专有网络中。创建专网络后,系统会在路由表中自动添加一条以_________为目标网段的路由条目,用于VPC内的云产品通信。', '172.16.0.0/12', '10.0.0.0/8', '100.64.0.0/10', '192.168.0.0/16', 'C', '4', 1);
INSERT INTO `question` VALUES (478, 'ACP云计算', '用户把数据存储到OSS以后,就可以使用阿里云提供的其他产品和服务对其进行相关操作,比如OSS和ECS产品结合使用,假设OSS和ECS实例属于同地域,可以通过_________方式使用内网访问OSS资源;假设OSS和ECS实例属于不同的地域,可以通过_________来间接实现通过OSS内网地址访问OSS资源?', '通过VPN网关直接访问OSS资源、通过OSSbrowser访问OSS资源', '通过URL直接访问OSS资源、通过OSSbrowser访问OSS资源', '通过VPN网关直接访问OSS资源、通过配置ECS反向代理', '通过URL直接访问OSS资源、通过配置ECS反向代理', 'D', '4', 1);
INSERT INTO `question` VALUES (479, 'ACP云计算', 'CDN支持配置Referer防盗链。访问请求到达CDN节点后, CDN节点会根据预设的防盗链黑名单或白名单,对访客的身份进行过滤。符合规则的用户可以顺利请求到资源,不符合规则的用户,请求会返回_________响应码。', '200', '403', '500', '503', 'B', '4', 1);
INSERT INTO `question` VALUES (480, 'ACP云计算', '32对象存储OSS支持数据冷热分层,提供更具性价比的存储方式,适合存放任意类型的文件。支持三种不同的存储类型,全面覆盖从热到冷的各种数据存储场景,其中不包括', '标准存储', '冗余存储', '归档存储', '低频访问存储', 'B', '4', 1);
INSERT INTO `question` VALUES (481, 'ACP云计算', '某电商网站被竞争对手攻击,网站遭受大量的恶意CC攻击,长时间占用消耗服务器的核心资源,造成服务器性能瓶颈,如CPU、内存、带宽,导致网站业务响应缓慢或是无法正常提供服务。如果你是阿里云安全工程师,你会建议用户开通', 'WAF防火墙', '云安全中心', '内容安全', '安全众测', 'A', '4', 1);
INSERT INTO `question` VALUES (482, 'ACP云计算', '某用户通过阿里云管理控制台创建ECS实例时,若未在该地域创建安全组,阿里云会在创建该ECS实例的同时,为用户创建一个默认安全组,默认安全组为普通安全组,网络类型和ECS实例一致,默认打开的端口不包括', 'ICMP协议', 'SSH 22端口', 'RDP 3389端口', 'HTTP 80端口', 'D', '4', 1);
INSERT INTO `question` VALUES (483, 'ACP云计算', '阿里云云服务器ECS提供多种实例规格,满足用户多种业务场景的需要。某中小企业用户想要使用两台ECS,一台部署单个Tomcat服务,另外一台部署Memcache,以下配置最推荐的是', 'Tomcat:4核4G, Memcache:2核8G', 'Tomcat:4核4G, Memcache:2核16G', 'Tomcat:4核8G, Memcache:2核8G', 'Tomcat:4核8G, Memcache:2核16G', 'B', '4', 1);
INSERT INTO `question` VALUES (484, 'ACP云计算', '是ECS在线扩容CentOS8系统盘后出现报错:\"xfs-growfs:is not a mounted XFS filesystem\"错误,错误原因为', 'xfsgrowts命令后需要接文件系统,而非文件系统挂载点。', 'xfs-growfs命令后需要接文件系统挂载点,而非文件系统。', 'centos8目前不支持在线扩容', 'centos8目前只可以进行离线扩容', 'B', '4', 1);
INSERT INTO `question` VALUES (485, 'ACP云计算', '《网络安全法》第二十一条规定:网络运营者应当按照网络安全等级保护制度的要求,履行下列安全保护义务,保障网络免受干扰、破坏或者未经授权的访问,防止网络数据泄露或者被窃取、篡改。而在现实网络环境中,木马病毒、勒索病毒、挖矿程序等持久化、顽固型病毒已经成为网络安全最大的威胁。在使用云服务器ECS的过程中,如果发现ECS感染了木马病毒,建议用户最先采取的措施是', '修改远程登录端口', '修改系统管理员账户的密码', '检查是否有陌生的异常进程在运行', '安装查杀病毒防木马软件,对服务器进行全盘病毒扫描和查杀', 'B', '4', 1);
INSERT INTO `question` VALUES (486, 'ACP云计算', '安全组是一种虚拟防火墙,由同一地域内具有相同保护需求并相互信任的实例组成,具备状态检测和数据包过滤能力,用于在云端划分安全域,可以通过_________创建安全组。', 'https://r.kaokao.mobi/image/kk202009171713388340v7pf82ib6.png', 'https://r.kaokao.mobi/image/kk20200917171338840ri72riu6c0.png', 'https://r.kaokao.mobi/image/kk20200917171338843jlcmf18snv.png', 'https://r.kaokao.mobi/image/kk20200917171338829grfjxh6mth.png', 'D', '4', 1);
INSERT INTO `question` VALUES (487, 'ACP云计算', '云服务器ECS提供了基础安全服务,包括异常登录检测、漏洞扫描、基线配置核查等。如果用户在创建ECS实例时,选择_________选项操作,系统会自动为新创建的ECS实例安装基础安全服务的安全插件Agent', '安全加固', '登录凭证', '实例RAM角色', '创建部署集', 'A', '4', 1);
INSERT INTO `question` VALUES (488, 'ACP云计算', '阿里云提供了多种连接ECS实例的方式,如果使用SSH方式登录ECS (Linux)失败,且没有明确的报错信息,首先应该采取的处理措施是', '检查ECS运行状态', '检查ECS安全组规则', '尝试使用控制台VNC登录', '发起工单流程', 'C', '4', 1);
INSERT INTO `question` VALUES (489, 'ACP云计算', 'IDC服务器、虚拟机、其他云平台的云主机或其他类型的服务器想要迁移至阿里云可以使用阿里云提供的_________工具,该工具迁移过程无需停机,不影响源服务器系统业务。', '快照', 'SMC', 'VPC', '镜像', 'B', '4', 1);
INSERT INTO `question` VALUES (490, 'ACP云计算', '负载均衡提供标签管理功能,方便用户通过标签对负载均衡实例进行分类。关于负载均衡标签的使用限制说法错误的是', '标签必须绑定在某个负载均衡实例上', '一个实例上的每个标签的标签键必须唯一', '每个实例可以绑定的标签数量有限制', '不同地域中的的标签信息互通', 'D', '4', 1);
INSERT INTO `question` VALUES (491, 'ACP云计算', '某用户在阿里云上创建了包年包月类型的ECS,创建后才发现,操作系统误选为Windows (实际需要使用Linux) ,下列选项中,能够解决这个问题的最佳方式是', '使用root用户登录ECS,重装操作系统', '发起工单,请售后协助更换操作系统', '为该ECS更换系统盘', '申请5天无理由退款,重新创建ECS', 'C', '4', 1);
INSERT INTO `question` VALUES (492, 'ACP云计算', '在使用负载均衡服务前,需要添加ECS实例作为负载均衡实例的后端服务器,用来接收负载均衡监听转发的请求。负载均衡服务通过设置_________。将添加的同一地域的多台ECS实例虚拟成一个高性能、高可用的应用服务池', '虚拟服务地址', '后端端口', '后端协议', '前端协议', 'A', '4', 1);
INSERT INTO `question` VALUES (493, 'ACP云计算', '在对象存储服务OSS中,用户创建了存储空间(Bucket)之后,可以上传任何类型的文件(Object)到存储空间中。OSS服务支持多种文件上传方式,如果需要上传的文件大小为10MB,建议用户采用_________上传方式', '控制台上传', '表单上传', '分片上传', '追加上传', 'A', '4', 1);
INSERT INTO `question` VALUES (494, 'ACP云计算', '专有网络的VPC路由条目指定了网络流量的导向目的地,由目标网段、下一跳类型、下一跳三部分组成。某用户需要创建一条自定义路由条目,用于将VPC网络通过高速通道连接到用户本地IDC,该用户的路由条目中,需要选择的下一跳类型为_________', 'VPN网关', 'NAT网关', '路由器接口(专有网络方向)', '路由器接口(边界路由器方向)', 'D', '4', 1);
INSERT INTO `question` VALUES (495, 'ACP云计算', '管理控制台是统一查看和管理阿里云产品及服务的平台。管理控制台面向阿里云用户,通过图形化界面、Cloud shell命令行工具等进行配置操作。创建云服务器ECS后,可以在控制台对其进行管理, ECS管理控制台直接支持的功能不包括_________', '重启实例', '停止实例', '释放实例', '迁移实例', 'D', '4', 1);
INSERT INTO `question` VALUES (496, 'ACP云计算', '云盘是阿里云为云服务器ECS提供的数据块级别的块存储产品,具有低时延、高性能、持久性、高可靠等特点。创建云盘后,用户可以在控制台对其进行管理,云盘的管理控制台直接支持的功能不包括_________', '扩容磁盘', '回滚磁盘', '共享磁盘', '备份磁盘', 'C', '4', 1);
INSERT INTO `question` VALUES (497, 'ACP云计算', '某用户在使用云服务器ECS Linux实例时发现,在重启系统后会出现数据盘分区丢失或者数据丢失的问题,这可能是因为该用户未在etc/fstab文件里设置自动挂载。此时,您可以先手动挂载数据盘分区。如果手动挂载时报分区表丢失,建议其采取的操作方法不包括', '通过fdisk恢复分区', '通过testdisk恢复分区', '通过testdisk直接恢复数据', '通过Disk Genius直接恢复数据', 'D', '4', 1);
INSERT INTO `question` VALUES (498, 'ACP云计算', '云安全中心是一个实时识别、分析、预警安全威胁的统一安全管理系统,以下关于云安全中心的描述错误的是', '云安全中心不支持解绑阿里云ECS服务器,即使卸载了Agent插件,该服务器仍将以离线状态出现在服务器列表中,而不会从列表中移除', '部分弱密码检测项(例如:MySQL弱密码检测、PostgresQL弱密码检测、Microsoft SQL Server弱密码检测可能采用尝试登录的方式进行检测,从而会占用一定的服务器资源,产生较多的登录失败记录)和系统等保、CIS标准检测项默认关闭', '未安装Agent插件的服务器将不受云安全中心保护,但控制台页面仍会显示该资产的任何漏洞、告警、基线漏洞和资产指纹等数据)', '资产指纹调查的配置管理功能支持设置监听端口、软件资产、运行进程、账户资产、计划任务和中间件数据的采集刷新频率', 'C', '4', 1);
INSERT INTO `question` VALUES (499, 'ACP云计算', '按照《中华人民共和国网络安全法》的规定,某企业需要获取等保合规三级认证,建议其使用阿里云_________服务,提高运维安全保障', 'web应用防火墙', 'SSL证书', '安全众测', 'VPN网关', 'C', '4', 1);
INSERT INTO `question` VALUES (500, 'ACP云计算', '负载均衡SLB是对多台云服务器进行流量分发的负载均衡服务,负载均衡SLB的应用场景,不包括', '消除应用层单点故障', '扩展应用系统的服务能力', '提高网站首页的加载速度', '多可用区容灾', 'C', '4', 1);
INSERT INTO `question` VALUES (501, 'ACP云计算', '负载均衡的操作日志需要在_________控制台查看。操作审计记录云账户资源操作,提供操作记录查询并可以将记录文件保存到指定的OSS存储空间。', 'KMS', 'ConsoleSignin', 'Action Trail', 'RAM', 'C', '4', 1);
INSERT INTO `question` VALUES (502, 'ACP云计算', '某行业大牛的个人博客只有一些静态页面,他想用最经济且简便的方式将网站部署在阿里云上,另外由于很多浏览器已经把没有使用HTTPS协议的网站全部标记为不安全,他想要为网站开启HTTPS协议支持。根据该用户的情况,最推荐的阿里云方案是__', '使用虚拟主机部署博客网站,并在虚拟主机中部署SSL证书', '使用云服务器ECS部署博客网站,并在ECS中部署SSL证书', '使用云服务器ECS和负载均衡SLB部署网站,将SSL证书托管在SLB上', '使用对象存储OSS存储网站静态页面,绑定自定义域名,并开启证书托管', 'D', '4', 1);
INSERT INTO `question` VALUES (503, 'ACP云计算', 'OSS服务费用由存储费用、流量费用、请求费用、数据处理费用、对象标签费用、传输加速费用组成,以下_________不会产生流量费用。', '通过互联网从OSS下载到本地', '本地上传图片到OSS', '通过CDN服务层下载OSS数据', '将源Bucket数据同步复制到目标Bucket ,源Bucket和目标Bucket属于不同区域', 'B', '4', 1);
INSERT INTO `question` VALUES (504, 'ACP云计算', '某用户在云上开通了OSS服务用于存储其站点的图片资源,并且使用CDN进行内容的分发加速。该用户为了在业务中避免被盗图的风险,对于OSS服务中的公共读文件开始了原图保护,限制向外暴露图片的URL,该用户开启原图保护以后,通过_________方式访问OSS服务可能会造成原图保护失效。', '直接访问OSS文件', '匿名访问OSS文件', '通过CDN请求访问文件', '通过处理参数请求缩略图', 'C', '4', 1);
INSERT INTO `question` VALUES (505, 'ACP云计算', '某用户要求可以及时的监控到ECS实例网络配置的异常活动、软件崩溃和硬件使用情况,可及时记录网络、软件或硬件问题,我们可以用_________配合云监控的指标监视功能,灵活自定义计算资源维护的健康水位。', '监控', '自助诊断系统', '实例健康状况', '系统日志', 'C', '4', 1);
INSERT INTO `question` VALUES (506, 'ACP云计算', '用户需要快速创建多个ECS实例,这些实例的密钥对、RAM角色、实例类型和网络设置全部一致,为了方便快速的创建这些ECS实例,我们可以通过创建来实现。', '镜像', '实例启动模板', '快照', '弹性供应', 'B', '4', 1);
INSERT INTO `question` VALUES (507, 'ACP云计算', '安全组是一种虚拟防火墙,具备状态检测和数据包过滤能力,用于在云端划分安全域。某客户对运维效率、ECS实例规格以及计算节点的规模要求比较高,那么我们应该为客户推荐____最合适。', '企业安全组', '普通安全组', '默认安全组', '自定义安全组', 'A', '4', 1);
INSERT INTO `question` VALUES (508, 'ACP云计算', '某云上用户开通了阿里云的弹性伸缩服务以应对其业务运行过程中可能出现的流量高峰。某日,因业务需要,运维人员需要更换伸缩组内的某一台ECS的操作系统,因此需要对其进行停机,为了避免其因健康检查失败而被释放,建议运维人员采用的操作是', '将该ECS实例转为备用状态,弹性伸缩不会检查处于备用状态的ECS实例健康状态,也不会释放ECS实例。', '将该ECS实例转为保护状态,弹性伸缩不会检查处于保护状态的ECS实例健康状态,也不会释放ECS实例。', '将该ECS实例转为挂起状态,弹性伸缩不会检查处于挂起状态的ECS实例健康状态,也不会释放ECS实例。', '将该ECS实例加入白名单,弹性伸缩不会检查白名单内的ECS实例健康状态,也不会释放ECS实例。', 'A', '4', 1);
INSERT INTO `question` VALUES (509, 'ACP云计算', '负载均衡提供监听级别的访问控制,用户可以针对不同的监听设置访问白名单或黑名单,如果某个用户开启了白名单或者黑名单访问,但访问策略组中没有添加任何IP,则负载均衡监听会', '转发所有请求', '拒绝所有请求', '白名单为空,拒绝所有请求黑名单为空,转发所有请求', '白名单为空,转发所有请求黑名单为空,拒绝所有请求', 'A', '4', 1);
INSERT INTO `question` VALUES (510, 'ACP云计算', '对象存储服务OSS的版本控制功能用于管理某一存储空间(Bucket)中相同对象(Object)的不同版本,根据我们日常使用来看,对版本控制应用场景及解决方案,下列_________是不正确的。', '通过版本控制的恢复历史版本功能,可以将因意外操作或应用程序故障删除或覆盖的object快速恢复', 'Bucket开启版本控制功能后,大量冗余的历史版本object占用存储空间,产生费用,建议通过配置历史版本过期规则来自动清理历史版本object', 'BucketK开启版本控制后,所有手动删除的object不会直接删除,而是会成为历史版本,存储在Bucket中,建议修改此默认配置,使手动删除的object不再保存历史版本。', 'Bucket开启版本控制后,删除其中object会产生删除标记,大量不清除的删除标记会影响getbucket操作性能,建议配置生命周期规则,定期清除删除标记', 'C', '4', 1);
INSERT INTO `question` VALUES (511, 'ACP云计算', '某视频社交APP计划将系统部署在阿里云上,其业务系统需要包括视频的原始数据采集、视频上传、存储、视频转码、分发以及视频播放的完整功能,如果你是阿里云产品经理,以下选项中的阿里云方案,你会向用户推荐的是—————。', '文件存储+视级点播+媒体处理+内容分发CDN+阿里云播放器', '文件存储+短视频SDK+媒体处理+内容分发CDN+阿里云播放', '对象存储OSS+视频直播+媒体处理+内容分发CDN+阿里云播放器', '对象存储OSS+短视烦SDK+媒体处理+内容分发CDN+阿里云播放器', 'D', '4', 1);
INSERT INTO `question` VALUES (512, 'ACP云计算', '对象的生命周期是从上传成功到被删除为止,在整个生命周期内,只有通过——继续写入数据,其他上传方式上传的Object内容无法编辑', '追加上传', '断点续传', '覆盖上传', '重复写入', 'A', '4', 1);
INSERT INTO `question` VALUES (513, 'ACP云计算', '基于目前主流的隧道技术,专有网络隔离了虚拟网络。每个VPC都有一个独立的隧道号,一个隧道号对应着一个虚拟化网络。一个VPC内的ECS实例之间的传输数据包都会加上隧道封装,带有唯一的隧道ID标识,然后送到物理网络上进行传输。不同VPC内的ECS实例因为所在的_________不同,本身处于两个不同的路由平面,所以不同VPC内的ECS实例无法进行通信,天然地进行了隔离。', '账号', '隧道ID', 'SDN', 'CDN', 'B', '4', 1);
INSERT INTO `question` VALUES (514, 'ACP云计算', '负载均衡SLB是对多台云服务器进行流量分发的负载均衡服务,提供公网和私网两种类型的负载均衡实例,关于公网类型负载均衡实例的IP地址,以下说法正确的是', '系统会为其分配一个公网服务地址', '系统会为其分配一个弹性公网IP', '系统会为其分配一个公网服务地址和一个私网服务地址', '系统会为其分配一个弹性公网IP和一个私网服务地址', 'A', '4', 1);
INSERT INTO `question` VALUES (515, 'ACP云计算', '负钱均衡SLB是对多台云服务器进行流量分发的负载均衡服务,支持4层和7层流量的转发,其中,4层负载均衡服务不支持_________功能。', '域名URL转发', '健康检查', '访问控制', '加权最小连接数(WLC)调度算法', 'A', '4', 1);
INSERT INTO `question` VALUES (516, 'ACP云计算', '基于隧道技术和___技术,阿里云的研发在硬件网关和自研交换机设备的基础上实现了VPC产品', 'CDN', 'SDN', 'Overlay', 'GRE', 'B', '4', 1);
INSERT INTO `question` VALUES (517, 'ACP云计算', 'DDoS高防IP服务提供IP级别的流量清洗策略调整功能,针对DDoS攻击提供四种四层清洗模式供用户选择,默认清洗模式为', '宽松模式', '正常模式', '严格模式', '攻击紧急模式', 'B', '4', 1);
INSERT INTO `question` VALUES (518, 'ACP云计算', '设计云上架构时,如果能充分了解云服务的特点,就能场长避短,充分利用云的优势。例如,云服务器ECS由于是虚拟服务器,其单机性能或许不如传统物理机,但是,在云上可以很便捷地搭建两个ECS和一个负载均衡SLB实例的架构,这种架构比单台高配ECS的方式相此,优势不包括', '隐藏后端IP,避免被扫描,被直接攻击', '方便后续水平扩展', '方便管理运维', '避免单点故障', 'C', '4', 1);
INSERT INTO `question` VALUES (519, 'ACP云计算', '阿里云弹性伸缩服务AutoScaling可以根据业务需求和策略自动调整弹性计算资源。某用户使用弹性伸缩服务AutoScaling应对业务高峰,其打算使用定时任务作为伸缩活动的触发条件,创建定时任务时,不支持配置', '执行时间', '冷却时间', '重试过期时间', '重复周期', 'B', '4', 1);
INSERT INTO `question` VALUES (520, 'ACP云计算', '实人认证服务是指依托活体检测、人脸比对等生物识别技术,证件OCR识别技术等进行的自然人真实身份的校验服务,目前仅支持对拥有_________的人士进行认证。', '通过支付宝实名认证的用户', '真人免冠近照', '中华人民共和国第二代居民身份证', '二代身份证、护照、学生证等合法证件', 'C', '4', 1);
INSERT INTO `question` VALUES (521, 'ACP云计算', '阿里云提供公网和私网两种类型的负载均衡服务。可以根据业务场景选择配置对外公开或对内私有的负载均衡,系统会根据客户的选择分配公网或私网服务地址,下列属于公网负载均衡的描述正确的是', '只能在阿里云内部使用,可以转发的请求只能来自具有负载均衡的私网访问权限的客户端', '负载均衡实例的服务地址由阿里云统一分配和管理。该私网负载均衡服务只能被经典网络ECS实例访问。', '负载均衡实例可以通过Internet将客户端请求按照制定的监听规则分发到添加的后端服务器ECS上,', '实例的服务地址会从指定的专有网络的交换机网段内分配。该负载均衡服务只能被相同VPC内的ECS实例访问。', 'C', '4', 1);
INSERT INTO `question` VALUES (522, 'ACP云计算', 'OSS使用基于_________技术的数据冗余存储机制,将每个对象的不同冗余存储在同一个区域内多个设施的多个设备上,确保硬件失效时的数据可靠性和可用性。', '纠删码', 'RAID—x', 'rsync', '镜像复制', 'A', '4', 1);
INSERT INTO `question` VALUES (523, 'ACP云计算', '由于使用隧道封装技术对云服务器的IP报文进行封装,所以云服务器    的_________信息不会进入物理网络,实现了不同云服务器间网络隔离,因此也实现了不同专有网络间网络隔离。', '物理层', '数据链路层', '传输层', '应用层', 'B', '4', 1);
INSERT INTO `question` VALUES (524, 'ACP云计算', '某公司使用阿里云对象存储OSS存储文件,其管理员发现某个Bucket每天会自动生成名称中包含日期的Object, 最可能的原因是<span style=\"border-bottom:1px solid;\">。</span>', 'Bucket开启了lifecycle生命周期规则', '该Bucket开启了版本控制功能', '某个Bucket开启了日志存储,并且将日志的存储路径设置为上述Bucket', '某个Bucket开启了跨区域复制功能,并且将复制的目标路径设置为上述Bucket', 'C', '4', 1);
INSERT INTO `question` VALUES (525, 'ACP云计算', '用户购买了一台ECS服务器只允许开放80口进行公网访问,需要进行_________操作,', '在所有安全组中新增一条规则只允许80端口流量入', '新建一个安全组,并新增—条安全组规则允许80端口的公网入流量,将该云服务器实例放入该安全组,并从原先的默认安全组挪出', '在该实例所在的默认安全组添加一条公网访问规则允许80端口的公网出访问,并添加一条公网访问规则不允许所有的公网出流量', '新建一个安全姐,并新增一条安全组规则允许80端口的公网入流量,将该云服务器实例放入安全组', 'B', '4', 1);
INSERT INTO `question` VALUES (526, 'ACP云计算', 'EIP是一种NATIP。它实际位于阿里云的公网网关上,通过NAT方式映射到了被绑定的云资源上,和云资源绑定后,云资源可以通过EIP与公网通信。用户开通了EIP后,发现无法绑定到现有的ECS上,经过排查发现是_________原因造成的。', 'ECS为经典网络类型', 'ECS为专有网络类型', 'EIP的地域和ECS实例的地域相同', '该ECS实例没有公网IP', 'A', '4', 1);
INSERT INTO `question` VALUES (527, 'ACP云计算', 'Windows实例的系统盘由40GB扩容到100GB,确认扩容已经完成,计算机分区C显示大小99.9GB,但是可用空间(百分比)反而比之前更小。这个错误出现的原因是<span style=\"border-bottom:1px solid;\">.</span>', '虚拟内存被开启且设置的是系统自动管理', 'windows目前不支持在线扩容', '更换系统盘错误', '内存满被占满', 'A', '4', 1);
INSERT INTO `question` VALUES (528, 'ACP云计算', '某创业公司,由于刚刚起步,没有专业的运维工程师,但是需要应对随时变化的互联网业务,为用户提供更优质的体验,避免由于用户访问量突发造成系统瘫痪,可以使用_________来帮助该公司改善系统体验。', '负载均衡', '弹性伸缩', '云服务器', '云数据库', 'B', '4', 1);
INSERT INTO `question` VALUES (529, 'ACP云计算', '阿里云的资源是多地域(Region)、多可用区(Zone)部署的,通常来讲,同地域下不同可用区之间的距离大概在__范围。', '数百米', '数公里', '数十公里', '数百公里', 'C', '4', 1);
INSERT INTO `question` VALUES (530, 'ACP云计算', '您成功地创建了一台阿里云的云服务器ECS实例,准备进行应用的部署。在部署应用之前,如何保证该云服务器ECS实例具备防御500M以内的DDoS攻击的能力?', '其他都不对', '需要您自己架设防DDoS服务', '阿里云的云盾产品提供免费的基础DDoS防护服务,不需要单独购买', '需要另行购买DDoS防护服务才可以具备防护功能', 'C', '4', 1);
INSERT INTO `question` VALUES (531, 'ACP云计算', '如果您在非阿里云的服务器上安装了云盾安骑士客户端,通过_________可以确保在您的管理控制台中能查到此服务器的相关报告。', '报告无法在阿里云的管理控制台查看', '通过阿里云官网帐号关联', '非阿里云服务器不支持安骑士', '在安装安骑士的过程中需要输入“安装验证key”,通过此Key关联用户的管理控制台', 'D', '4', 1);
INSERT INTO `question` VALUES (532, 'ACP云计算', '阿里云的专有网络VPC中用于连接VPC内的各个交换机的设备是_________。', '路由器', '路由表', '云服务器ECS', '负载均衡SLB', 'A', '4', 1);
INSERT INTO `question` VALUES (533, 'ACP云计算', '用户在阿里云以外的服务器上安装“安骑士客户端”后,通过_________方式与指定的阿里云官网帐号关联。', '在管理控制台生成的安装验证key', '用户帐号ID', '用户的AccessKey', '用户名和密码', 'A', '4', 1);
INSERT INTO `question` VALUES (534, 'ACP云计算', '阿里云对象存储OSS支持分块上传操作,即可以将大文件内进行分块处理后再分别上传,该操作适合在上传大文件或者网络较差的情况下使用。在分块上传的所有任务完成之后,如果没有调用Complete Multipart Upload接口,就会产生一些不完整的文件,这些不完整的文件会占用用户bucket的空间。为了方便用户剽除这些不完整的文件,OSS专门提供了_________功能。', '在线批量删除', 'OpenAI批量删除', 'Bucket清空', '碎片管理', 'D', '4', 1);
INSERT INTO `question` VALUES (535, 'ACP云计算', '您希望通过管理控制台进行阿里云的专有网络VPC内的云服务器ECS实例的私网IP地址的修改,但发现操作的菜单不可用,是什么原因引起的。', '该云服务器ECS实例没有绑定EIP', '该云服务器ECS实例所在的VPC类型是默认专有网络', '该云服务器ECS实例不是运行状态', '没有针对该云服务器ECS实例进行停止操作,且该云服务器ECS实例不是停止状态', 'D', '4', 1);
INSERT INTO `question` VALUES (536, 'ACP云计算', '某O2O网站基于阿里云的云服务器ECS、负载均衡SLB以及弹性伸缩(Auto Scaling)搭建了线上系统,其中ECS用来处理用户响应, SLB用来分发流量,弹性伸缩负责动态分配资源以满足突发的业务变化,三个产品配合可以提供稳定、便捷、低成本的服务。现在想要增加云数据库RDS到该架构中来,用来保存用户访问时产生的数据。以下说法中正确的是?', '如果在伸缩组中指定了RDS实例,则弹性伸缩会自动把伸缩组中的ECS实例的IP地址添加到RDS的白名单中去,无需手工设置', '可以把RDS实例直接挂在SLB下,由SLB按照转发规则分配任务到RDS实例进行处理', '可以把RDS实例手工添加到伸缩组, RDS实例可以保存用户访问的数据', '对RDS实例所在的地域(Region)没有硬性规定,但是建议和弹性伸缩在同一个地域,可以减小网络传输的延迟,提升响应速度', 'A', '4', 1);
INSERT INTO `question` VALUES (537, 'ACP云计算', '某企业基于阿里云的产品构建了在线电子商务系统,系统每天产生大量的交易日志都上传到标准存储类型的对象存储OSS上,平均每天300G。架构师小赵发现超过1年的日志很少有进一步处理的需求,仅仅是一个备份。基于这种情况小赵可以采取手段降低日志存储的成本。', '把数据存储到本地', '把数据存储到RDS', '把数据存储到ECS', '转换OSS存储类型为归档存储', 'D', '4', 1);
INSERT INTO `question` VALUES (538, 'ACP云计算', '安全组A中存在5台阿里云的云服务器ECS实例,您针对安全组A增加了一个新的规则来允许来自特定IP地址的入流量,并且增加了3台新的云服务器。以下描述中正确的是?', '无需任何额外的操作,特定来源IP地址可以访问所有的8台云服务器', '需要进行所有服务器的重启之后,特定来源IP地址可以访问所有的8台云服务器', '特定来源IP地址仅可以访问新增的3台云服务器', '特定来源IP地址仅可以访问新增的3台云服务器,当之前的5台服务器进行重启之后也可以进行访问', 'A', '4', 1);
INSERT INTO `question` VALUES (539, 'ACP云计算', '您在阿里云的帐号A下基于云服务器ECS实例搭建好了Web应用服务,如果需要在另一个阿里云的帐号B中快速创建包含相同应用的云服务器ECS实例,可以通过方式最高效的实现。', '在帐号A中创建该云服务器ECS实例的自定义镜像,并将该镜像共享给帐号B,帐号B基于该共享镜像进行新的云服务器ECS实例的创建', '直接在帐号B中申请云服务器ECS实例并进行相同的web应用的部署', '在帐号A中进行该云服务器ECS实例的快照创建,并将该快照共享给帐号B,基于该快照进行新的ECS实例的创建', '其他方式都不可以完成', 'A', '4', 1);
INSERT INTO `question` VALUES (540, 'ACP云计算', 'SLB支持同一Region跨数据中心容灾,还可以通过与那种服务结合支持跨Region容灾?', 'DNS', 'ECS', 'OSS', 'OCS', 'A', '4', 1);
INSERT INTO `question` VALUES (541, 'ACP云计算', '在您进行负载均衡服务监听配置时就可以选择是否开启会话保持。您可以针对不同的监听配置不同的会话保持策略。下列关于SLB的会话保持说法正确的是?', '仅支持基于源IP的TCP层的会话保持', '仅支持基于cookie的HTTP层会话保持,', '同时支持TCP层和HTTP层的会话保持', '会话保持针对TCP层和HTTP层都是基于源IP的', 'C', '4', 1);
INSERT INTO `question` VALUES (542, 'ACP云计算', '由于业务的流量增长,您需要对您的阿里云的包年包月的云服务ECS实例的带宽进行临时升级操作,以下的描述中错误的是', '可以为ECS实例设定当月内指定时间段内的带宽临时增加', '可以多次叠加操作临时升级操作', '可以按天进行升级,升级后如果云服务器ECS实例下个月进行续费,仍然按照升级前的基础带宽进行续费', '支持按天升级,升级后如果云服务器ECS实例下个月进行续费,按照升级后的带宽进行续费', 'D', '4', 1);
INSERT INTO `question` VALUES (543, 'ACP云计算', '如果您在创建Linux操作系统的云服务器ECS实例的同时选择增加了数据盘,以下关于系统盘和数据盘的说法中错误的是', '在数据盘使用前,需要先格式化再mount数据盘', '可以根据业务需要,对数据盘进行多分区配置', '支持对数据盘进行二次分区', '如果对系统盘进行二次分区操作,不会发生任何异常', 'D', '4', 1);
INSERT INTO `question` VALUES (544, 'ACP云计算', '某用户的业务系统搭建在云上,由ECS实例对外提供服务,为了应对业务系统的突发流量,该用户还开通了弹性伸缩服务。已知架构中某一台ECS实例承载了关键业务,为保证业务正常运行,该用户开启了实例释放保护。下列选项中,________不会释放掉该ECS实例。', '实例达到自动释放时间', '用户手动释放', '账号欠费超过15天', '实例由弹性伸缩自动创建,在缩容时被移出伸缩组', 'B', '4', 1);
INSERT INTO `question` VALUES (545, 'ACP云计算', '您希望通过华北2 (北京)地域的阿里云专有网络VPC中的云服务器ECS实例通过内网访问OSS,但是连接oss—cn—beiing—internal.aliyuncs.com (华北2 (北京)地域的通用内网地址)失败,可以通过_________解决。', '通过VPC专用的OSS内网地址进行访问, vpc100—oss—cn—beijing.aliyuncs.com', '换其他地域的内网地址进行访问,如华北1 (青岛)地域的通用内网地址(oss—cn—qingdao—internal.aliyuncs.com)', '只能通过外网地址进行访问', '其他方式都不能解决', 'A', '4', 1);
INSERT INTO `question` VALUES (546, 'ACP云计算', '阿里云的云服务器ECS产品的应用非常广泛,既可以单独的作为Web服务器,也可以与其他阿里云产品配合提供多媒体解决方案。在多媒体、大流量的APP或网站应用场景下,云服务器ECS实例与阿里云的_________产品搭配,可大幅减少用户访问的等待时间并降低带宽费用。', '对象存储OSS', '内容分发网络CDN', '弹性伸缩Auto Scaling', '负载均衡SLB', 'B', '4', 1);
INSERT INTO `question` VALUES (547, 'ACP云计算', '阿里云的云服务器ECS产品的应用非常广泛,既可以单独的作为Web服务器,也可以与其他阿里云产品配合提供多媒体解决方案。在多媒体、大流量的APP或网站应用场景下,云服务器ECS实例与阿里云的_________产品搭配,可以存储静态图片、视频、下载包等来降低存储费用。', '对象存储OSS', '云数据库RDS', '弹性伸缩Auto Scaling', '块存储', 'A', '4', 1);
INSERT INTO `question` VALUES (548, 'ACP云计算', '在使用阿里云弹性伸缩(Auto Scaling)时,伸缩组中包含的云服务器ECS实例有两种类型,一种是根据用户的伸缩配置和伸缩规则由弹性伸缩服务自动创建的云服务器ECS实例,另外一种是由用户手工添加到伸缩组中的ECS实例。关于这两种云服务器ECS实例,以下说法正确的是?', '当ECS实例被弹性伸缩从伸缩组中移出时,对于自动创建的ECS实例会停止和释放,对于手工添加的ECS实例则不会停止和释放', '手工添加的ECS实例,不受伸缩配置中定义的规格的限制', '手工添加的ECS实例对地域没有要求,但是对状态有要求,必须是\"运行中\"', '当伸缩组需要减少ECS实例时,自动创建的ECS实例会被优先移出伸缩组,手工添加的ECS实例会被尽量保留', 'B', '4', 1);
INSERT INTO `question` VALUES (549, 'ACP云计算', '阿里云的云盾具备不同级别的防DDos攻击产品,当有DDos攻击的情况下,业务可用性需要保持在99.9%以上时,最适合使用的产品是', 'DDoS基础防护', '安全管家', '态势感知', 'DDos高防IP', 'D', '4', 1);
INSERT INTO `question` VALUES (550, 'ACP云计算', '采用云计算服务与传统自建IT系统不同,相比传统自建方式,云计算带来了巨大的便利性。以阿里云服务器ECS为例,这些便利性中不包括?', '用户无需再去维护和管理硬件', '获得服务器实例在几分钟内而不是数天数周', '用户无需参与任何安全管理工作', '用户按照需要获得计算量而不是按照峰值设计', 'C', '4', 1);
INSERT INTO `question` VALUES (551, 'ACP云计算', '阿里云的负载均衡SLB提供对多台云服务器ECS实例进行流量分发的服务。负载均衡SLB实例和后端提供服务的云服务器ECS实例必须具有相同的_________', '地域( Region)', '规格配置(CPU、内存等', '可用区', '带宽设置', 'A', '4', 1);
INSERT INTO `question` VALUES (552, 'ACP云计算', '您基于阿里云的专有网络VPC环境构建应用,只想购买一个的弹性IP(EIP),但是该VPC内有多台云服务器ECS实例需要对外提供服务时,可以采用以下_________方式解决该问题', '不用任何操作,某台云服务器ECS实例绑定EIP之后,外网可以直接访问该PC内的所有云服务器ECS实例', '可以通过端口映射的方式,现将EIP绑定到某台云服务器ECS实例A上,并将实例A的端口映射到其他云服务ECS实例上', '在所有云服务器ECS实例所在的安全组增加一条策略,允许所有外网的访问流量', '其他的选项都不对', 'B', '4', 1);
INSERT INTO `question` VALUES (553, 'ACP云计算', '阿里云的云盾DDOS高防IP是阿里云在“基础DDoS防护”之上提供的高级防护产品,支持四层和七层的抗攻击能力,以下关于云盾DDoS高防IP的功能描述错误的是_________', '防护多种DOS类型攻击,包括但不限于以下攻击类型ICP Flood,UDP Flood,TCP Flood,SYN Flood, ACK Flood等', '支持弹性按天计费', 'DDoS防护阈值弹性调整,您可以随时升级更高级别的防护,调整过程服务中断5分钟', '提供实时精准的流量报表及攻击详情,让您及时准确获得当前服务详情', 'C', '4', 1);
INSERT INTO `question` VALUES (554, 'ACP云计算', '阿里云对象存储OSS是阿里云对外提供的海量、安全、低成本、高可靠的云存储服务。与自建存储对比,OSS在可靠性、安全、成本和数据处理能力等几个方面都具有优势。以下_________是OSS在安全性方面表现出来的优势。', '流量清洗和黑洞设备需要另外购买,阿里云的云市场有多家优质的供应商可供选择', 'OSS提供灵活的开发接口,兼容市面上主流的安全防护设备和协议', '提供多种鉴权和授权机制,以及白名单,防盗链,主子账号功能', '阿里云提供定制化的安全服务,可根据用户的需要一对一定制开发', 'C', '4', 1);
INSERT INTO `question` VALUES (555, 'ACP云计算', '在使用阿里云服务器ECS时,如果业务量的变化时间可以预测,可以配合弹性伸缩的<span style=\"border-bottom:1px solid;\">什么</span>伸缩模式,在业务高峰到达前准备好足量的计算资源,或者在业务高峰后释放空闲的计算资源。', '固定数量模式', '健康模式', '定时模式', '动态模式', 'C', '4', 1);
INSERT INTO `question` VALUES (556, 'ACP云计算', '对象存储服务OSS是一种海量、安全、低成本、高可靠的云存储服务,针对存放在Bucket的Object的访问, OSS提供了多种权限控制方式,其中,_________不是OSS支持的权限控制方式。', 'Security Group', 'ACL', 'Bucket Policy', 'RAM Policy', 'A', '4', 1);
INSERT INTO `question` VALUES (557, 'ACP云计算', '阿里云提供了多种多样的ECS供用户选择,某用户希望能够选择最适合自己的ECS,其需要考虑的因素不包括_________', '价格', '地理位置', '宿主机配置', '应用场景', 'C', '4', 1);
INSERT INTO `question` VALUES (558, 'ACP云计算', '使用阿里云云服务器ECS的过程中,如果发现Windows磁盘空间满了,可以通过清理磁盘中不需要的文件,或者扩容磁盘来解决。此外,日常也应养成良好的磁盘使用习惯,这些习惯不包括_________', '文件压缩保存', '定期清理不必要的应用程序', '磁盘快照压缩保存', '设置磁盘监控', 'C', '4', 1);
INSERT INTO `question` VALUES (559, 'ACP云计算', '用户想根据业务需求和策略设置伸缩规则,在业务需求增长时自动增加ECS实例以保证计算能力,在业务需求下降时自动减少ECS实例以节约成本,可以使用阿里云的_________实现该需求。', '负载均衡', '云数据库', '弹性伸缩', '专用网络', 'C', '4', 1);
INSERT INTO `question` VALUES (560, 'ACP云计算', '当用户访问OSS图片处理服务出现错误的时候,图片处理服务会返回给用户相应的错误码和错误信息。用户无法解决问题时,可以将错误消息中的元素_________发送给图片处理服务的工程师,定位错误的原因。', 'Code', 'Message', 'RequestlD.', 'HostlD', 'A', '4', 1);
INSERT INTO `question` VALUES (561, 'ACP云计算', '某工程企业拥有数十台阿里云ECS实例,由于经常受遭受到大流量的DDOS攻击,所以使用了阿里云的DDOS高防服务,并且由于客户业务量较大,拥有数十个账号,现在客户想要了解每个账号的活动,可以使用阿里云的_________产品来监控包括某个账号使用阿里云控制台、云上产品等行为,可以下载到本地以进行安全分析。', '操作审计', '云安全中心', '堡垒机', '数据审计', 'B', '4', 1);
INSERT INTO `question` VALUES (562, 'ACP云计算', '在使用阿里云负载均衡SLB的管理控制台时,如果需要添加某台云服务器ECS实例到某负载均衡SLB实例的后端服务器池中,ECS实例必须处于________状态。', '运行中', '启动中', '已停止', '已创建', 'A', '4', 1);
INSERT INTO `question` VALUES (563, 'ACP云计算', '阿里云对象存储OSS是阿里云提供的海量、安全、低成本、高可靠的云存储服务。如果您有一个网站是纯静态页的,或者是对某复杂网站做过动静分离,您可以直接将静态网页通过OSS提供服务,不仅如此,OSS还可以与________产品结合起来提供加速服务。', '云服务器ECS', '内容分发网络CDN', '专有网络VPC', '高性能计算HPC', 'B', '4', 1);
INSERT INTO `question` VALUES (564, 'ACP云计算', '阿里云弹性伸缩(Auto Scaling)提供了“弹性自愈\"功能,能保持伸缩组中的云服务器ECS实例都处在正常状态,从而使业务始终保持正常的负载,提高系统的稳定性。关于\"弹性自愈\",以下说法中正确的是?', '如果伸缩组中某台ECS实例状态不是运行中,则该ECS实例会被移出伸缩组,同时自动创建一台新的ECS实例,添加到伸缩组中来', '如果伸缩组中某台ECS实例的状态不是运行中,阿里云弹性伸缩会启动ECS实例的故障检查与修复进程,对ECS实例进行自动修复', '如果伸缩组中某台ECS实例状态不是运行中,阿里云弹性伸缩首先会自动重启该ECS,重启成功后继续保持在伸缩组中,如果重启失败,则从伸缩组中移除,同时创建新的ECS实例键入到伸缩组中来', '如果伸缩组中某台ECS实例状态不是运行中,阿里云弹性伸缩首先会自动重启该ECS,重启成功后继续保持在伸缩组中,如果重启失败,则会使用指定的镜像恢复该ECS实例到初始状态,继续保持在伸缩组中', 'A', '4', 1);
INSERT INTO `question` VALUES (565, 'ACP云计算', '某用户的业务系统搭建在云上,由ECS实例对外提供服务,为了应对业务系统的突发流量,该用户还开通了弹性伸缩服务。已知架构中某一台ECS实例承载了关键业务,为保证业务正常运行,该用户开启了实例释放保护。下列选项中,________不会释放掉该ECS实例。', '实例达到自动释放时间', '用户手动释放', '账号欠费超过15天', '实例由弹性伸缩自动创建,在缩容时被移出伸缩组', 'D', '4', 1);
INSERT INTO `question` VALUES (566, 'ACP云计算', '客户准备使用阿里云来部署他们的业务系统,为保证业务处理的正确性和服务不中断,可以通过________实现高IO云盘的数据备份,通过跨可用区、部署集、负载均衡等实现应用容灾。', '使用mysqldump命令', '规格族', '快照', '本地快照', 'D', '4', 1);
INSERT INTO `question` VALUES (567, 'ACP云计算', '负载均衡SLB是对多台服务器进行流量分发的负载均衡服务,提供公网和私网两种类型的负载均衡实例。关于公网类型负载均衡的IP地址,以下说法正确的是________ 。', '系统会为其分配一个公网服务地址', '系统会为其分配一个弹性公网IP', '系统会为其分配一个弹性公网IP', '系统会为其分配一个弹性公网IP和一个私网服务地址', 'A', '4', 1);
INSERT INTO `question` VALUES (568, 'ACP云计算', '专有网络VPC是用户独有的云上虚拟网络,用户可以将云资源部署在自定义的专有网络中。以下关于VPC中交换机与其他组件的对应关系,错误的是________。', '子网是与交换机绑定的', '网络ACL是与交换机绑定的', '安全组是与交换机绑定的', '路由表是与交换机绑定的', 'C', '4', 1);
INSERT INTO `question` VALUES (569, 'ACP云计算', '用户开通并配置WAF防火墙,控制台提示“未检测到CNAME接入且无流量”,或是“当前无流量”等错误信息,可能的原因不包括________。', '机房故障的极端情况下,WAF无法正常使用', '精准域名接入状态下,无CNAME接入且没有流量', '泛域名接入状态下,无流量或流量很低', 'WAF之外还部署了CDN等代理,域名解析CDN上', 'A', '4', 1);
INSERT INTO `question` VALUES (570, 'ACP云计算', '阿里云弹性伸缩(AutoScaling)是根据用户的业务需求和策略,自动调整其弹性计算资源的管理服务,为了在弹性伸缩创建的ECS实例中可以直接正常处理用户请求,无需任何额外的系统调度和配置,弹性伸缩的云服务器ECS实例中部署的应用必须满足________。', '无状态且可横向扩展的', '有状态', '无状态且符合等性的', '所有数据保存在ECS实例磁盘上', 'A', '4', 1);
INSERT INTO `question` VALUES (571, 'ACP云计算', '某客户的业务是抢票应用服务,业务系统全部放在了阿里云中,使用了阿里云的ECS实例和RDS实例,现客户想要对磁盘使用率和CPU使用率以及使用的云产品做一个监控,并且可以在业务高峰期时收到预警,如果是阿里云的运维人员可以建议客户使用________来实现。', '阿里绿网', 'CDN', 'SLB', '云监控', 'D', '4', 1);
INSERT INTO `question` VALUES (572, 'ACP云计算', '您部署在阿里云云服务器ECS上的服务,希望能通过阿里云的产品来提升该服务的可用性,以下哪种方案的可用性最高?', '在同一个地域的多个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', '在同一个地域的同一个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', '在不同地域的多个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', '在不同地域的同一个可用区的多台云服务器上部署应用,并挂载在同一个负载均衡实例后面', 'A', '4', 1);
